css:
@-moz-document domain("autohotkey.com") 
{
body, .splash, .ribbon, #ipbwrapper { background-color: #222223 !important; color: #777 !important; }
  *[style="color:#000000"] { color: #777 !important; }
  body * { border-color: #666667 !important; }
  .panel { color: #777 !important; }
  table.table1 td { color: #454545 !important; }
  
  img[src*="//autohotkey.com/board/ahk_logo_ipb1.png"] { padding-left: 221px !important; padding-top: 72px !important; background: transparent url("http://i.imgur.com/2EbeUQ7.png") no-repeat !important; width: 0px !important; height: 0px !important; background-size: contain !important; }

  /* header */
  .navbar .nav-tabs { background-color: #06253F !important; }
  .navbar .nav-tabs .tab:hover { background-color: #555555 !important; color: #454545 !important; }
  .navbar .nav-tabs .tab.dropdown-visible { background-color: #555555 !important; }
  .dropdown .dropdown-contents { background-color: #444444 !important; }
  .navbar .nav-tabs .tab.selected { background-color: #555555 !important; }
  
  a.time:hover { color: #454545 !important; }
  a { color: #979fad !important; }
  strong { color: #979fad !important; }
  p { color: #CCCCCC !important; }
  ul { color: #AAAAAA !important; }
  #left { background-color: #333334 ;}
  #left span {color: #DDDDDD; }
  dt {color: #5e7bab; }
  h1 {color: #5e7bab; }

  /* search */
  .search-header .inputbox, .search-header .button, .search-header .post-buttons > #expand > a { background-color: transparent !important; border-color: transparent !important; }
  .search-header .inputbox:hover { background-color: transparent !important; border-color: transparent !important; }
  .search-header .inputbox.search { background-color: #111111 !important; color: #454545 !important; border-color: transparent !important; }
  .search-header .button:hover { background: #555555 none !important; border-color: #111111 !important; }
  .search-header .button:hover::before { background: transparent none !important; color: #454545 !important; }
  
  div#search-btn { background: #111111 none !important; color: #454545 !important; border: 1px solid #454545 !important; }
  div#search-btn:hover { background-color: #555555 !important; }
  
  /* input boxes and button */
  a.button2 { background: transparent none !important; color: #454545 !important; border: 1px solid #454545 !important; }
  a.button2:hover { background-color: #555555 !important; }
  input { background-color: #454545 !important; border: 1px solid #454545 !important; color: #888888 !important; }
  input.google-pure-input-1-2 { background: transparent url("http://i.imgur.com/aH3OnYp.png") no-repeat left !important; padding: 4px !important; }
  input.google-pure-input-1-2:focus { background: none !important; }
  .pure-button { background-color: #111111 !important; border: 1px solid #454545 !important; color: #454545 !important; }
  .pure-button:hover { background-color: #555555 !important; }
  .tools { border-color: #454545 !important; }
  input[type="reset"]:hover, input[type="submit"]:hover { background-color: #555555 !important; }
  .inputbox:focus, select:focus, .inputbox, select { background-color: transparent !important; color: #454545 !important; border: 1px solid #454545 !important; }
  
  .hdr-table .hdr-language li { background-color: #111111 !important; border: 1px solid #454545 !important; color: #454545 !important; }
  .hdr-table .hdr-language li:hover { background-color: #555555 !important; }
  
  /* docs header */
  .header { background-color: #111111 !important; }
  .navh1 { color: #454545 !important; }
  .nav { background-color: #222222 !important; color: #454545 !important; }
  .nav li span { color: #454545 !important; }
  .nav .selected { background-color: #000C47 !important; }
  .nav li span:hover { background-color: #555555 !important; color: #454545 !important; }
  .nav .selected span { color: #454545 !important; }
  #headerbar { background: #000C27 !important; border-bottom-color: #454545 !important; }
  
  .l-box b, .splash h1, h4 { color: #454545}
  dd { border-left-color: #454545 !important; }
  
  h1, h2, h3 { border-bottom-color: #454545 !important; }
  strong { color: #AAAAAA !important; }
  
  .right-col #main-content
  
  /* nav header */
  .navh1 { background-color: #000C27 !important; }
  
  /* examples */
  pre, code { border: 1px solid #444445 !important; background-color: #111111 !important; }
  
  /* side menu */
  ul.jqtree-tree li > .jqtree-element:hover { background-color: #555555 !important; }
  
  /* footer */
  .footer, .footer a { background-color: #111111 !important; color: #AAAAAA !important; }
  
  /**********/
  /* forums */
  /**********/
  
  #branding { background-color: #333333 !important; }
  #community_app_menu > li > a:hover, #community_app_menu > li > a.menu_active { background-color: #555555 !important; }
  
  .forabg ul.topiclist li.row, .forumbg ul.topiclist li.row, .navbar, .panel, .bg2 { background-color: #222222 !important; } /* some bgs */
  .bg1 { background-color: #333333 !important; }
  ul.topiclist li, .content h2, .panel h2, label, .content { color: #AAAAAA !important; }
  
  blockquote { background-color: #222222 !important; border-color: #454545 !important; border-width: 1px 1px 1px 3px !important; }
  
  .postbody, .post_body { color: #BBBBBB !important; }
  
  /* pagination - main forum */
  .pagination li.ellipsis span { color: #454545 !important; }
  .pagination li a { background-color: #222222 !important; }
  .pagination li a:hover { background-color: #555555 !important; }
  
  /* topic search ??? */
  .search-box .button, .search-box .button:hover { background-color: #111111 !important; border: 1px solid transparent !important; }
  
  /* controls */
  .ipsBox { background: #333444 !important; }
  #vnc_filter_popup_close, body#ipboard_body fieldset.submit, body#ipboard_body p.submit, .ipsForm_submit { background-color: #000C47 !important; }
  textarea:focus, .input_text:focus { background: transparent none !important; text-shadow: none !important; }
  textarea, .input_text { text-shadow: none !important; }
  .input_submit:hover { background-color: #555555 !important; }
  
  .codebox { background-color: #222222 !important; }
  .codebox_plus code, .codebox code { color: #777 !important; }
  .coMULTI, .co1 { color: #777777 !important; }
  .edit { background-color: #000C57 !important; text-shadow: none !important; }
  
  .post_spoiler_show:hover { background-color: #555555 !important; }
  
  /* search */
  #search_wrap { background-color: #444445 !important; border-bottom: none !important; }
  #adv_search:hover { background-color: #555555 !important; border-bottom: none !important; }
  #search_options { background-color: #333333 !important; border: 1px solid #454545 !important; }
  #search_options_menucontent { background-color: #050505 !important; }
  #search_options_menucontent label:hover { background-color: #444445 !important; }
  
  /* msg error */
  .message.error { background-color: #444445 !important; color: red !important;  }
  .message { background: none !important; text-shadow: none !important; }
  
  /* topic / login buttons */
  .topic_buttons li.disabled a, .topic_buttons li.disabled span { background-color: #444444 !important; }
  
  /* topic reply talley */
  #community_app_menu > li.active > a, #secondary_navigation, .topic_buttons li a, .ipsButton, a.ipsButton, .pagination .pages li.active, .forumHover tr:hover td, img[src*="f_icon.png"], img[src*="f_icon_read.png"], img[src*="f_redirect.png"], .maintitle, .maintitle.linkBar, ul.post_controls a, ul.post_controls a.ipsButton_secondary, .post_block h3, .horizontalView .author_info, .col_f_icon img, .ipsLikeButton.ipsLikeButton_enabled, #footer { background-color: #333334 !important; }
  
  /* page nav */
  .pagination a { color: #454545 !important; }
  .pagination .page a, .pagination .back a, .pagination .forward a { background: #222222 !important; color: #AAAAAA !important; border: 1px solid #454545 !important; }
  .pagination .page a:hover, .pagination .back a:hover, .pagination .forward a:hover { background-color: #555555 !important; } 
  
  /* post date */
  .horizontalInfoList { color: #777 !important; }
  
  /* posts */
  .border, .statistics, .post_block, .ipsComment, .popupInner, .no_messages, .general_box, .ipsSideBlock, .poll_question ol, .ipsBox_container, .ipsFloatingAction, .column_view .post_body { background-color: #222223 !important; }
  .postprofile { color: #005CC7 !important; }
  
  .ipsType_pagetitle { color: #aaaaaa !important; }
  .post_body:hover { background-color: #222222 !important; }
  .post_wrap .author_info { background-color: #222222 !important; }
  pre { color: #78a5ee !important; }
  pre {color: #5e7bab; }
  
  pre.prettyprint, code.prettyprint { background-color: #1d1d1d	 !important; margin-left: 20px; margin-right: 20px;}
    .str { color: #ffa0a0; } /* string  - pink */
    .kwd { color: #f0e68c; font-weight: bold; }
    .com { color: #87ceeb; } /* comment - skyblue */
    .typ { color: #98fb98; } /* type    - lightgreen */
    .lit { color: #cd5c5c; } /* literal - darkred */
    .pun { color: #AAA; }    /* punctuation */
    .pln { color: #ddd; }    /* plaintext */
    .tag { color: #f0e68c; font-weight: bold; } /* html/xml tag    - lightyellow*/
    .atn { color: #bdb76b; font-weight: bold; } /* attribute name  - khaki*/
    .atv { color: #ffa0a0; } /* attribute value - pink */
    .dec { color: #98fb98; } /* decimal         - lightgreen */
    .kw1 { color: #a674ca; } /* decimal         - pink */
    .kw3 { color: #98fb98; } /* decimal         - lightgreen */
	.sy0 { color: #AAA; } /* decimal         - lightgrey */
    .br0 { color: #AAA; } /* decimal         - lightgrey */
    .Syntax span.func { color: #98fb98; } 
  .ipbfs_titlebox { background-color: #111000 !important; }
  .horizontalView .author_info { background: #333334 none !important; }
  ul.post_controls { background-color: #222223 !important; color: #222223 !important;}
  
  /*********/
  /* other */
  /*********/
  
  table.info th { background-color: #444444 !important; }
}
