    background-color: #212121 !important;
}
.ipsBox {
    border: 1px solid grey;
}
.ipsWidget {
    background: #212121!important;
}
.ipsType_sectionTitle {
    color: black;
}
.cke_editable {
    color: #dbdbdb !important;
}
.fa-paperclip {
    color: black;
}
.ipsAttachment_supportDrag {
    color: black;
}
.ipsStreamItem_contentBlock, .ipsStreamItem_expanded {
    background: #616161;
}
.ipsType_medium, .ipsType_richText {
    color: #bcc5d6 !important;
}
.ipsComment {
    background: #616161;
}
#ipsLayout_footer a, #ipsLayout_footer p {
    color: lightgrey;
}
img {
    border: none!important;
    background: none!important;
}
.ipsQuote {
 margin:0;
 padding:0px 15px;
 border-width:1px 1px 1px 2px;
 border-color:#616161 #616161 #616161 #616161;
}
html[dir="ltr"] .ipsReact_blurb:not( .ipsHide ) + .ipsReact_types {
 border-left:1px solid #616161;
 padding-left:3px;
 margin-left:10px;
}
.ipsWidget.ipsWidget_vertical .ipsWidget_title, .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
 color:#828e99;
 background:#414141;
}
.ipsPager .ipsPager_next a:after, .ipsPager .ipsPager_prev a:after {
 color:#212121;
}
.ipsPager_type {
 text-transform:uppercase;
 font-size:11px;
 font-weight:bold;
 color:#212121;
}
.ipsRepBadge_positive, .ipsRepBadge_positive:hover {
 color:#5af418 !important;
}
.ipsHovercard {
	background: #212121;
	box-shadow: 0px 2px 10px rgba(0,0,0,0.1);
	border: 1px solid #ddd;
	border-radius: 4px;
	position: relative;
}
body {
	font-family: "Roboto", "Helvetica Neue", Helvetica, Arial, sans-serif;
	font-size: 13px;
	line-height: 18px;
	color: #ffffff;
}
html,body {
 background:#284456;
}
.ipsAreaBackground_light {
	background: #414141;
}
.ipsDialog > div {
	background: #414141;
}
.ipsDialog_title {
	color: #ffffff;
}
.cke_wysiwyg_frame, .cke_wysiwyg_div {
	background-color: #818181;
}
.cke_inner {
	background: #616161;
}
.cke_top {
	white-space: normal;
	background: #e0e0e0;
}
.ipsTabs_panel {
	height: 100%;
	background: #616161;
}
.ipsTabs {
	background: #414141;
	padding: 10px 10px 0 10px;
	margin: 0;
	list-style: none;
}
.ipsSpoiler, .ipsStyle_spoiler {
	background: #616161;
}
#ipsLayout_body {
	color: black;
}
.ipsBox:not(.ipsBox_transparent):not(.ipsModerated) {
	background-color: #616161;
}
element {
	background-color: transparent;
	color: #fff;
	font-size: 14px;
	text-align: left;
}
}

@-moz-document url-prefix("https://mlpforums.com/pastleaders/"), url-prefix("https://mlpforums.com/topmembers/") {
.cPastLeaders_cell {
	background: #616161;
	border-radius: 5px;
	min-height: 68px;
	padding: 10px;
	position: relative;
}
.ipsAreaBackground_light {
	background: #616161;
}
.ipsAreaBackground_reset {
	background: #414141;
}
.ipsStreamItem.ipsStreamItem_contentBlock {
	border: 1px solid #616161;
	padding: 15px;
	border-radius: 3px;
}
a {
    color: #414141;
    text-decoration: none!important;
}
}

@-moz-document url-prefix("https://mlpforums.com/search/") {
.ipsAreaBackground_reset {
	background: #616161;
}
.ipsAreaBackground_light {
	background: #616161;
}
.ipsStreamItem.ipsStreamItem_contentBlock {
	border: 1px solid #515151;
}
#ipsLayout_body {
	color: black;
}
.ipsStreamItem_snippet .ipsType_richText {
	color: #ffffff;
}
.ipsAreaBackground {
	background: #616161 !important;
}
}

@-moz-document url-prefix("https://mlpforums.com/clubs") {
.ipsMemberCard {
 background:transparent;
 border:2px solid #414141;
 text-align:center;
 padding:10px;
 margin-top:10px;
 position:relative
}
}
