css:
@-moz-document domain("labconnection.net") {
/* Stretch to Fit */
#aspnetForm{
	bottom:			0;
	display:		flex;
	position:		absolute;
	top:			0;
	width:			100%;
}
#site_center{
	display:		flex;
	flex:			1;
	flex-flow:		column nowrap;
}
.tall_content2{
	flex-grow:		1;
	font-size:		1.3em;
	margin-bottom:	25px;
	min-height:		inherit;
}
.tall_bottom{
	bottom:			0;
	height:			25px;
	position:		fixed;
	width:			100%;
}

/* Space Out Header */
.tall_top #header .row_2{
	margin:			15px auto;
	margin-left:	195px;
}

/* Larger Meat Portions */
.tall_content2 .indent table{
	width:			100% !important;
}
.tall_content2 .indent tr td:first-child{
	padding:		10px 50px;
	text-align:		center;
}
.tall_content2 .indent tr:not(.CellHeader) > td:nth-child(n+2){
	padding:		10px 15px;
}

}
