css:
@-moz-document regexp("https?:\\/\\/(www\\.)?ifixit\\.com\\/[Gg][Uu][Ii][Dd][Ee]\\/[^\\/]+\\/[0-9]+(#.*)?") {
body, .conclusionText {
    color: #fff;
}
#page {
    background-color: #333;
}
h1, h2, h3, .stepTitleTitle, .fragment-link, h1 a, h1 a:visited, h2 a, h2 a:visited, h3 a, h3 a:visited, .step-title a, .step-title strong {
    color: #bbb;
}
#guideConclusion p {
    color: #afafaf;
}
.guide-title-subtext .guide-title-subtext-strong {
    color: #5c707d;
}
.stats-container .stats-value {
    color: #7c7c7c;
    font-weight: bold;
}
.guide-details-container .details-item, .guide-complete, .attachment-link-list .attachment-container, .guide-details-container .item-contents, .step-content.text-only {
    background-color: #3d3d3d;
}
.guide-details-container .details-item-container, .guide-complete, .attachment-link-list .attachment-container, .attachment-link-list .button:hover, .step-content.text-only {
    border-color: #3d3d3d;
}
.guide-details-container .item-contents {
    border-color: #5d5d5d;
}
.guide-details-container .item-title, .guide-details-container .item-value, .attachment-link-list a p {
    color: #c4c4c4;
}
.button[role=button], a.button, button.button, input.button {
    border-color: #575757;
    color: #c6c6c6;
}
.button[role=button], a.button:hover, button.button:hover, input.button:hover {
    border-color: #858585;
}
#pageStats .stats-module-title {
    color: #666;
}
}
