css:
@-moz-document domain("www.roblox.com"), domain("web.roblox.com") {
/* i really want this to be global */


* {
    font-family: Pingfang TC !important;
}

.ad,
.ad *,
.img_ad,
.ad-annotations,
#google_image_div,
#aw0,
#AdvertisingLeaderboard {
    display: none !important;
}
.ad,
.ad *,
.img_ad,
.ad-annotations,
#google_image_div,
#aw0,
#AdvertisingLeaderboard {
    display: none !important;
}
/*Ad removal */

/* i want to fill up this space lik rlly bad */

#catalog-content {
    background-color: transparent;
    border: 5px solid transparent;
}


#header {
    background-color: #ECECEC;
}
.navigation .notification-icon,
.rbx-highlight {
    background: #ECECEC;
}
#chat-header {
    background: #ECECEC;
    /*Party header*/
    
    ;
}
.slide-item-container-left,
.profile-avatar-right {
    background: #ECECEC;
}
.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active {
    box-shadow: 0 -4px 0 .6 #ECECEC inset;
    ;
}
.forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th {
    background: #ECECEC !important;
    /*Forum colors and messaging underline color*/
    
    ;
}
.rbx-upgrade-now #upgrade-now-button,
.rbx-btn-secondary-xs.btn-more,
#AjaxCommentsContainer > div > div.comment-form > form > button,
.rbx-vip-server-create,
.btn-secondary-xs {
    background-color: #ECECEC !important;
    border-color: #ECECEC;
    /*Upgrade button*/
    
    ;
}
.rbx-upgrade-now #upgrade-now-button:hover,
.rbx-btn-secondary-xs.btn-more:hover,
#AjaxCommentsContainer > div > div.comment-form > form > button:hover,
.rbx-vip-server-create:hover,
.btn-secondary-xs:hover {
    background: #ECECEC important;
    border-color: #ECECEC;
}
#GamesListsContainer > div {
    background-color: transparent !important;
    width: 100% !important;
}
#navbar-search-input,
#navbar-search-btn {
    border: 1px solid #ECECEC;
    /*Search border color*/
    
    ;
}
body {
    background: transparent !important;
}
.content {
    background: transparent !important;
}
#fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://vignette.wikia.nocookie.net/deathnote/images/1/11/Matt-mello-near-L-death-note-2813165-600-418.jpg/revision/latest?cb=20120125214755") !important;
    /* Background */
    
    background-size: cover !important;
    background-attachment: fixed !important;
}
#party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off {
    background: linear-gradient(to bottom, #ECECEC, #ECECEC);
}
#ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4) {
    display: none;
}
#GamesPageLeftColumn {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
    background: transparent !important;
}
body {
    -webkit-animation: blur 2s;
    -moz-animation: blur 2s;
    -ms-animation: blur 2s;
    -o-animation: blur 2s;
    animation: blur 2s;
}
.dropdown-menu {
    animation: fadein 0.5s;
    box-shadow: 0px 0px 100px;
}
.roblox-popover-content.bottom .notification-stream-wrap.open {
    box-shadow: 0px 0px 100px;
}
.chat-main {
    box-shadow: 0px 0px 100px;
}
@keyframes blur {
    from {
        filter: blur(25px);
    }
    to {
        filter: blur(0px)
    }
}
.menu-vertical .menu-option .menu-secondary-option {
    animation: fadein 0.5s;
}
.menu-secondary-container {
    animation: fadein 0.5s;
}
.menu-vertical .menu-option .menu-secondary-option {
    animation: fadein 0.5s;
}
.menu-secondary-container {
    animation: fadein 0.5s;
    background-color: transparent !important;
    width: 100% !important;
}
#navigation {
    background-color: rgba(236,236,236)
    /*Left side navi*/
    
    ;
}
.rbx-btn-primary-lg {
    background-color: rgba(236,236,2365);
}
.rbx-btn-primary-lg:hover,
.rbx-btn-primary-lg:focus {
    background-color: rgba(2236,236,236);
}
.chat-container.collapsed .chat-main,
.chat-main,
.dialog-container,
.dialog-container.collapsed {
    transition: height 1s
}
.chat-friend-list .chat-friend-container,
.input-group-btn .dropdown-menu li a {
    transition: box-shadow 1s
}
.game-card-container {
    transition: height 0.5s, top 0.5s;
    height: 200px;
    overflow: hidden;
    position: relative;
    top: 25px
}
.game-card-container:hover {
    transition: height 1s, top 1s;
    height: 107%;
    top: 0px;
    box-shadow: 7px 7px 25px
}
#navigation {
    transition: width 1s
}
}
