css:
/* i really want this to be global */
body{
    background-color: #EDEEF0;
}

#head-wrapper{
    width: auto;
    height: 42px;
    background: #4A76A8;
}

#head-buttons{
    width: 960px;
    margin: 0 auto;
}

#head-buttons #head-button{
	background-image: none;
    width: auto!important;
    padding: 0;
	float: right;
}

#head-buttons #head-button a{
	padding: 0;
    font-size: 12.5px;
	font-weight: normal;
    padding: 11px;
}

#head-buttons #head-button a:hover{
    background-color: #3D6898;
    text-decoration: none;
}

#head-buttons #home{
    width: 165px;
    height: 42px;
    position: unset;
    float: left;
}

#head-buttons #home a{
    padding: 11px 131px 12px 0px;
    background: url(https://vk.com/images/svg_icons/ic_head_logo.svg) no-repeat 19px;
    background-position: 0px 11px; 
    height: 19px;
    width: 34px;
}
#head-buttons #head-button:nth-child(2){
	display: none;
}

#content-infoname{
    display: none;
}

#content-main{
	box-shadow: none;
    border: none;
    width: 795px;
    margin: 0;
    padding: 0;
}

#content-left, #content-right, #content-main>div{
    padding: 15px;
	background: #fff;
    border-radius: 2px;
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
}

#content-right{
    width: 520px;
}

#left-menu{
    width: 149px;
    margin: 0;
}

#content{
	width: 960px;
    margin-top: 15px;
}

#left-menu{
    margin-left: -8px;
}

#left-menu a{
    width: 147px;
    font-size: 12.5px;
    padding: 5px;
    border-radius: 2px;
    border: none;
}

#left-menu a:hover{
    border: none;
}
