css:
/* i really want this to be global */

/* Global & Backlog/grids view */
.border {
    border: 1px solid #eee;
}

.application-main--view {
	height: calc(100% - 45px);    
}

.border--v,border--b {
    border-color: #eee;
}

.mqm-masthead {
    height: 45px;
}

.svg-main-menu {
    width: 13px;
}

.mqm-main-menu-item>div {
    font-size: 17px;
    font-family: MetricWeb-Regular, Calibri, sans-serif;
}

.svg-masthead, .svg-masthead[alm-icon] {
    width: 16px;
    height: 16px;
}

.svg-mastead {
    width: 16px;
    height: 16px;
}

.mqm-masthead-modules-menu-dropdown.dropdown-menu {
	top: 45px;    
}

.alm-splitter-handler.alm-splitter-vertical {
	flex: 0 0 .5px;    
}

.alm-splitter-splitter {
    flex: 0 0 5px;
}

.toolbar.border--b {
    border-color: #eee;
}

.popup-timeline-dialog .timeline>.chart {
	width: 100%;	
}

/* Tags */

.badge__text {
    font-size: 12px;
}

.badge__text {
    border-radius: 0;
    border-color: #e6e6e6;
    color: #888;
    padding: 0 8px;
}


/* Tabs */
.tabs {
    border-color: #eee;
}

.tabs--bottom-line .tabs__item {
    padding-bottom: 13px;
    padding-top: 8px;
    font-size: 14px;
    margin-left: 20px;
    text-transform: none;
    color: #888;
    font-family: MetricWeb-Regular, Calibri, sans-serif;
    letter-spacing: .3pt;
}

.tabs--bottom-line .tabs__item:first-of-type {
    margin-left: 4px;
}

.tabs--bottom-line .tabs__item.open {
    padding-bottom: 10px;
}

/* Context Filter */
.context-filter-label {
    font-size: 13px;
    text-transform: none;
    font-family: MetricWeb-Regular, Calibri, sans-serif;
}

.context-filter-container .badge__text {
    font-size: 12px;
}

.context-filter-container .badge__text {
    margin-right: 0;
    border-right: none;
}

.context-filter-viewer div:nth-last-of-type(2) .badge__text {
    border-right: 1px solid #ddd;
}

.context-filter-main-panel {
    height: 40px;
    line-height: 40px;
    border-bottom: 1px solid #eee;
}

.context-filter-navigation-arrows {
	display: flex;
    align-items: center;
    margin-top: 0;
    vertical-align: auto;
    line-height: 0;
}

.context-filter-navigation-arrows>div {
    margin-top: 6px;
}

.context-filter-viewer {
    padding-right: 10px;
}

.context-filter-viewer>.cols {
    display: none;
}

/* Timeline */
.popup-timeline-dialog {
	width: 98vw;
	left: 1vw;
	margin-left: 0;
}



/* Left Panel */
.tree-module-main-entity-tree {
	height: calc(100% - 40px);    
}

.alm-splitter-box-column-west {
    margin-top: -1px;
}

.alm-splitter-top-tree-close-button {
    height: 40px;
    line-height: 40px;
}

.alm-splitter-box-column-west .tree-module-toolbar.tree-module-toolbar {
    border-color: #eee;
}

.toolbar {
    height: 40px;
    line-height: 40px;
}

.tree-module-toolbar .button-toolbar .svg--sm, .svg--sm.svg--sm {
    height: 14px;
    width: 14px;
}

.alm-button-hide-west .svg--sm.svg--sm {
	width: 16px;
    height: 16px;
}

.tree-module-main-entity-tree ul.dynatree-container span.dynatree-node a {
    font-size: 13px;
}

.tree-module-main-entity-tree ul.dynatree-container .dynatree-node.dynatree-active a {
    color: #555;
    font-family: MetricWeb-Regular, Calibri, sans-serif;
}

ul.dynatree-container li>span {
    height: 26px;
    line-height: 26px;
}

ul.dynatree-container a {
    line-height: 26px;
    margin-left: 1px;
}

.alm-entity-tree__entity-label__box {
    width: 16px;
    height: 16px;
    min-width: 16px;
}

span.dynatree-expander {
    margin-right: 0;
}

alm-entity-tree[supports-drag-and-drop-reparenting=true] ul.dynatree-container>li>ul>li>span span.dynatree-expander {
    margin-left: 10px;
}

alm-entity-tree[supports-drag-and-drop-reparenting=true] ul.dynatree-container>li>ul>li>ul>li>span span.dynatree-connector {
	margin-left: 25px;    
}


/* Right Panel */
.layered-filter-container.alm-splitting-second-block {
    padding-top: 77px;
}

.alm-splitting-second-block .toolbar {
    display: flex;
}

.alm-splitting-second-block .toolbar .right-panel-toggle-splitter {
	flex-grow: 0;    
}

.alm-splitting-second-block .toolbar .tabs-container {
 	flex-grow: 1;  
    display: flex;
    justify-content: center;
}

.alm-splitting-second-block .toolbar .tabs-container>div:first-of-type {
    margin-left: 0;
}

.right-panel-in-splitter-height {
	height: calc(100% - 40px);	
}

.right-panel-in-splitter-height .height--16 {
	height: 8px;
}

.right-panel-in-splitter-height .layered-filter-group-title {
    font-size: 13px;
    color: #555;
}

.add-badge-button.add-badge-button {
	border-radius: 0;
}

/* Right Panel Planning */
.right-panel-in-splitter-height .release-buckets-containers>.release-buckets-containers-release {
	border-right: none;	
}

.right-panel-in-splitter-height .release-buckets-containers>.release-buckets-containers-release>span {
	font-size: 12pt;
}

.right-panel-in-splitter-height .release-buckets-containers>.release-buckets-containers-release>.bucket-releases-label>span {
	font-size: 12pt;	
}

.right-panel-in-splitter-height .release-buckets-containers .release-bucket-root>.sprint-bucket {
	padding: 0;	
}

.right-panel-in-splitter-height .release-buckets-containers .release-bucket-root>.sprint-bucket div[data-aid=bucket-sprint-root] {
	padding: 0 10px;	
}

.right-panel-in-splitter-height .release-buckets-containers .release-bucket-root>.sprint-bucket div[data-aid=bucket-sprint-selector] {
	padding-left: 7px;
}

.right-panel-in-splitter-height .release-buckets-containers .release-bucket-root>.sprint-bucket .team-progress-container .release-bucket-team-sprint {
	margin-top: 0;
}

.right-panel-in-splitter-height .release-buckets-containers .release-bucket-root>.sprint-bucket .team-progress-container .release-bucket-team-sprint .team-name {
	font-size: 10pt;	
}

.right-panel-in-splitter-height .release-buckets-containers .release-bucket-root>.sprint-bucket>.text-align--center {
	height: 20px;
	border-top: 1px solid #eee;
}

.right-panel-in-splitter-height .release-buckets-containers .release-bucket-root>.text-align--center {
	height: 20px;
	border-top: 1px solid #eee;
}

.right-panel-in-splitter-height .release-buckets-containers .release-bucket-root>.sprint-bucket>.text-align--center .release-bucket--expend-sprints {
	height: 100%;
	line-height: 20px;
	padding-top: 5px;
}

.right-panel-in-splitter-height .release-buckets-containers .release-bucket-root>.text-align--center .release-bucket--expend-sprints {
	height: 100%;
	line-height: 20px;
	padding-top: 4px;
}


/* Right Panel Entity Preview */
.right-panel-in-splitter-height preview-panel {
	margin-left: 0;
}

.right-panel-in-splitter-height preview-panel>.preview-panel {
	margin-top: 0;
	padding: 10px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.flex--0 {
	margin-right: 0;	
	padding-bottom: 10px;
	border-bottom: 1px solid #eee;
	margin-bottom: 10px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.flex--0 .entity-form-header__entity-label__box {
	display: block;
	width: 3px;
	height: calc(100% - 8px);
	position: absolute;
	left: 0;
	top: 0;
	border-radius: 0;
	margin-right: 0;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.flex--0 .entity-form-header__entity-label__box span {
	display: none;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.flex--0 .entity-form-header__entity-label__box .entity-form-header__entity-label__text {
	font-size: 9pt;	
	line-height: 20px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.flex--0>.rows {
	margin-left: 10px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.flex--0>.rows .text--lg, .right-panel-in-splitter-height preview-panel>.preview-panel>.flex--0>.rows .text--sm {
	font-size: 11pt;
	line-height: 16px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[data-aid=release-sprint-pane] {
	margin-top: 0;
	padding-bottom: 10px;
	border-bottom: 1px solid #eee;
	margin-bottom: 10px;
	font-size: 12pt;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.userTagsSupported"] {
	border-bottom: 1px solid #eee;
	padding-top: 5px;
	padding-bottom: 10px;
	margin-bottom: 10px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.userTagsSupported"] .mqm-user-tag {
	margin-bottom: 0;	
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.userTagsSupported"] .mqm-user-tag .tag-filter-item {
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.userTagsSupported"] .mqm-user-tag .badge__text_last {
	margin-right: 5px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.userTagsSupported"] .mqm-user-tag .badge-related-controls-add-and-remove {
	margin-bottom: 5px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.userTagsSupported"] .mqm-user-tag .badge-related-controls-add-and-remove .add-badge-button {
	width: 20px;
	padding: 0;
	margin: 0;
	line-height: 20px;
	text-align: center;
	box-sizing: border-box;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.userTagsSupported"] .mqm-user-tag .badge-related-controls-add-and-remove .add-badge-button>svg {
	margin-top: 0;
	height: 20px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.userTagsSupported"] .mqm-user-tag .badge-related-controls-add-and-remove .platform-filter-panel-clear>svg {
	width: 14px;
	height: 14px;
	opacity: 0;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.userTagsSupported"] .mqm-user-tag:hover .badge-related-controls-add-and-remove .platform-filter-panel-clear>svg {
	display: inline-block;	
	opacity: 1;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.displayedEntity.phase"] {
	height: 35px;
	margin: 0 0 10px 0;
	padding: 5px 0 10px 0;
	border-bottom: 1px solid #eee;
	align-items: center;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.displayedEntity.phase"] div[data-aid=entity-life-cycle-widget]>div {
	min-width: 0;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.displayedEntity.phase"] div[data-aid=entity-life-cycle-widget] span {
	font-size: 11pt;	
	text-transform: none;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.displayedEntity.phase"] div[data-aid=entity-life-cycle-widget] span:first-of-type {
	font-family: MetricWeb-Regular, Calibri, sans-serif;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.displayedEntity.phase"] div:nth-of-type(2) {
	width: 1px;
	height: 100%;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.displayedEntity.phase"] .my-items-right-pane-transition-button {
	font-size: 11pt;	
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.displayedEntity.phase"] .my-items-right-pane-transition-button .buttons-group>span {
	max-width: none;
	font-size: 11pt;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.displayedEntity.phase"] .my-items-right-pane-transition-button .buttons-group .drop-down {
	margin-left: 4px;	
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div[ng-if="previewPanel.displayedEntity.phase"] .my-items-right-pane-transition-button .buttons-group .drop-down svg {
	width: 13px;
	height: 18px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>div.text--medium {
	font-size: 11pt;
	font-family: MetricWeb-Regular, Calibri, sans-serif;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>form[name="previewPanel.form"] .my-items-description-container {
	margin: 0;
	margin-top: 4px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>form[name="previewPanel.form"] .my-items-description-container .field-container {
	font-size: 10pt;	
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>form[name="previewPanel.form"] .my-items-description-container .field-container .alm-ckeditor-content {
	font-size: 10pt;	
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>my-items-comments {
	margin-top: 15px;
	border-top: 1px solid #eee;
	padding-top: 10px;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>my-items-comments>div[data-aid=my-items-right-pane-comments]{
	margin-top: 0;	
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>my-items-comments>div[data-aid=my-items-right-pane-comments] > .text--medium {
	font-size: 11pt;
	font-family: MetricWeb-Regular, Calibri, sans-serif;
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>my-items-comments>div[data-aid=my-items-right-pane-comments] > div:nth-of-type(2) {
	margin-top: 4px;	
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>my-items-comments>div[data-aid=my-items-right-pane-comments] > div:nth-of-type(2) > div.padding-r--lg {
	padding: 0;	
}

.right-panel-in-splitter-height preview-panel>.preview-panel>.alm-scroll-bar>my-items-comments>div[data-aid=my-items-right-pane-comments] > div:nth-of-type(2) > div.padding-r--lg .mqm-new-comment-placeholder-height {
	margin-left: 0;
	border-radius: 0;
	height: 25px;
	padding: 0 0 0 4px;
	line-height: 24px;
	font-size: 10pt;
	font-family: MetricWeb-Regular, Calibri, sans-serif;
	
}

comment-lines>div[data-aid=alm-entity-form-comment-lines]>.alm-entity-form-comment-entry {
	padding: 0;
	margin-top: 10px;
}

comment-lines>div[data-aid=alm-entity-form-comment-lines]>.alm-entity-form-comment-entry > div:first-of-type img {
	width: 20px;
	height: 20px;
	margin-top: 0;
}

comment-lines>div[data-aid=alm-entity-form-comment-lines]>.alm-entity-form-comment-entry > .comment-bubble {
	padding: 0;
	border-radius: 0;
	border: none;
	margin-left: 8px;
}

comment-lines>div[data-aid=alm-entity-form-comment-lines]>.alm-entity-form-comment-entry > .comment-bubble:hover {
	background: #fff;	
}

comment-lines>div[data-aid=alm-entity-form-comment-lines]>.alm-entity-form-comment-entry > .comment-bubble > div:first-of-type {
	margin-bottom: 0;
}

comment-lines>div[data-aid=alm-entity-form-comment-lines]>.alm-entity-form-comment-entry > .comment-bubble > div:first-of-type div[data-aid=comment-author] {
	font-size: 9pt;
	color: #bbb;
	font-family: MetricWeb-Regular, Calibri, sans-serif;
	margin: 2px 0 0 0;
}

comment-lines>div[data-aid=alm-entity-form-comment-lines]>.alm-entity-form-comment-entry > .comment-bubble > div:first-of-type div[data-aid=comment-date-time] {
	opacity: 0;	
}

comment-lines>div[data-aid=alm-entity-form-comment-lines]>.alm-entity-form-comment-entry > .comment-bubble:hover > div:first-of-type div[data-aid=comment-date-time] {
	opacity: 1;	
}

comment-lines>div[data-aid=alm-entity-form-comment-lines]>.alm-entity-form-comment-entry > .comment-bubble .alm-entity-form-comment-entry-text {
	min-height: 0;
	border: 1px solid #eee;
	padding: 7px 8px;
	height: auto;
}

comment-lines>div[data-aid=alm-entity-form-comment-lines]>.alm-entity-form-comment-entry > .comment-bubble .alm-entity-form-comment-entry-text p {
	font-size: 10pt;
	min-height: 0;
	height: auto;
	margin-bottom: 0;
}

/* Main Panel */
.alm-splitter-box-column-center {
    border-top: none !important;
}


/* Entity name above center content */
.entity-form-header-container {
    margin-top: 0;
    width: 100%;
}

.entity-form-header-entity-id-container {
    font-size: 16px;
}

.entity-form-header-entity-id-separator {
    display: none;
}

.entity-form-header__entity-label__box {
    height: 20px;
    width: 20px;
    margin-right: 4px;
    font-size: 12px;
}

.entity-form-header__entity-label__container {
    margin-left: 3px;
}


.entity-form-header__entity-label__text {
    top: 0;
}

.entity-form-header-entity-id-container {
    line-height: 42px;
}

.entity-form-header-name-field-container div.text-field-input {
    font-size: 16px;
    line-height: 37px;
    color: #555;
}

/* Main Toolbar */
.alm-form-container .toolbar {
    height: 39px;
    padding-top: 0;
    padding-left: 0;
    display: flex;
    align-items: center;
}

.alm-form-container .toolbar-buttons-group {
    margin-right: 0;
    padding: 0 8px;
    align-items: center;
}

.toolbar-toggle-button-group {
    align-items: center;
    line-height: 0;
}

.toolbar-toggle-button-group .toolbar-toggle-button {
    margin-top: 0;
}

.toolbar-buttons-group .button--toolbar svg.svg--sm {
	width: 14px;
    height: 14px;
}

.toolbar-toggle-button-group .svg-s-toggle-includes-children-on {
    width: 20px;
    height: 20px;
}

.dropdown-container {
	display: flex;
    align-items: center;
}

.toolbar .dropdown .dropdown-button-toolbar {
    height: 20px;
    margin-top: 0;
    line-height: 0;
}

.button--toolbar .text--micro--xl {
	font-size: 13px;  
    color: #555;
}

/* Toolbar Filters Panel */

.entities-container-filter-panel {
	background: none;
}

.entities-container-filter-panel .alm-filter-panel {
    background: #f9f9f9;
    border-top: 1px solid #eee;
    padding-top: 10px;
}

.platform-filter-field.normal {
    height: 20px;
    line-height: 18px;
    font-size: 12px;
    color: #666;
    padding-left: 5px;
    margin-right: 5px;
}

.filter-field-span-field-name {
	color: #666;
    font-family: MetricWeb-Light, Calibri, sans-serif;
}

.platform-filter-field-predicate-viewer {
    font-size: 12px;
}

.display-inline-the-filter-panel.and-or-menu-container {
 	margin-left: 0;
}

.platform-filter-panel .platform-filter-panel-add-and {
	border-color: #ddd !important;    
}

.platform-filter-panel .platform-filter-panel-add-plus {
    height: 20px !important;
    width: auto;
    min-width: 20px;
    margin: auto;
    padding: 0 6px !important;
}

.platform-filter-panel .platform-filter-panel-add-plus>svg {
 	width: 11px;
    height: 11px;
    left: 0;
    top: -2px;
    margin-right: 0 !important;
}

.platform-filter-panel .add-filter-vertical-line {
    height: 18px;
    border-color: #ddd;
}

.platform-filter-panel .dropdown-and-or {
    height: 20px;
    border-color: #ddd;
    vertical-align: top;
    padding: 0 2px;
}

.platform-filter-panel .dropdown-and-or svg {
	width: 13px;
    height: 13px;
    position: relative;
    top: -2px;
}

.platform-filter-panel .platform-filter-panel-clear {
	height: 20px;   
    vertical-align: top;
    line-height: 20px;
}

.platform-filter-panel .platform-filter-panel-clear .platform-filter-panel-clear-icon {
	width: 14px;
    height: 14px;
    margin-top: 0;
    
}

/* Grid Header */
.slick-header.ui-state-default {
    border-color: #f5f5f5;
    height: 30px;
}

.slick-header-column.ui-state-default {
    margin: 0;
    height: 30px;
    line-height: 30px;
}

.slick-header-column.ui-state-default .slick-column-name {
    font-family: MetricWeb-Regular, Calibri, sans-serif !important;
    color: #888 !important;
    font-size: 12px !important;
}

.slick-header-column-sorted.ui-state-default .slick-column-name {
    font-family: MetricWeb-Medium, Calibri, sans-serif !important;
    color: #666 !important;
}

.slick-header-column span.slick-sort-indicator {
    top: 7px;
}

.slick-resizable-handle {
    height: 100%;
    margin-top: 0;
    background: none;
    border-right: 1px solid #eee;
    border-left: none;
    margin-right: 2px;
}


/* Entities Grid */

.slick-viewport .slick-row {
    height: 45px !important;
    display: flex !important;
    align-items: center !important;
    border-color: #eee;
}

.slick-viewport .slick-row>div {
 	height: 45px !important;
    line-height: 45px !important;
    font-size: 14px;
}

selection-status-bar {
    display: flex !important;
    justify-content: center;
    background: none !important;
    border: none !important;
    height: auto !important;
