/* i really want this to be global */

body {
	font-size:11px;
	line-height:1.4em;
}

.col-xs-9 {
	width: 83%;
}

.col-lg-7 {
	width: 63.3%;
}

rm-save-button .btn-save.dropdown-toggle {
	display:none;
}

rm-save-button .dropdown-menu {
	display:inline-block;
	position:relative;
	margin:0;
	margin-top: 1px;
	margin-left: 4px;
	padding:0 0!important;
	box-shadow:none;
	border: 1px solid #ddd;
	min-width:100px;
}

rm-save-button .dropdown-menu>li {
	margin: 4px;
	padding: 0;
}

rm-save-button .dropdown-menu>li>a {
	line-height:1.5;
	padding: 0;
}

