	min-height:10px;
}

.ibox-content {
	padding:4px;
}

[rm-subprojects] {
	width:80px;
	padding-left:4px;
	padding-right:4px;
}

[rm-project-view] {
	padding-right:0;
	padding-left:0;
}

[rm-project-view].col-xs-10 {
	width:calc(100% - 80px);
}

