	width:calc(80% - 80px);
}

[rm-widget-sidebar].col-xs-3 {
	width:17%;
}

[rm-widget-sidebar="::['Offerte', 'edit']"].col-xs-3 {
	width:17%;
}

.subproject-list>li div.title-container {
width: 100%;
overflow: hidden;
text-overflow: ellipsis;
display: inline-block;
margin-left: 0;
}

.subproject-list>li {
	margin-left:0px!important;
}

body.mini-navbar .navbar-static-side {
    width: 60px;
}

