css:
@-moz-document url-prefix("https://vk.com") {
.media_voting_header {
    height: 60px;
    text-align: left;
    padding: 0 5px;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    -ms-flex-pack: center;
    justify-content: center;
    position: relative;
    z-index: 1;
    padding-left: 20px;
}
.media_voting_question {
    color: #222;
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
    text-overflow: ellipsis;
    word-wrap: break-word;
    padding-top: 5px;
    letter-spacing: 0.4px;
    font-weight: 500;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    line-height: 34px;
    max-height: 102px;
    font-size: 14px;
}
.media_voting_info {
    line-height: 18px;
    font-size: 14px;
    letter-spacing: -0.2px;
    text-align: left;
    color: #656565;
    padding-top: 5px;
    padding-bottom: 8px;
    padding-right: 40px;
    background: rgba(0, 0, 0, 0);
}
.media_voting_footer {
    min-height: 16px;
    padding: 8px 0 8px;
    margin: 0 20px;
    line-height: 16px;
    font-size: 13px;
    color: #656565;
    text-align: left;
    position: relative;
    z-index: 1;
}
.fans_fan_img {
    vertical-align: top;
    width: 35px;
    height: 35px;
    border-radius: 50%;
}
.fans_fan_ph {
    height: 35px;
    width: 35px;
    display: block;
}
.ui_zoom {
    position: absolute;
    bottom: -30px;
    background: #333;
    background: rgba(0, 0, 0, 0);
    width: 35px;
    height: 30px;
    box-sizing: border-box;
    padding: 9px 0 6px;
    -o-transition: bottom 0.2s ease;
    transition: bottom 0.2s ease;
}
.fans_fanph_wrap, .fans_idolph_wrap {
    position: relative;
    width: 35px;
    height: 35px;
    margin: 0 auto;
    overflow: hidden;
}
.media_voting_long_question .media_voting_question {
    line-height: 32px;
    max-height: 96px;
    font-size: 14px;
}
}
