css:
@-moz-document domain("stips.co.il")
{
  :root
  {
    --1: #232931;
    --2: #393e46;
    --2-5: #434850;
    --3: #4ecca3;
    --4: #ccc;
  }

  body,html
  {
    background-color: var(--1)!important;
  }

  .navigation-topbar[_ngcontent-c5] mat-toolbar[_ngcontent-c5]
  {
    background-color: var(--2);
  }

  .navigation-topbar[_ngcontent-c5] mat-toolbar[_ngcontent-c5] button[_ngcontent-c5]
  {
    color: var(--3);
  }

  .navigation-topbar[_ngcontent-c5] .count-badge[_ngcontent-c5]
  {
