  }

  .items-box.theme-four-on-one[_ngcontent-c20] .items-box-item[_ngcontent-c20] .item-card[_ngcontent-c20] .content-container[_ngcontent-c20] .title-wrapper[_ngcontent-c20],.items-box.theme-four-on-one[_ngcontent-c20] .items-box-item[_ngcontent-c20] .item-card[_ngcontent-c20],.item-card[_ngcontent-c26]
  {
    background-color: var(--2);
  }

  .item-card[_ngcontent-c26] .bottom[_ngcontent-c26]
  {
    background-color: var(--2-5);
  }

  .items-box.theme-four-on-one[_ngcontent-c20] .items-box-item.theme-small-circle-image[_ngcontent-c20]:nth-of-type(n+2) .item-card[_ngcontent-c20] .item-image[_ngcontent-c20]
  {
    border-color: var(--2);
  }

  .info-icons[_ngcontent-c27]
  {
    color: var(--3);
  }

  #app-component,.item-card[_ngcontent-c26] a.content-container[_ngcontent-c26]:hover,.item-card[_ngcontent-c26] a.content[_ngcontent-c26]:hover,span[_ngcontent-c20].title-wrapper:hover span[_ngcontent-c20].title,span[_ngcontent-c20].title:hover,:hover span[_ngcontent-c20].title
  {
    color: var(--4)!important;
  }

  .items-section[_ngcontent-c17] .section-title[_ngcontent-c17]
  {
    color: var(--3);
  }

  .mat-fab.mat-accent,.mat-flat-button.mat-accent,.mat-mini-fab.mat-accent,.mat-raised-button.mat-accent
  {
    background-color: var(--3);
  }

