  {
    color: var(--4);
  }

  .elastic-layer[_ngcontent-c6]
  {
    background-color: var(--1);
  }

  .voteButton.selected[_ngcontent-c34] .voteIcon.up[_ngcontent-c34],.score[_ngcontent-c34]
  {
    color: var(--3);
  }

  section[_ngcontent-c26]
  {
    color: var(--4);
  }

  .page-item-card[_ngcontent-c43] .text-content[_ngcontent-c43]
  {
    background: var(--2-5);
  }

  .page-item-card[_ngcontent-c43] .bottom[_ngcontent-c43]
  {
    background-color: var(--2);
  }

  .feed-container[_ngcontent-c21] .topic-area[_ngcontent-c21] .section-title[_ngcontent-c21]
  {
    color: var(--3);
  }

  .feed-container[_ngcontent-c21] .topic-area[_ngcontent-c21] .section-title[_ngcontent-c21] .section-title-line[_ngcontent-c21],.items-section[_ngcontent-c17] .section-title[_ngcontent-c17] .section-title-line[_ngcontent-c17]
  {
