css:
@-moz-document domain("isod.ee.pw.edu.pl") {
/* Whole page */
a {
	color: #ccc;
	transition: color .4s;
}

a:hover {
	color: #fff;
}

.container {
	background-color: #181818;
	background-image: none;
}

.header,
.footer {
	background-color: #333;
	color: #888;
}

.red {
	color: #8c1317;
}

.page {
	background-color: #222;
	color: #888;
}


/* Buttons */
.button.blue {
	background: #181818;
	border-color: #333;
	color: #ccc;
	transition: background .4s;
}

.button.blue:hover {
	background: #333;
	border-color: #333;
	color: #ccc;
}

.actions > li > a {
	background-color: #181818;
	transition: background .4s;
}

.actions > li > a:hover {
	background-color: #333;
}

.actions > li > a > span.descr {
	color: #888;
}

.actions > li > a.more {
	background-color: #181818 !important;
	transition: background .4s;
}

.actions > li > a.more:hover {
	background-color: #333 !important;
}


/* Plan of classes */
.plan-item {
	border: 2px solid #888 !important;
}

.panel-center {
	background: #181818;
}

.editable-plan-semester span,
.editable-label span {
	border: 1px solid #ccc;
}


/* Subpages */
ul.crumbs,
.grid > tr {
	background: #191919;
}

.grid > tr.even {
	background: #222;
}

.crumbs > li > a {
	background-image: none;
	color: #ccc;
	transition: color .4s;
}

.crumbs > li > a:hover,
.crumbs > li > a:active {
	color: #fff;
}

.headers,
.table-header,
.navigation,
.filters-tr,
.filters-btn,
.panel,
.panel-center,
.panel-pad,
.quick-filter-panel {
	background: #333;
}

.less-significant-text {
	color: #aaa;
}

.passed {
	background: #4e7d0e;
	color: #ccc;
}

.failt {
	background: #8c1317;
	color: #ccc;
}

.not-attended,
tr.not-attended > td {
	background: #222;
	color: #ccc;
}

.attended {
	background: #555;
	color: #ccc;
}
}

@-moz-document url("https://isod.ee.pw.edu.pl/isod-stud/signin") {
.login .container,
.page,
.header {
	background-color: #181818;
}

label {
	color: #ccc;
}
}
