css:
@-moz-document domain("eksisozluk.com")
{


::selection {
    background: #aab0b4;
    color: #fff;
}

body {
    background-color: rgb(34,41,49);
    color: #c8c9ca;
}



#content-body a {
    color: #4073a7;
}

#content-body a:hover {
    color: #2ba5d4;
}

#top-navigation a.new-update  {
    background: #4073a7;
}

#logo a {
    color: #4073a7;
    background-image: url('https://image.ibb.co/fcXzmx/Ek_i_S_zl_k_logo.png');
	background-position: 85px 60%;
    background-size: 125px 21px;
}

#index-section a:hover {
    color: #fff;
}

#search-form.inline button {
    background: #4073a7;
    margin-top: 2px;
}

#search-form #search-textbox {
    color:#d5d5d5;
    background: #3f454b;
    border: 1px solid #56616d;
    margin-left: 100px;
    margin-top: 2px;
}

#sub-navigation.loggedin li {
    color: #2ba5d4;
}

body > header {
    border-top: 0px;
    border-top: 3px solid #4073a7;
}

#sub-navigation > ul > li.active > a {
    color: #4073a7;
    border-color: #4073a7;
    text-decoration: none;
}

html.no-touch #sub-navigation>ul>li:hover>a, html.no-touch #sub-navigation>ul>li.active>a {
    color: #2ba5d4;
    border-bottom: 3px solid #7FB43D;
    text-decoration: none;
}

#content-body .topic-list li.new-update a {
    color: #2ba5d4;
    font-weight: bold;
}

#content-body .topic-list li.new-update a:hover {
    color: #DFE0E1;
    font-weight: bold;
}

#top-bar>#advanced-search-form {
    padding: 10px;
    background-color: #DFE0E1;
    border: 1px solid #333;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    color: #333;
    position: absolute;
    z-index: 1000;
	margin-left: 190px;
    margin-top: 2px;
    width: 300px;
    display: none;
    left: 270px;
    top: 50px;
}

#top-bar>#advanced-search-form div {
    margin-left: 33px;
}

#top-bar>#advanced-search-form button {
    margin-left: 45px;
}

#content-body .topic-list li a:hover {
    color: #DFE0E1;
}

#adrazzi-sponsored-entry { display:none }

#sticky-ad, .ad-banner, .ad-728x90, .ad-300x250, .ads, 
.ad-doubleclickwebsponsoredentry, 
.ad-doubleclickwebhomepagesponsoredentry, 
.ad-doubleclickwebinterstital, 
#web-sponsored-entry, #div-gpt-ad-1458669134912-0
data-id#5637399 {
    display: none;
}

aside  .vertical textarea, aside .vertical label + textarea {
        width: 300px;
        height: 50px;
}

aside {
    padding-top: 20px;
}
}
