css:
@-moz-document domain(fivethirtyeight.com) {
:root {
    --color-font: #ddd;
    --color-borders: #666;
}

a, 
p,
.nav-global-menu-item > a,
.promo .head,
.promo a,
.btn-cta,
.table-container .th-caption,
.caption,
.head,
#promo-index .head,
#promo-index .caption,
.button,
#intro .byline a, 
#intro .byline a:visited, 
#intro h1,
#intro .article-subtitle,
.nav .toggle-label-text,
h1, h2, h3, h4, h5, h6, 
.team-name,
#related h2 a,
.promo td.score,
.nav .is-index .select-teams,
.nav .select-teams, 
.long-form .datetime, .long-form .single-metadata {
	color: var(--color-font)  !important;   
}

.nav-global-menu-item > a:hover,
.header-just-updated {
    color: #ccc;
}

.header-global-wrapper,
.header-global,
.nav-global,
.header-global-top,
.header-global-bottom,
.page-header,
.header-global-bground {
	background: #222 !important;
    color: var(--color-font) !important;
}

.page-header {
    border-bottom: 5px solid #333;
}

.header-global .site-logo,
.search-button,
.chart,
.logo {
   filter: invert(.75);
}

.header-global-wrapper {
    height: 95px !important;
}

.site-main::before {
    content: '\b';    
}

#intro::before {
    line-height: 3;
    content: '\b';    
}

body,
.site-main,
#wrapper,
#main .page-header,
table.match-card .buffer,
.entry-comments-content {
	background: #333 !important;
    color: var(--color-font);
}

.large-featured-video-info,
.large-featured-video-info p, 
.large-featured-video-info a{
	color: #333 !important;
}

.single-fte_features .widget.embed iframe {
    background: transparent;
    border: 0px;
}

.entry-content a, 
.entry-content a:visited, 
.liveblog-feed a, 
.liveblog-feed a:visited,
#intro .see-also a,
.additional-credits a:active, 
.additional-credits a:focus, 
.additional-credits a:hover, 
.notes a, 
.notes a:active, 
.notes a:focus, 
.notes a:hover, 
.sources a:active, 
.sources a:focus, 
.sources a:hover {
    color: #afe5ff;
}

.single-fte_features .widget.fivethirtyeight-sidebar-ad {
    background: transparent;
}

body:not(.via-espn) .col-abc.more-features .-textonly, 
body:not(.via-espn) .col-abc.more-features .-textonly:hover, 
.nav .toggle-label, 
thead th.lower, 
.tags .tag,
.tease-podcast .podcast-content,
.tease-podcast:focus .podcast-content,
.tease-podcast:hover .podcast-content,
.vertical .featured-banner.-textonly,
table.match-card td.odds, 
table.match-card td.score,
.nav .form-page,
.nav .form-page:hover,
.nav .form-page .option,
.nav .is-index .select-teams .option,
.home-teases-primary-secondary .hentry.-textonly .post-info,
.home-teases-primary-secondary .hentry.-textonly .post-info:hover {
    background: #444;
    color: var(--color-font) !important;
}

.nav .toggle-radio:checked + .toggle-label,
thead th.lower.sorted,
table.viz td.highlight,
tbody tr.eliminated td,
.newsletter-input,
.header-global .search-form-input, .header-global .search-form-input-wrapper,
.nav .is-index {
    background: #555 !important;
    color: var(--color-font);
}

.nav .toggle-radio:hover + .toggle-label,
.nav .toggle-radio:checked:hover + .toggle-label, 
thead th.lower:hover{
    background: #666;
    color: var(--color-font);
}

thead th.border-right, 
thead th.top.bordered-right,
thead th.upper{
    border-right: 10px solid transparent !important;
}

thead th.logo, thead th.lower {
    border-bottom: 2px solid #cdcdcd;
}

tbody tr td.num,
table.viz td.heat{
	filter:  invert(1);
    color: #000 !important;
}

tbody tr.eliminated td.num {
	filter: invert(0);
}

.row-ab .col-a,
#primary,
table.match-card td.flag {
	border-right: 1px solid var(--color-borders);
}

table.match-card td.odds, 
table.match-card td.score, 
table.match-card td.team-name, 
table.match-card td.tie-text {
    border-top: 2px var(--color-borders) solid;
    border-bottom: 2px var(--color-borders) solid;
    border-left: 2px var(--color-borders) solid;
    border-right: 2px var(--color-borders) solid;
}

.home #primary .col-a .hentry,
.widget-every-data .hentry,
.interactives,
.section-title,
.widget-title,
.promo .caption,
.table-container .th-caption, 
.table-container .th-live, 
.table-container h5,
.table-container tbody,
footer.viz,
.tags .filed-under,
#entry-comments h3, 
.entry-footnotes h3,
.content-area .hentry,
.widget.fivethirtyeight-latest-stories .hentry, 
.widget.fivethirtyeight-most-popular-stories .hentry, 
.widget.widget-best-of .hentry {
	border-bottom: 1px solid var(--color-borders);
}

.home-featured-video,
header.viz {
    border-top: 1px solid var(--color-borders);
}

.single-fte_features #secondary, .single-fte_features div.filler {
    border-left: 1px solid var(--color-borders);
}

.home #secondary .fivethirtyeight-sidebar-ad, 
.tax-fte_verticals #secondary .fivethirtyeight-sidebar-ad {
    border-bottom: 0px;
    margin-bottom: 0px;
}
}
