css:
/* i really want this to be global */
body, body.main, body.side, #side_footer {
    background-color: #303030;
    background-image: inherit;
    background-attachment: inherit;
    background-repeat: inherit;
}

div.snapin div.content {
    background-image: none;
    background-color: #656565;
    margin-left: 5px;
    width: 219px;
}

table.nform {
    background-image: none;
    background-color: #656565;
    border-color: inherit;
}

div.dashlet_inner div.stats, .dashlet .dashlet_inner.background, table.tacticaloverview td, .wato table.data th, table.nform tr.heading > td {
    background-color: gray;
    
}

div.dashlet table.hoststats td, table.performance {
    background-color: #191919;
}

div.contextlink, label.image_button {
    background-image: none !important;
    background-color: gray;
    border-radius: 4px 4px;
    box-shadow: 1px 2px black;
    margin: 0 2px;
}

div.snapin div.footnotelink a {
    background-image: none !important;
    background-color: gray;
    border-radius: 3px;
}

label.image_button input, div.floatfilter {
    background-image: none;
    border-color: inherit;
}

#side_header, #side_footer, #snapin_search,
div.head, div.foot, div.footnotelink,
#filters_on, #painteroptions_on, #commands_on, #checkbox_on
{
    filter: grayscale(100%);
}

.wato div.inherited b,
table.nform > tbody > tr td,
div.dashlet table.hoststats a,
#data_container table.data a,
#data_container table.data td.age.recent,
#data_container table.data
{
    color: white;
}

#data_container td.icon img {
    filter: drop-shadow(0px 0px 1px gray);
}

#data_container img.icon {
    filter: drop-shadow(0px 0px 1px gray);
}

b.stmark.state1 {
    box-shadow: 1px 1px 2px yellow;
}

b.stmark.state2 {
    box-shadow: 1px 1px 2px red;
}

b.stmark.state3 {
    box-shadow: 1px 1px 2px orange;
}

td.state {
    background-color: black;
    font-size: 13px;
    letter-spacing: 2px;
    border: 1px solid white;
}

td.state0 {
    color: green;
}

td.state1 {
    color: yellow;
}

td.state2 {
    color: red;
}

td.state3 {
    color: orange;
}

tr.odd0, tr.odd1, tr.odd2, tr.odd3, tr.oddp {
    background-color: #191919;
    color: #eee;
}

tr.even0, tr.even1, tr.even2, tr.even3, tr.evenp {
    background-color: #080808;
    color: white;
}
