@-moz-document url("https://www.freelancer.com"), url-prefix("https://www.freelancer.com") {
/* ==UserStyle==
@name         Freelancer - Improved User Interface (Rash UI)
@description  Small theme that reduces de amount of Promos, Ads and Banners. Also reduces the Tricky Buttons that makes you pay.
@version      1.0.0
@author       David R. Mier
@homepageURL  https://www.behance.net/DrMier
@supportURL   Please write on the comments sections.
@license      Attribution-NonCommercial-NoDerivatives 4.0 International
==/UserStyle== */




/* ::::::::::::::::: */
/* :: ADS SECTION :: */
/* ::::::::::::::::: */

/* Dashboard - Top Ad */
div#skills-lab-banner.fl-banner.fl-banner-compact.skills-lab-banner{
    display: none;
}
/* Dashboard - Skills Top Promo */ 
.gaf-container .fl-banner-compact .fl-banner-button, .gaf-container .fl-banner-compact .fl-banner-content, .gaf-container .fl-banner-compact .fl-banner-image {
    display: none;
}
/* Dashboard - Upsell Corporate Top Promo */
.gaf-container .DashboardPromotionBanner:not(:empty) {
    display: none;
}

/* Job Description Section - Top Ad Upsell Ad */
.gaf-container .upsell-banner.wrapper {
    display: none;
}


/* ::::::::::::::::::::::::::::::::: */
/* :: TRICKY MODAL PROPOSAL POPUP :: */ 
/* : Offers highlight for 4 Dollars: */

div#sponsorUpsellModal.modal.hide.fade.LiteModal.in div.LiteModal-inner div.LiteModal-footer div#acceptSponsorBtn.LiteModal-footerItem {
    height:10%;
}
/* Money Hungry Button Text */
#acceptSponsorBtn > button:nth-child(1){
    background: -webkit-linear-gradient(top,#139ff0 0,#0087e0 100%);
    background: linear-gradient(to bottom,#139ff0 0,#0087e0 100%);
    border: 1px solid #0087e0;
    color: #F7F7F7;
    font-weight: lighter;
    text-shadow: 0 -1px transparent;
    font-size: 0.775rem;
}
#acceptSponsorBtn > button:nth-child(1):hover{
    border: 1px solid #f00;
    background: red;
    color: #F7F7F7;
    font-weight: 900;
    font-size: 0.975rem;
}


/* Cancel the payment and Continue Option corrected */
.gaf-container .LiteModal-cancelAction {
     font-variant: small-caps;
    font-size: 20px;
    font-size: 1.475rem;
    font-weight: 800;
    line-height: 1.80;
    color: #2b3340;
}
    

/*::::::::::::::::::*/  
/*:: CHAT SECTION ::*/
/*::::::::::::::::::*/
/* DELETE EVERYTHING BELOW TO ENABLE IT AGAIN */
/* Chat Side App */ 
webapp-compat-messaging.ng-star-inserted > app-messaging:nth-child(1){
    display: none;
}




}
