@-moz-document url-prefix('https://web.whatsapp.com'), url-prefix('https://web.whatsapp.com') {
	/*User Image Container*/
	 html[dir] ._1WliW {
	    padding: 5px;
	    border-radius: 10px;
	    background: #a2c5da;
	    background: -moz-linear-gradient(top, #a2c5da 0%, #94cdb3 19%, #94cdb3 19%, #80e88d 57%, #00ff40 100%);
	    background: -webkit-linear-gradient(top, #a2c5da 0%, #94cdb3 19%, #94cdb3 19%, #80e88d 57%, #00ff40 100%);
	    background: linear-gradient(to bottom, #a2c5da 0%, #94cdb3 19%, #94cdb3 19%, #80e88d 57%, #00ff40 100%);
	    transform: scale(0.7);
	    -webkit-transform: scale(0.7);
	}
	/*User Image*/
	 html[dir] .Qgzj8 {
	    border-radius: 3px;
	}
	/*User Default Image*/
	 ._3ZW2E svg {
	    background-color: #DDE3E7;
	    border-radius: 5px;
	    width: calc(100% - 10px);
	    height: calc(100% - 10px);
	    margin: 5px;
	}
	/*Header Chat*/
	 ._1Iexl #main header,
	/*Header Profile*/
	 #side > header {
	    height: 45px;
	    background: #85d0ff;
	    background: -moz-linear-gradient(top, #85d0ff 0%, #60b0e4 40%, #439bd8 53%, #439bd8 53%, #5ba9dc 100%);
	    background: -webkit-linear-gradient(top, #85d0ff 0%, #60b0e4 40%, #439bd8 53%, #439bd8 53%, #5ba9dc 100%);
	    background: linear-gradient(to bottom, #85d0ff 0%, #60b0e4 40%, #439bd8 53%, #439bd8 53%, #5ba9dc 100%);
	    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.3);
	}
	/*Header font*/
	 header span:not([data-icon="image"]) {
	    color: #fff;
	}
	/*Header icons*/
	 header span:not([data-icon="image"]) > svg > path {
	    fill: #fff;
	}
	/*Search icon*/
	 .C28xL ._3M2St svg {
	    background-image: url(https://vignette.wikia.nocookie.net/logopedia/images/7/7b/Windows-Live-Messenger-2009-14.0.8117.png/revision/latest?cb=20120529142106);
	    background-size: 24px 24px;
	}
	/*Search icon svg remove*/
	 .C28xL ._3M2St svg > path {
	    visibility: hidden;
	}
	/*Right side*/
	 html[dir=ltr] ._1GX8_ {
	    background: #5ba9dc;
	    background: -moz-linear-gradient(top, #5ba9dc 0%, #96cfee 10%, #f3fafc 15%, #f3fafc 100%);
	    background: -webkit-linear-gradient(top, #5ba9dc 0%, #96cfee 10%, #f3fafc 15%, #f3fafc 100%);
	    background: linear-gradient(to bottom, #5ba9dc 0%, #96cfee 10%, #f3fafc 15%, #f3fafc 100%);
	}
	/*Icon background*/
	 .YUoyu {
	    opacity: 0.03;
	}
	/*Chat*/
	 ._3zJZ2 {
	    /* margin-top: 4%; */
	}
	/*Chat footer*/
	 html[dir] ._3oju3 {
	    background: #f3fafc;
	    background: -moz-linear-gradient(top, #f3fafc 0%, #edf9fd 63%, #def4fc 75%, #def4fc 75%, #ccedf9 86%, #cfe9f6 100%);
	    background: -webkit-linear-gradient(top, #f3fafc 0%, #edf9fd 63%, #def4fc 75%, #def4fc 75%, #ccedf9 86%, #cfe9f6 100%);
	    background: linear-gradient(to bottom, #f3fafc 0%, #edf9fd 63%, #def4fc 75%, #def4fc 75%, #ccedf9 86%, #cfe9f6 100%);
	}
	/*Chat input*/
	 html[dir] ._2bXVy {
	    border-color: #c0c0c0;
	    border-bottom-left-radius: 0;
	}
	/*Chat - Arrow border*/
	 ._2bXVy:before {
	    content:'';
	    position: absolute;
	    left: 44px;
	    bottom: 10px;
	    z-index: 9;
	    border-top: 0px solid transparent;
	    border-right: 0px solid transparent;
	    border-bottom: 15px solid #c0c0c0;
	    border-left: 20px solid transparent;
	}
	/*Chat - arrow*/
	 ._2bXVy:after {
	    content:'';
	    position: absolute;
	    left: 47px;
	    bottom: 11px;
	    z-index: 9;
	    border-top: 0px solid transparent;
	    border-right: 0px solid transparent;
	    border-bottom: 15px solid #fff;
	    border-left: 20px solid transparent;
	}
	/*Footer emoticon*/
	 html[dir=ltr] ._2Fofa svg {
	    background-image: url(https://web.whatsapp.com/img/04d6bfbf4caebc9d0c6143cd3daa6665.webp);
	    background-size: 130px 130px;
	    background-position: -105px -79px;
	    border-radius: 50%;
	}
	/*Footer emoticon svg remove*/
	 html[dir=ltr] ._2Fofa svg > path {
	    visibility: hidden;
	}
	/*Chat message - out*/
	 /*html[dir=ltr] .message-out,*/
	/*Chat message - in*/
	 /*html[dir] .message-in {
	    box-shadow: none;
	    background: transparent;
	}*/
	/*Chat message - out (arrow)*/
	 /*html[dir=ltr] .message-out .tail-container,*/
	/*Chat message - in (arrow)*/
	 /*html[dir] .message-in .tail-container {
	    visibility: hidden;
	}*/
	/*Chat message - Sender name*/
	 /*html[dir=ltr] .Tkt2p .copyable-text:before {
	    content: attr(data-pre-plain-text);
	    color: #b0b0b0;
	    margin-bottom: 3px;
	    margin-left: -5px;
	    display: inline-block;
	}*/
	/*Chat selected*/
	 html[dir] ._2EXPL._1f1zm {
	    background-color: #D3EDF6;
	}
	/*All left side borders*/
	 div#side * {
	    border-color: #B8E2FD !important;
	}
	/*Contacts border remove*/
	 html[dir] ._3j7s9 {
	    border-top: none;
	}
}
