css:
/*
*
* Simplenote Doff Dark Theme v1.04
* Last updated: 09/07/2018
* Created by semarngaji
* https://userstyles.org/users/669719
*
*/
@-moz-document domain(app.simplenote.com) {


/* Core */
body {    
    position: fixed;
    top: 0;
    left: 0;
    bottom: -1px;
    right: 0;
    background: #333;
}
a:link, a:visited, a {
    color: #999;
}
.wrapper, .app {
    bottom: 0;
    background:transparent;
}
.toolbar {
    left: 0;
    border-top: 1px solid #555;
    border-bottom: 1px solid #222;
    background:#333;
}
.searchfield {
    border: 1px solid #222;
    box-shadow: 0 1px 0 #555;
    color: #999;
}
.sidebar {
    background: #333;
    width: 300px;
    border-right: 1px solid #555;
}
.sideview {
    border-bottom: 1px solid #555;
    z-index: 1;
}
.notes {
    top: 74px;
}
.notes li {
    text-shadow:none;
    border-top: 1px solid #555;
    border-bottom: 1px solid #222;
    border-right: 1px solid #222;
    padding: 12px 10px;
}
.notes li.selected, .notes li:hover {
    background: #333;
    font-weight: bold;
}
.notes li .note-preview-title {
    color:#999;
    width: 246px;
}
.notes li:hover .note-preview-title {
    font-weight:bold;
}
.notes li .note-preview-line {
    width: 246px;
}
.notes .with-0-preview-lines {
    color:#999;
}
.tagbar {
    background: #333;
    border-bottom: 1px solid #555;
    padding-left: 10px;
    z-index: 1;
}
.as-selections .as-original input {
    border-bottom: 1px solid #555;
}
ul.as-selections li.as-original input {
    color: #999;
}
.note {
    background: #222;
    left: 301px;
    top: 74px;
    padding-top: 0;
}
.note #txtarea, .note #historytxt {
    color: #999;
    font-size: 16px;
    font-family: 'Courier New' !important;
    padding: 20px 30px;
}
.footer-right:after{
    content: 'Doff Dark Theme by semarngaji';
    color: #999;
    font-weight: bold;
    padding: 3px 5px;
    border-radius: 2px;
    opacity: 0.75;
}
.footer-right li {
    display:none;
}
::-webkit-scrollbar { 
    display: none;
}

/* Sign in, sign up, forgot password, settings */
h1.logo a {
    color: #448ac9;
}
h1 {
    text-shadow: none;
}
#fullpage {
    background: #333;
    border-top: 1px solid #222;
}
.page {
    border: none;
}
.inner {
    background: transparent;
}
#login input[type="text"], #login input[type="email"], #login input[type="password"] {
    background: #333;
    color: #999;
    border-top: 1px solid #222;
    border-left: 1px solid #222;
    border-right: 1px solid #555;
    border-bottom: 1px solid #555;
}
#login input[type="text"]:focus, #login input[type="email"]:focus, #login input[type="password"]:focus{
    background: #333 !important;
    color: #999 !important;
}
#login .submit.button, #register .submit.button {
    width: 364px;    
    background: #333;
    color: #999;
    border-top: 1px solid #555;
    border-left: 1px solid #555;
    border-right: 1px solid #222;
    border-bottom: 1px solid #222;
}
.easy-form.account .terms, .account#accountsettings .terms {
    color: #999;
}
.easy-form label.option, #accountsettings label.option {
    color: #999 !important;
}
#accountsettings {
    height: 512px;
    overflow-y: scroll;
    padding: 0 20px;
}
.editing input[type="email"], .editing input[type="button"], .editing input[type="submit"], .editing input[type="password"] {
    border-radius: 3px;
    background: #333;
    border: 1px solid #222;
    box-shadow: 0 1px 1px #555;
    padding: 3px;
    color: #999;
    margin: 2px 0;
    width: 120px;
}
.editing input[type="submit"]{
    width: 128px;
}
#settings .setting-button, #settings .delete-setting-button {
    border-radius: 3px;
    background: #333;
    padding: 3px;
    color: #999;
    margin: 2px 0;
    border-top: 1px solid #555;
    border-left: 1px solid #222;
    border-right: 1px solid #222;
    border-bottom: 1px solid #222;
    box-shadow: none;
}
select {
    border-radius: 3px;
    background: #333;
    border: 1px solid #222;
    box-shadow: 0 1px 1px #555;
    padding: 3px;
    color: #999;
    margin: 2px 0;
    width: 128px;
}
.warn, .flash-error {
    color: #999;
    font-style: italic;
}
.cookiebanner{
  display: none;
}
}
