css:
@-moz-document domain(jira.infor.com) {
.alertHeader { visibility: hidden; height: 0; padding: 0 !important }
.ghx-plan-extra-fields-2 { margin-bottom: 1px !important; font-size: 0.8em; color: rgb(153, 153, 153); }
.ghx-extra-field-seperator { visibility: hidden;  width: 6px !important; padding: 0 !important; }
.ghx-extra-field { color: rgb(153, 153, 153) }

/* From Jira Compact Sprint Board and Backlog */
div.sticky-wrapper > div > div.ghx-sprint-info.aui-group.aui-group-split,
.ghx-extra-field-row .ghx-extra-field[data-tooltip="Labels: None"],
.ghx-issue-content .ghx-plan-extra-fields .ghx-extra-field[data-tooltip="Labels: None"],
.ghx-type-6 .ghx-extra-field-row .ghx-extra-field[data-tooltip="Component/s: None"],
.ghx-type-6 .ghx-plan-extra-fields .ghx-extra-field[data-tooltip="Component/s: None"] {
    display: none;
}
.ghx-mode-planning span.subnav-page-header,
.ghx-mode-planning #ghx-view-presentation {
    display: none;
}
#ghx-backlog-search {
    left: 5px;
    margin: 3px 0 0 0;
}
#jira:not(.ghx-header-compact) #ghx-controls-plan,
#jira:not(.ghx-header-compact) #ghx-controls-work {
    margin-top: -9px;
}
#ghx-backlog-search .ghx-search {
    height: 20px;
}
.ghx-marker{
	height: 32px;
}
#ghx-backlog-search .aui-icon {
    top: 2px;
}
.ghx-mode-planning #ghx-header {
    padding: 14px 12px 10px 24px;
    margin-bottom: 0px;
}
.ghx-mode-planning #ghx-modes-tools {
    margin-top: -3px;
}
.ghx-mode-planning .ghx-issue-compact .ghx-row {
    height: 14px;
}
.ghx-mode-planning .ghx-issue-compact {
    font-size: 13px;
}
.ghx-mode-planning .ghx-backlog-header {
    padding-top: 6px;
    min-height: 29px;
}
.ghx-mode-planning .ghx-backlog-container.ghx-open .ghx-backlog-header {
    height: 10px;
}
.ghx-mode-planning .ghx-assigned-work-stats {
    display: none;
    padding: 0px 20px;
}
.ghx-mode-planning #ghx-header h1,
.ghx-mode-planning #ghx-header h2,
.ghx-mode-planning span.subnav-container {
    height: 1.1em;
}
.ghx-mode-planning .ghx-backlog-container .ghx-issues {
    margin: 0px 20px;
}
span.subnav-trigger {
    font-size: 22px;
}
span.subnav-page-header {
    display: none;
}
div.ghx-swimlane .ghx-first {
    padding-top: 20px;
}
div.ghx-column-header-group .ghx-fixed {
    top: 141.865px;
}
.ghx-agile .aui-button.aui-button-compact.js-sprint-actions-trigger,
.js-sprint-start.aui-button.disabled,
.js-sprint-start.aui-button,
.js-add-sprint.aui-button{
	padding-top: 1px;
    padding-bottom: 1px;
    height: 1.5em;
    margin-top: 5px;
}
.ghx-sprint-planned .ghx-backlog-header .aui-group.ghx-controls{
	margin-top: -4px;
}
#ghx-header {
    padding: 10px 0px 0px 20px;
    margin-bottom: 8px;
}
#ghx-column-headers {
    padding-top: 0px;
}
.ghx-column-headers .ghx-column {
    padding: 10px 10px 0 0;
}
#ghx-column-headers h2 {
    font-size: 12px;
}
.ghx-swimlane-header {
    line-height: 0.75;
    font-size: 12px;
}
.ghx-issue {
    font-size: 12px;
}
.ghx-editable {
    padding: 1px 25px 1px 6px;
}
.ghx-parent-stub,
.ghx-backlog-container .ghx-name input {
    padding: 0;
}
/*
.aui-label {
    display: none;
    font-size: 12px;
}*/

.ghx-issue .ghx-highlighted-fields {
    display: none;
    margin-top: 5px;
}
.ghx-issue .ghx-issue-content {
    min-height: 0px;
}
.ghx-issue .ghx-extra-fields {
    position: absolute;
    left: 115px;
    top: 9px;
}
.ghx-extra-fields {
    margin-top: 0px !important;
}
.ghx-issue .ghx-end {
    top: 10px;
    right: 50px;
}
.ghx-issue .ghx-summary {
    margin-right: 25px;
}
.js-compact-toggle {
    display: none;
}
.ghx-sprint-meta .time {
    display: none;
}
.ghx-issue .ghx-extra-fields .ghx-extra-field-row {
    float: left;
    margin-right: 12px;
}
#ghx-report {
    display: inline-block;
}
.ghx-quick-content.aui-expander-content {
    padding-top: 0px;
    margin-bottom: 0px;
}
.ghx-controls-plan,
.ghx-header-compact .ghx-controls-report,
.ghx-controls-work {
    min-height: 31px;
}
.ghx-controls-filters dd a {
    line-height: 0.5;
}
.ghx-issue-compact .ghx-plan-extra-fields .ghx-extra-field{
	overflow: inherit;
}
.ghx-parent-stub:not(.ghx-filtered)~.ghx-subtask-group{
	padding-bottom: 0;
}
/*  Un-comment below to hide quick filters from sprint board  */
/*
#ghx-column-headers {
    margin-top: -6px;
}
#ghx-column-header-group.ghx-fixed {
    margin-top: 0px;
    top: 95px !important;
}
.ghx-swimlane.ghx-first {
    margin-top: 7px;
}
.ghx-mode-planning #ghx-operations,
#ghx-operations {
    display: none;
}
#ghx-header {
	margin-bottom: 0px !important;
}
*/
}
