css:
@-moz-document domain(www.evernote.com), url-prefix('https://www.evernote.com/Home.action') {

:root {
    --black: rgba(0, 0, 0, 1);
    --sepia: rgba(251, 240, 217, 1);
    --dark-grey: rgba(33, 33, 33, 1);
    --black-five: rgba(0, 0, 0, 0.05);
    --vibrant-black: rgba(30, 14, 29, 1);
    --vibrant-grey: rgba(216, 203, 199, 1);
    --white-five: rgba(255, 255, 255, 0.05);
    --transparent-white: rgba(255, 255, 255, 0);
}
html {
    overflow: hidden;
}
body {
    -webkit-app-region: drag;
}
::-webkit-scrollbar {
    /* Notes list scrollbar */
    width: 6px !important;
}
::-webkit-scrollbar-track {
    /* Notes list scrollbar track */
    border-radius: 0px !important;
}
::-webkit-scrollbar-thumb {
    /* Notes list scrollbar thumb */
    border-radius: 0px !important;
    background: #2DBE60 !important;
}
#switch-link {
    /* Allow text selection on Macos */
    -webkit-app-region: no-drag;
}
div#gwt-debug-NoteView-root {
    -webkit-app-region: no-drag;
}
div#gwt-debug-notesListView {
    -webkit-app-region: no-drag;
}
div#gwt-debug-FocusView-root {
    /* Main app window background */
    background-color: white !important;
}
.GJDCG5CP4B {
    /* Elephant logo */
    left: 0;
    right: 0;
    bottom: 0;
    top: 25px;
    height: 66px;
    position: relative;
}
.GJDCG5CG5B {
    /* Sidebar buttons */
    margin: 20px 0px !important;
}
.FocusClipperButton-button {
    visibility: hidden !important;
}
.focus-NotesView-NotesView {
    background-color: #FFFFFF !important;
}
.focus-NotesView-RemindersList {
    background-color: #FFFFFF;
}
.GJDCG5COYB {
    /* Note list header */
    background-color: #FFFFFF;
    margin-bottom: -6px !important;
}
.GJDCG5CP3B {
    /* Profile background */
    background-color: #F8F8F8 !important;
}
.GJDCG5CE1B {
    /* Note list transition background */
    background-color: #FFFFFF !important;
}
div#gwt-debug-NotesHeaderView-root {
    background-color: #FFFFFF !important;
}
div#gwt-debug-NotesHeader-title {
    /* Notes list title */
    padding-bottom: 10px !important;
}
html.side-bar-hidden div#gwt-debug-sidebar {
    /* Make side bar visible */
    display: none !important;
}
html.side-bar-hidden div#gwt-debug-stage {
    margin-left: 0px !important;
}
html.side-bar-hidden-macos div#gwt-debug-NotesHeader-title {
    padding-top: 13px !important;
}
@media all and (max-width: 800px) {
    div#gwt-debug-NoteView-root {
        margin-left: 0px !important;
    }
    div#gwt-debug-notesListView {
        /* Auto-hide note list */
        display: none !important;
    }
}
::-webkit-scrollbar-track {
    /* Notes list scrollbar track */
    background: var(--dark-grey) !important;
}
div#gwt-debug-NotesHeader-title,
div#gwt-debug-NoteAttributesView-root {
    background-color: var(--dark-grey) !important;
}
.GJDCG5CJEB {
    /* Metting note background */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CPYB {
    /* Note list header */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CBR.GJDCG5CCR,
.GJDCG5CDR {
    opacity: 1 !important;
}
.Dropdown-menu {
    background: var(--dark-grey) !important;
}
.GJDCG5CMGB,
.GDAMOPFCAHB,
.GDAMOPFCPGB,
.GDAMOPFCHYC.GDAMOPFCLD {
    /* Chat windows */
    background: var(--dark-grey) !important;
}
.GJDCG5CKKC,
.GJDCG5CK-B {
    /* Reminder modal */
    background: var(--dark-grey) !important;
}
.GDAMOPFCHEB {
    background: var(--dark-grey) !important;
}
.GJDCG5CJ-B,
.GDAMOPFCEQB,
.GDAMOPFCDAC.GDAMOPFCM5B,
.GJDCG5CK-B .GJDCG5CE1B {
    /* Search modal */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CK-B,
.GJDCG5CDXB {
    border-bottom: 1px solid var(--white-five) !important;
}
.GJDCG5CEXB {
    border-bottom: 1px solid var(--dark-grey) !important;
}
.GJDCG5CGWB,
.GDAMOPFCIXB,
.GJDCG5CKXB.GJDCG5CHXB,
.GDAMOPFCFTB {
    /* Notebooks modal */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CDXB.GJDCG5CPXB {
    /* Selected notebook in notebooks modal */
    background-color: #2DBE60 !important;
}
.GJDCG5CJTB {
    /* Select notebook at toolbar option */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CJJ,
.focus-drawer-Filter-input {
    /* Search notebook/tags input text */
    color: #E1E1E1 !important;
}
div#gwt-debug-GlassModalDialog-container {
    /* Glass modal background */
    background-color: var(--dark-grey) !important;
}
div#gwt-debug-GlassModalDialog-glass {
    /* Glass modal bottom line */
    background-color: var(--dark-grey) !important;
}
.GDAMOPFCPE {
    /* Notebook info */
    color: #E1E1E1 !important;
}
.GDAMOPFCN-B .GDAMOPFCC1B,
.GDAMOPFCDQB {
    /* Saved search */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CJ3B,
.GJDCG5CH3B,
.GJDCG5CN5B,
.GJDCG5CGHB {
    /* Shortcuts modal */
    background-color: var(--dark-grey) !important;
    border-right: 3px solid var(--white-five) !important;
}
.GJDCG5CP2 {
    /* Settings menu */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CC5B {
    background: var(--dark-grey) !important;
    background-color: var(--dark-grey) !important;
    border-right: 1px solid var(--white-five) !important;
}
.GDAMOPFCJYB {
    /* Notes list head */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CHQB {
    /* Notes list left border */
    background-color: var(--dark-grey) !important;
    border-left: 1px solid var(--white-five) !important;
}
.GJDCG5CB-B {
    background-color: var(--dark-grey) !important;
}
.GDAMOPFCA5B {
    /* Notes list left border */
    border-left: 1px solid var(--white-five) !important;
    border-right: 1px solid var(--white-five) !important;
    background: var(--dark-grey) !important;
    background-color: var(--dark-grey) !important;
}
.focus-NotesView-Note.focus-NotesView-Note-selected .focus-NotesView-Note-selectOverlay {
    /* Selected note bordered box */
    border: 3px solid var(--white-five) !important;
}
.focus-NotesView-RemindersList {
    /* Reminders list borders */
    border-top: 2px solid var(--white-five) !important;
    border-bottom: 3px solid var(--white-five) !important;
    background-color: var(--dark-grey) !important;
}
.focus-NotesView-NotebookReminders-DropAreaContainer {
    /* Notebook reminder background */
    background: var(--dark-grey) !important;
}
.focus-NotesView-Subheader {
    /* Subheader border */
    border-bottom: 1px solid var(--white-five) !important;
}
.GJDCG5CLLB {
    background-color: var(--dark-grey) !important;
}
.GJDCG5CJKB,
.GJDCG5CA0B {
    /* Toolbar bottom border */
    border-bottom: 1px solid var(--white-five) !important;
}
.GDAMOPFCILB,
.GJDCG5CAOB {
    /* Note attributes background */
    background: var(--dark-grey) !important;
}
.GJDCG5CP0,
.GJDCG5CLKB,
.GJDCG5CCQB {
    /* Toolbar separators */
    background: var(--dark-grey) !important;
    border-left: 1px solid var(--white-five) !important;
}
.focus-NotesView-Note .focus-NotesView-Note-snippetDivider {
    /* Notes divider color */
    border-top: 1px solid var(--white-five) !important;
}
.GJDCG5CP3B {
    /* Profile background */
    background-color: var(--dark-grey) !important;
    border-top: 1px solid var(--white-five) !important;
}
.GDAMOPFCPYB {
    /* Note list transition background */
    background-color: var(--dark-grey) !important;
    border-top: 1px solid var(--white-five) !important;
}
.GDAMOPFCMYB {
    /* Note list header */
    background-color: var(--dark-grey) !important;
    margin-bottom: -6px !important;
}
div#gwt-debug-FocusView-root {
    /* Sidebar transition background */
    background-color: var(--dark-grey) !important;
}
.focus-NotesView-NotesView {
    /* Notes list body */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CNNB,
.GDAMOPFCOPB {
    /* Note editor body */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CKKB {
    /* Note editor toolbar */
    background-color: var(--dark-grey) !important;
    border-bottom: 1px solid var(--dark-grey) !important;
}
.GJDCG5CMKB {
    border-left: 1px solid var(--dark-grey) !important;
}
.GJDCG5CJ1 {
    /* Note editor toolbar options */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CONB {
    /* Note editor background */
    background-color: var(--dark-grey) !important;
}
.GJDCG5COF {
    /* Colors background */
    border: 1px solid var(--dark-grey) !important;
    background-color: var(--dark-grey) !important;
}
.GJDCG5CHK,
.GJDCG5CGK.GJDCG5CAK,
.GJDCG5COQ.GJDCG5CAR {
    /* Submenus background */
    background-color: var(--dark-grey) !important;
}
.GDAMOPFCNHC {
    /* Dialogs background */
    background-color: var(--dark-grey) !important;
}
.GJDCG5CFLB {
    /* Note editor head */
    background-color: var(--dark-grey) !important;
}
div#imggal-glass {
    /* Image gallery background*/
    background-color: var(--dark-grey) !important;
}
#imggal-top .close-icon {
    /* Image gallery exit button */
    border-radius: 0% !important;
    /* Makes the button square */
    background-color: var(--dark-grey) !important;
}
#tinymce.mce-content-body {
    color: lightgrey;
}
}
