css:
@-moz-document domain("scriptinghelpers.org") {
/* Default colors:
darkest - #000
even-darker - #080808
darker - #111
more-dark - #222
chat-notification - #002027
less-dark - #333
logo-background - #333
not-so-dark - #555
typing-text-in-chat - ???
notification - #888
grey-text - #888
light-grey - #aaa
less-lighter - #ddd
lighter - #eee
lightest - #fff

Lua Interpreter
highlighted - #001023
purple - #f0f
red - #f00
blue - #44f
selection - #00449c

If you are not familiar with css, this text above is for self reference and doesn't affect the code. This is all wrapped in a comment.
*/

/*Backgrounds*/
#panel, header, body, body.fullscreen .fullscreen-content, .login-area a, .topic .posts>li .post-content, .chat-disconnected-info i, .ace-crimson-editor, .header-container, .container-outer, .blog-post, .chat-pane, .chat-intro-inner, .notification-box, .cta, .modal-content {
    background: /*[[darkest]]*/;
}
.syntaxhighlighter .line.alt1 {
    background: /*[[darkest]]*/ !important;
}
.notif-select li:hover, .btn-group .btn-default:active, .btn-group .btn-default:focus, .btn-group .btn-default:hover, .hljs, .dropdown-menu {
    background: /*[[even-darker]]*/
}
.chat-donator {
    background: /*[[chat-notification]]*/;
}
.notification-icon {
    background: /*[[notification]]*/;
}
.divider {
    background: /*[[less-dark]]*/;
}
body.fullscreen .fullscreen-content .logo, .site-logo img {
    /*Temporary fix for logo*/
    background: /*[[logo-background]]*/;
}
.q-link .a:hover, .dropdown a:hover, .help-article a:hover, .question-service-links a:hover, .search-item a:hover, .pull-right>.dropdown-menu, .dropdown-menu .divider, .topic .posts>li .moderator-tools .dropdown-menu>li>a:hover, .glossary-word, .ace-crimson-editor .ace_print-margin {
    background: /*[[more-dark]]*/;
}
.center-text, .question-comment:hover, .help-article:nth-child(even), .categories>li .content .full-height-panel, .category>ul>li .content .full-height-panel, .categories>li .col-md-3 .full-height-panel, .category>ul>li .col-md-3 .full-height-panel, .topic .posts>li .moderator-tools .dropdown-menu, .notif-select li.selected, .ace-crimson-editor .ace_gutter, .panel-container, .account>.row, .groups.details .post-body, .alert-window .alert, .panel, .users-container .users-box, .question-status-box, .panel-container { /*.center-text,*/
    background: /*[[darker]]*/;
}
select, .syntaxhighlighter .line.alt2, .syntaxhighlighter .toolbar, .question-code-view-all:hover {
    background: /*[[more-dark]]*/ !important;
}
.qtip-blue {
    background-color: /*[[more-dark]]*/;
    border-color: /*[[not-so-dark]]*/;
}
.question-points img, .fa-check:before {
    background: /*[[lightest]]*/;
}
.btn-morph { /*Following color*/
    background: #311;
}
/*Borders*/
hr, .question-comment, .question-post-container {
    border-top: 1px solid /*[[lighter]]*/;
}
.syntaxhighlighter .toolbar {
    border: 1px solid /*[[more-dark]]*/ !important;
}
.glossary-word:hover {
    border-bottom: 1px solid /*[[darker]]*/;
}
.glossary-word h3, .blog-comment {
    border-bottom: 1px solid /*[[more-dark]]*/;
}
.header-container {
    border-bottom: 1px solid rgba(255, 255, 255, .1);
}
nav ul li.nav-active {
    border-bottom: 1px solid rgba(255, 255, 255, .4);
}
nav ul li:hover, .site-logo:hover {
    border-bottom: 1px solid /*[[lightest]]*/;
}
hr, .question-comment, .question-post-container, .modal-footer {
    border-top: 1px solid /*[[darker]]*/;
}
.chat-timestamp {
    color: /*[[light-grey]]*/;
    text-shadow: 1px 1px 2px #444;
}
.user-profile-info h3, .user-profile-reputation h3, .wide-line-title {
    border-bottom: 1px solid /*[[less-dark]]*/;
}
.notification, .settings .checkbox, .modal-header {
    border-bottom: 1px solid /*[[darker]]*/;
}
.question-comment-post {
    border-color: /*[[less-dark]]*/;
}
.format-btn:hover {
    border: 1px solid /*[[more-dark]]*/;
}
#chatinput:focus {
    border-top: 1px solid #596c82;
}
#studio #edit {
    border: 1px solid /*[[not-so-dark]]*/
}
select {
    border-color: /*[[even-darker]]*/ !important;
}
/*Text*/
.h1 .small, .h1 small, .h2 .small, .h2 small, .h3 .small, .h3 small, .h4 .small, .h4 small, .h5 .small, .h5 small, .h6 .small, .h6 small, h1 .small, h1 small, h2 .small, h2 small, h3 .small, h3 small, h4 .small, h4 small, h5 .small, h5 small, h6 .small, h6 small, .categories-title, .topic [component="post/anchor"], .topic [component="post/upvote"], .topic [component="post/downvote"], .ace_line .ace_rparen, .ace_line .ace_rparen, .ace_line .ace_lparen, .blog-catinfo, .btn-group .btn-default, .btn-group .open>.dropdown-toggle.btn-default, .category>ul>li .category-text-container h2.title>a, .categories>li .category-text-container h2.title a, .categories>li .teaser, .category>ul>li .category-text-container h2.title a, .category>ul>li .teaser, .stats span, .account .profile .account-stats .stat-label, .stats small, .posts-list .posts-list-item .topic-title, .breadcrumb>.active, .input-group-addon {
    color: /*[[light-grey]]*/;
}
 .muted, input:-ms-input-placeholder, textarea:-ms-input-placeholder, input::-ms-input-placeholder, textarea::-ms-input-placeholder, .nav-search:-ms-input-placeholder, .nav-search::-ms-input-placeholder {
    color: /*[[grey-text]]*/;
}
.account .profile .aboutme>p, .pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover, input::placeholder, textarea::placeholder, .nav-search::placeholder {
    color: /*[[grey-text]]*/;
}
input::placeholder, textarea::placeholder, .nav-search::placeholder { /*Style settings are giving an error, I had to seperate these*/
	opacity: 1;
}
.cta .icon-close, .account .profile .profile-meta strong, .dropdown-menu>li>a, .hljs, .hljs-keyword, .hljs-selector-tag, .hljs-subst {
    color: /*[[less-lighter]]*/;
}
.login-area a {
    color: /*[[lighter]]*/;
}
p, h1, h2, h3, h4, h5, small, code, .q-sort-type ul li:hover, .q-sort-type ul li.active, .chat-intro-reqs ul, .chat-intro-reqs ul li, .settings-radio ul li:hover, .small, .question-comment, .loggedin-avatar, .user-profile-info .info, .user-profile-badge, .ace-crimson-editor .ace_identifier {
    color: /*[[lightest]]*/;
}
div, nav ul li a, select, .syntaxhighlighter .plain, .syntaxhighlighter .plain a, .loggedin-name, .link-dark, .settings-radio-selected {
    color: /*[[lightest]]*/ !important;
}
.input-group-addon {
    border: 1px solid /*[[darker]]*/;
}
.fa-check:before {
    color: /*[[darkest]]*/
}
.q-list-points {
    color: rgba(255, 255, 255, .35);
}
/*Style settings soon for below, it's just that the code text colors aren't even completely done yet.*/
/*.hljs-title, .hljs-section, .hljs-selector-id {
    
}*/





/*Mixed properties or other*/
.form-control {
    color: /*[[lightest]]*/;
    background: /*[[darker]]*/;
}
.category>ul>li .tag-list .tag, .nav>li>a:hover {
    background: /*[[even-darker]]*/;
    color: /*[[light-grey]]*/;
}
.btn-default.active, .btn-default.focus, .btn-default:active, .btn-default:focus, .btn-default:hover, .open>.dropdown-toggle.btn-default {
    color: /*[[not-so-dark]]*/;
    background-color: /*[[more-dark]]*/;
}
blockquote {
    background: /*[[darker]]*/;
    border-left: 5px solid /*[[not-so-dark]]*/;
}
.dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover {
    color: /*[[less-lighter]]*/;
    background: /*[[darker]]*/;
}
 .qtip:not(.ie9haxors) div.qtip-content, .qtip:not(.ie9haxors) div.qtip-titlebar {
    color: /*[[lightest]]*/;
    background: /*[[more-dark]]*/;
}
input, textarea, .nav-search {
    background: /*[[darker]]*/;
    border: 1px solid /*[[darker]]*/;
    color: /*[[lightest]]*/;
}
.blog-catinfo, .btn-group .btn-default, .btn-group .open>.dropdown-toggle.btn-default {
    background: /*[[darker]]*/;
}
code {
    background: /*[[darker]]*/;
    border: 1px solid /*[[less-dark]]*/;
}
table, tr, th, td {
    border: 1px solid /*[[darker]]*/;
}
.button[disabled=disabled] {
    background: /*[[not-so-dark]]*/ !important;
    border-bottom: 1px solid /*[[not-so-dark]]*/;
}
.fade-out {
    background-image: -webkit-linear-gradient(top,rgba(16,16,16,0),#111);
}
.alert-info {
    background-color: #001925;
    border-color: #001925;
}
.pagination>li>a, .pagination>li>span, .pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover {
    background-color: /*[[darker]]*/;
    border: 1px solid /*[[even-darker]]*/;
}
.pagination>li>a:focus, .pagination>li>a:hover, .pagination>li>span:focus, .pagination>li>span:hover {
    background-color: /*[[even-darker]]*/;
    border-color: /*[[darkest]]*/
}
.ace-crimson-editor .ace_marker-layer .ace_active-line {
    background: /*[[highlighted]]*/;
}
.ace-crimson-editor .ace_string {
    color: /*[[purple]]*/;
}
.ace-crimson-editor .ace_support.ace_function {
    color: /*[[red]]*/;
}
.ace-crimson-editor .ace_keyword {
    color: /*[[blue]]*/;
}
.ace-crimson-editor .ace_marker-layer .ace_selection {
    background: /*[[selection]]*/;
}
}
