css:
@-moz-document url-prefix("https://animevost.org/") {
::-webkit-scrollbar{width:1px;height:1px;}
#wrap{margin: 10px 269px 10px 0px!important;}
/*Clean Block*/
div.headbgnew,div.headbgnewo,div.headbgnewfour,div.headbgnewthree,div.headbgnewtwo,div.headbgnewone{height:10px;background:none !important;}
div.menu{top: 0px!important;}
.banerTop,#buttonLike,.present,#banner,#vk_groups,.gwd-page-container,.interDub div.interDubBg,#stext,.shortstoryContent table td p+div,.shortstoryFuter,.skrin legend,#shortstoryContentTegi,.banerTopTwo,.loginExit,fieldset+div,.functionPanel+a{display:none!important;margin:0px !important;width:0px!important;}
#futer, #ambn53991333,.interDubBg{display: none !important;}
.shortstoryContent table td p+div{display:none;}
/*Top Block Site*/
/*Login*/
#login_password,#login_name{background:#000!important;}
div.topLine div.login form{background:#0d2363;}
.topLine .login .loginLink{background: #01186d70!important;padding:12px 0 11px 0!important;}
.topLine{margin:0px 0px 12px 6px;}
.loginLink{border-radius:0px!important;}
.loginAva{background: #01186d70!important;height: 38px;border-radius:0px!important;}
.loginAva img{padding: 2px 2px!important;border-radius:0px!important;}
.login a img{width:36px;height:36px;content:url(data:image/png;base64,);}
.loginLinkZ,.loginLinkS{padding:10px;text-decoration:none!important;}
.loginLinkS {padding-right:10px!important;border-radius:0px!important;}
.loginLinkZ:hover,.loginLinkS:hover{padding:10px;text-decoration:none!important;background:rgba(5,65,68,0.76);}
/*search*/
.search{margin:0px -265px 0px 0px;width:300px;}
div.topLine div.search form input.search{background:#000;}
#searchsuggestions{background-color:#fff;overflow-y:scroll;height:544px!important;border:0px!important;}
#searchsuggestions p{color:#fff!important;}
#searchsuggestions a{background-color:rgba(34,41,90,0.95);border:0px!important;}
#searchsuggestions a:hover{background:rgb(0,0,0);}
.searchheading,.seperator{background:rgb(29,80,104)!important;}
/*Menu*/
div.menu{background:#021d5a!important;border-radius:0px;width:1261px!important;margin:11px!important;}
#blok_1,#blok_2,#blok_3,#blok_4,#blok_5,#blok_6,#blok_7,#blok_8,#blok_78,#blok_19,#blok_20{border-radius:0px;background:rgba(18,21,39,0.86)!important;padding:8px 2px;}
#blok_20{width:100px;}
ul#topnav li span.sar span a{padding:3px 0px 7px 0px;color:#9e701c;font-size:16px;text-decoration:none;border:0px;text-align:center;}
ul#topnav li span.sar span a:hover{background:rgba(19,27,130,0.98);border-radius:1px;}
.menu ul#topnav li span.sar{z-index:99999!important;top:50px!important;position:absolute!important;}
/*Left Block*/
#leftBlock{margin:0px 0px 0px 6px;}
div.imgOngoing img{background:rgba(0,0,0,0);}
div.imgOngoing span{font-size:19px;color: #a4ffd9;background:rgba(0,0,0,0.47);}
.moduleLeft{border:0px!important;}
.interDub div.interDubBg{border:1px!important;}
#leftBlock,#centerBlock{bottom:300px;margin-top:5px;}
div.interDubBgTwo ul li a{color:#fff!important;font-size:14px;text-shadow:none;}
.interDub div.interDubBgTwo {background:rgba(16,17,68,0.94);border:0px;border-radius:8px;}
.interDubBgTwo a{background:rgba(52,140,132,0.5)!important;}
.interDubBgTwo b{border:0px!important;}
.interDubBgTwo ul li{border-bottom:0px!important;background:rgba(0,0,0,0.62);margin:4px 4px 4px 4px;}
.interDubBgTwo ul li:hover{background: rgb(0,0,0)!important;}
.interDubBgTwo ul li a {border-bottom:0px!important;}
.interDubBgTwo h2{border:0px!important;}
.interDubBgTwo b,.interDubBgTwo h2{background:linear-gradient(to bottom,#15363e 0%,#061035 100%)!important;border-radius:8px 8px 0 0!important;}
.raspis .popup{background:rgba(0,0,0,0.76)!important;color:#c9de1f!important;text-shadow:none;border:0px;border-radius:13px;}
.popup .nexttime{background:rgba(255,255,255,0.5);color:black;}
/*Center Block Site*/
#centerBlock{position:relative;top:6px;right:0;bottom:0;left:0;margin:0px 0px 0px 0px!important;}
body{background:#404040;background-repeat:no-repeat;background-size:cover;background-attachment:fixed;}
body{background: /*[[fon]]*/;}
p,span,img{font-size:15px;color:black;}
.shortstory{box-shadow:none!important;border-radius:10px;background:rgba(52,55,56,0.6);width:988px;}
.shortstory .shortstoryHead h2 a, .shortstory .shortstoryHead h1 a{color:#277b05;}
.shortstory .shortstoryHead h2, .shortstory .shortstoryHead h1{color:#277b05;font-size:18px!important;text-shadow:0 0px!important;font-weight:bold!important;background: none;}
.shortstoryHead{width: 988px;}
.shortstoryContent{width: 964px!important;border-top:0px solid #fff!important;border-bottom:0px!important;}
.shortstoryContent h4{border-radius:0px!important;color:#afafaf!important;background:rgba(19,27,72,0.5)!important;margin:0px 9px 0px 252px;text-shadow:none;}
.shortstoryFuter{margin:54px 0px 0px 0px;}
#nexttime{background:rgba(255,255,255,0.5);color:black;}
.text_spoiler,.title_spoiler{border:1px solid #0a3d4c!important;background:rgb(7,0,47)!important;}
.text_spoiler{color:#7d6345}
.staticInfo{background:rgba(12,20,41,0.5)!important;width:988px!important;}
.skrin{border: 0px!important;}
.skrin img{width:314px!important;height:156px!important;}
.userinfo{background:rgba(63,111,171,0.5);}
div.miniInfo{background:rgba(0,0,0,0.5)!important;color:#fff!important;border-radius:0px!important;}
.imgRadius{border-radius:0px;}
h4{background:#fff;border-radius:0px;}
p{color:#985201!important;}
p span{color:#985201;}
 .global_search{width:984px!important;background: #1d1248a1!important;border:0px!important;color:wheat!important;}
/*Player Block*/
#stdplayer{border-left:0px;}
#scrolltwo{width:886px!important;}
div#items {display: block!important;}
.epizode{background:rgba(0,35,49,0.5)!important;border-right:0px;border-top:0px;}
.active,.epizode:hover{background:rgba(6,66,90,0.9)!important;}
.prev{background:url('data:image/png;base64,')!important;width:39px!important;height:38px!important;}
.next{background:url('data:image/png;base64,')!important;}
#player2{width:964px;height:540px;border:0px;background:rgba(56,160,202,0.5)!important;}
.functionPanel{border:0px;}
.fastPunkt,.modeKino,.bottomProblem{background:linear-gradient(to bottom,#04050a 0%,#2b2721 100%)!important;}
.fastPunkt:hover,.modeKino:hover,.bottomProblem:hover{background:linear-gradient(to bottom,#142265 0%,#2b2721 100%)!important;}
.fastPunkt{border:0px;}
.modeKino{width:504px;border:0px;}
.ui-widget-content{background:#611414;}
.ui-widget-header{background: #0e6f8c;}
.ui-dialog .ui-dialog-content{background:#092984;}
#fastPunkt .one{background:#94a0bb;border:0px}
#fastPunkt .two{background:#4c5369;border:0px}
#fastPunkt .two:hover{background:#616a86;border:0px}
.bottomProblem{border:0px;}
#player{width:1240px;height:698px;border:2px solid #880000;background: rgba(78,10,10,0.6);}
#plist{width:1214px;padding:12px 0 0 27px;height: 125px;border:2px solid #880000;background: rgba(78,10,10,0.6);}
#scroll{width:1195px;}
#toup{background:url('data:image/jpeg;base64,');}
.series{border:0px;margin:0px 10px 0 0;}
.series span{text-shadow:0px 0px 0px;background:rgba(0,0,0,0.56);color:#f19200;}
#todown{background:url('data:image/jpeg;base64,');}
/*Down Block*/
.block_4 a{background:#0a4c4c;border-radius:0px;padding:10px 16px;}
.block_4 span,.block_4 a:hover{background:#3f5858;border-radius:0px;padding:10px 16px;text-decoration: none;}
#comment{width:984px!important;}
#newComment{background:rgba(25,32,35,0.6);border:0px!important;border-radius:13px!important;}
#newComment p,.userinfoHead{color:#fff!important;}
#rulesComment{border:0px;border-radius:13px!important;background:linear-gradient(to bottom,#001bfb 0%,#007084 100%)!important;background: rgba(0, 0, 0, 0.32)!important;}
#rulesComment a{text-decoration:none;text-align:center;width:974px;}
#rulesComment:hover{background:rgba(0,0,0,0);background:rgba(0,0,0,0.62)!important;}
.form_comment ul li{color:#ff0014!important;border:0px;}
#form_comment button {border:0px;background-color:#191c3e;border-radius:33px;}
.commentFinal{background:rgba(36,33,82,0.67)!important;border-radius:10px;border:none;}
.commentFinalText{background:rgba(55,70,76,0.7)!important;min-height:66px!important;border-radius:0px;color:#c5ae8e;border:none;}
.commentFinalAva{background: rgba(0,0,0,0)!important;width:115px;float:left;border-right:0px;}
.commentFinalData{height:16px;border-left:0px;border-bottom:0px;color:#00b327;background:rgba(35,45,56,0.65);}
.commentFinalIt{background:rgba(35,45,56,0.65);border-top:0px;color:#00b327;border-left:0px;}
.title_quote,.quote{background:rgb(48,78,90)!important;border:1px solid #5a5a5a;border-radius: 0px;}
.container{display:none;}
/*Работает только со скриптом*/
 .download{display:block;z-index:1;margin-left:5px;background-color:rgba(74,24,24,0.42);box-shadow:0px 0px 20px #000000;position:absolute;width:249px;height:370px;overflow-y:scroll;}
.HD,.SD{width:50%;height:90%;position:absolute;text-align:center;font-size:20px;color:#e28109;}
#HD,#HD li,#SD,#SD li{margin:14px;}
#HD a,#SD a{color:darkgray;}
.SD{margin-left:50%;}
.TopNav{width:100%;height:10%;text-align:center;font-size:32px;color:#e28109;}
}
@-moz-document domain("aniland.org") {
object {width:964px!important;height:540px!important;}
#videoplayer {width:964px!important;height:540px!important;}
#knpki{background:rgba(19,16,60,0.84)!important;}
a{background:#0f1844!important;border:1px solid #52370b!important;}
}
