/* ==UserStyle==
@name           yassersouri.ir
@namespace      github.com/openstyles/stylus
@version        0.1.0
@description    A new userstyle
@author         Me
==/UserStyle== */

@-moz-document domain("yassersouri.ir") {
main.container::before {
    content: "The installation procedure of the userscript has changed. Please disable the CSS in Stylus, visit https://christian-grund.de/wordpress/en/annotation-task-plugins and follow the instructions to update the Userscript in Greasemonkey or Tampermonkey.";
    background-color: #ff0000;
-webkit-user-select: text;
-moz-user-select: text;
-ms-user-select: text;
user-select: text;

}

* {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    user-drag: none;
    -webkit-user-drag: none;
    -moz-user-drag: none;
}
body{background-color: #000; color:#fff}
.form-check-label{padding: 5px 0}
  .force-show{fill-opacity:0.5}
  .object-segment-active{fill:rgba(255,133,27,0.75)!important; stroke: #7c3a00 !important}
  .object-segment-selected{fill:rgba(133, 13, 201, 0.75)!important; stroke: #32004f !important}
  .alert-primary {background-color: #002244; border: none;}
    .container{width:100%; max-width:1000px;padding:5px 0px !important; display:table}
    main .container .row{display: table-row}
    main .container .row>*{display: table-cell}
    main .container .col-9{width: 1px; flex: auto; padding: 0; margin:0;}
    footer .double-spaced {margin-right:25px!important}
    footer .badge {
        display: inline-block;
        font-weight: 400;
        text-align: center;
        white-space: nowrap;
        vertical-align: middle;
        -webkit-user-select: none;
        -moz-user-select: none;
        -ms-user-select: none;
        user-select: none;
        border: 1px solid transparent;
        padding: .375rem .75rem;
        font-size: 1rem;
        line-height: 1.5;
        border-radius: .25rem;
    }
    #image-canvas{margin: 0px auto}
    footer {
        background-color: #222 !important;
        height: auto !important;
        line-height:normal !important;
        margin-top:5px;
        position: static !important;
    }
    footer .container {padding: 5px 80px 5px 5px !important}
    body{margin: 0; line-height: normal}
    nav{
        position:static !important;
    }
    @media screen and (max-width: 650px) {
        main .container .card{position: fixed; right: 0; bottom:0; width: 80px; height: 80px; padding: 0px; vertical-align: middle; z-index:5; background-color: #333;} 
        main .container .card #show-all{padding: 3px; font-size: 0.7em; }
    }    
     @media screen and (min-width: 650px) {
        main .container .card{position: relative; padding: 0px; z-index:5; background-color: #333;} 
        main .container .card #show-all{position: absolute; top: 0; width: 100%; height: 100%;}
    }
.form-check input:disabled {
  visibility: hidden;
}

.form-check input:disabled+label {
  visibility: hidden;
}    
}
