css:
@-moz-document url-prefix("https://www.youtube.com/") {
.skeleton-bg-color {
    background-color: #1A237E !important;
    border-color: #2b2b2b !important;
    border-bottom: 1px solid #2b2b2b !important;
}
.watch-skeleton .skeleton-light-border-bottom {
	border-bottom: 1px solid #2b2b2b !important;
}
.masthead-skeleton-icon {
	background-color: #1A237E;
}
#masthead {
	background-color: #283593 !important;
}

.ytd-guide-renderer {
    background-color: #283593 !important;
}
.guide-links-primary {
    background-color: #283593 !important;
}
.ytd-popup-container, ytd-post-renderer, ytd-post-renderer yt-icon-button, ytd-post-renderer yt-icon {
    background-color: #283593 !important;
}
.ytd-multi-page-menu-renderer {
    background-color: #1A237E !important;
}
.yt-multi-page-menu-section-renderer {
    background-color: #283593 !important;
}
#guide-content {
    background-color: #283593 !important
}
.style-scope.ytd-watch {
    background-color: #1A237E !important;
}
.hide-skeleton {
    background-color: #1A237E !important;
}
.ytd-page-manager {
    background-color: #1A237E !important;
}
.ytd-browse {
    background-color: #1A237E !important;
}
.ytd-playlist-panel-renderer {
    background-color: #283593 !important;
}
.ytd-searchbox {
    background-color: #1A237E !important;
}
#channel-header {
    background-color: #283593 !important;
}
#tabs-inner-container {
    background-color: #283593 !important;
}
.yt-notification-action-renderer {
    background-color: #283593 !important;
}
.ytd-backstage-post-renderer {
    background-color: #1A237E !important;
}
#columns {
    background-color: #1A237E !important;
}
.ytd-two-column-browse-results-renderer {
    background-color: #1A237E !important;
}
.ytd-topic-channel-details-renderer, #channel-details, .ytd-rich-metadata-renderer {
    background-color: #001064 !important;
}

/*Menu*/
paper-listbox {
    background-color: #283593;
}
  
/*Player*/
#movie_player {
    background-color: #1A237E !important;
}

/*Chat*/
.yt-live-chat-renderer {
    background-color: #283593 !important;
}
.yt-live-chat-item-list-renderer {
    background-color: #1A237E !important;
}
.ytd-toggle-button-renderer {
    background-color: #1A237E !important;
}
.ytd-live-chat-frame {
    background-color: #283593 !important;
}
.yt-dropdown-menu {
    background-color: #283593 !important;
}
.settings-card {
    background-color: #1A237E !important;
}
.panel-close-button {
    background-color: #283593 !important;
}

/*ScrollBar*/
::-webkit-scrollbar,
::-webkit-scrollbar-corner,
::-webkit-scrollbar-track-piece {
	background: #0D47A1 !important;    
}

::-webkit-scrollbar {
	width: 6px !important;
	height: 6px !important;
}

::-webkit-scrollbar-thumb {
	border-radius: 0px !important;
    box-shadow: inset 0 0 0px #1565C0;
	border: 0px solid #1565C0 !important;
}

::-webkit-scrollbar-thumb:horizontal {
    background: linear-gradient(to right, #1565C0, #1565C0, #1565C0, #1565C0);
}
::-webkit-scrollbar-thumb:vertical {
    background: linear-gradient(#1565C0, #1565C0, #1565C0, #1565C0);
}
}
