css:
@-moz-document domain(transifex.com) {
/*Wanna hack pentagon? Start here*/ /*If you would like to improve this theme feel free to shoot me a message on Transifex 'falcontr06'*/
/*revision.2018.06.30*/
/*GENERAL COLOR*/
.u-color-primary, html, body, display
{background-color:black;color:#ccc!important}

.u-backgroundColor-white, .u-backgroundColor-muted, .u-backgroundColor-main, .u-backgroundColor-stateFirst, .u-backgroundColor-data, :after, .u-bgColor-whiteBlue, .u-bgColor-white, .u-bgColor-grayLight
{background-color:black!important}

.u-backgroundColor-whiteFaded 
{background-color:rgba(0,0,0,0.5)!important}

/*INPUT FIELDS AND TEXT CONTAINERS*/
.django-form input[type="text"], .django-form input[type="email"], .django-form input[type="password"], .django-form input[type="url"], .django-form input[type="number"], #translated-string, #source-string, .o-textinput, .o-select__option:active, .active-result, .search-choice, .search-field, textarea, .c-select-dropdown__controller, .c-select-dropdown__container
{color:#ccc!important}

/*INPUT BOXES*/
.django-form input[type="text"], .django-form input[type="email"], .django-form input[type="password"], .django-form input[type="url"], .django-form input[type="number"], #translation-string-area, #source-string-area, .search-input, .o-select__input, .translated-term, .o-textinput, .active-result, .search-choice, .search-field, .chzn-container.chzn-container-multi .chzn-choices, .chzn-container .chzn-results .highlighted, textarea, .c-select-dropdown__controller, .c-select-dropdown__container
{background-color:black!important;background:black!important}

/*SO CALLED 'FLYING' THINGS (BADGE ETC) YOU BETTER IGNORE THIS EXPLANATION AS IT HAS NO SIGNIFICANCE*/
.mark-username, .o-badge, .js-discuss-username, .suggest-tm, .o-pill{background-color:black!important;color:white;border:1px solid #00A}

/*EVERYTHING RELEATED TO REVIEW&STATUS*/
.string-rev, .js-reviewed1, .u-backgroundColor-stateReviewed, .js-review{background-color:#113!important}
.js-unreview {background-color:#33a!important}
/*.u-color-stateReviewed {color:#33A!important}*/

/*EVERYTHING RELEATED TO WARNING&STATUS*/
.string-warn, .c-notification--warning, .scurrent-status{background-color:#220}

/*EVERYTHING RELEATED TO TRANSLATED&STATUS*/
.js-translated, .u-backgroundColor-stateTranslated
 {background-color:#33A!important;color:#33a!important}
/*.u-color-stateTranslated {color:#33F!important}*/

/*GENERAL UI*/
.c-infoarea, .u-display-flex, #filter-area, .container-fluid, #appspace-disabled, #resources-area, .create-suggestion, #js-stringsscrollbar, .nano-pane, .c-tooltip__content, .c-sidebar, .content, .c-system-message
{background-color:black!important;border-color:#333!important}

/*THOSE COOL GRAPH LINES (not working)*/

.u-backgroundColor-graphBase {background-color:#33a!important}
.u-color-graphBase {color:#33a!important}
.u-fill-graphBase {fill:#33a!important}
.s-callout--translation{border-left-color:#33a!important}

/*BUTTONS AND CLICKABLES*/
.o-select__container, .c-dropdown__container, .c-taglist__tag, a, input, button, select
{background-color:black!important;color:#ccc;border-color:#333}
.o-button, .check-mass span, #filter-area
{background-color:black;color:#ccc;border-color:#333}
.o-button--fill.is-disabled, .is-disabled
{background-color:black!important;color:#333!important}

/*BORDERS*/
.u-borderRight-divider, .u-borderLeft-divider, .u-borderTop-divider, .u-borderBottom-divider, .u-borderHorizontal-divider, #edit-left-column, #user-suggestion-area, #string-list > div, #discussions-area .discussions-steady, #discussions-area > ul li, .u-border-divider, .massive-operations--translator input[type="text"], .u-borderBottom-background, .suggest-steady {border-color:#222!important}

/*MAIN&DOCS PAGE, YES LOW EFFORT HERE*/
.txd-o-sidebar, .c-navPrimary__list,.c-article, .c-searchForm, .c-internalNav, .c-wrapper__sidebar, .c-box {background:black!important;background-color:black!important;color:white!important}
.u-color-dark{color:#AAA!important}
.c-page.page-template-home_v2 .c-page-hero{background-image:url(http:iwanttodie.)}

/*THINGS WITH SPECIAL PROPERTIES*/
#string-list > div.string-selected {background-color:#222;color:#ccc}
.morris-hover {background-color:#000000AA!important;border-color:#333}
.o-progressBar {background-color:#333!important}
.search-choice-close {background-color:#444!important}
.chzn-container .chzn-results li em {background-color:#555!important}
.c-marker {background-color:transparent!important}
.s-placeholder, .checked, .check-mass:hover, .check-mass:hover:after, .check-mass:not(.check-no-bg):hover {background-color:#444!important}
.o-diff--added {background-color:#040;color:white}
.o-diff--removed {background-color:#A00;color:white}
}
