.menu_div{border-bottom:1px #333 solid;padding:3px 0}
.menu_div_wrap{font-size:.8em;color:#ddd;border:1px solid #333;border-radius:3px;padding:2px 10px;background:#2b2b2b}

input.btnadmn,input.inpadmn{font-family:"segoe ui","trebuchet MS","Lucida Sans Unicode","Lucida Sans",Sans-Serif}
input.btnadmn{font-size:12px;cursor:pointer;font-weight:600;text-transform:uppercase;width:83px;text-align:center;border-radius:0 3px 3px 0;border-top:1px #333 solid;border-bottom:1px #333 solid;border-right:1px solid #333;border-left:0;height:25px;line-height:22px;    background-color: #444;
    height: 25px;
    color: #ddd;}
input.inpadmn{font-size:12px;vertical-align:top;width:136px;border-radius:3px 0 0 3px;border:1px #ddd solid;background:#fff;height:25px;line-height:25px;padding:0 5px;    color: #ddd;
    border: 1px #333 solid;
    background: #444;}
.cookie_button {
    border: 1px solid #333;
    background-color: #2b2b2b;
    border-bottom: 2px solid #333;
}
#float-box-1, #float-box-2, #float-box-3 {
    background: #666;
}
#markdown{ padding:0 10px;}
#markdown code {padding: 3px;margin: 0;background-color: #252525;border-radius: 3px;}
#markdown blockquote {margin: 0;padding: 0 5px;color: #ddd;border-left: 2px solid #444;}
#markdown table {margin: 8px 0;overflow: auto;border-spacing: 0;border-collapse: collapse;display: table;}
#markdown table th, #markdown table td {padding: 4px 8px;border: 1px solid #444;}
#markdown hr {height: 3px;padding: 0;margin: 12px 0;background-color: #333;border: 0;}
#markdown ol {background: #252525;color: #eee;}
#code_frame {
    background-color: #252525;
}
.pagination a {
    background: #2b2b2b;
    border: 1px solid #333;
    border-bottom: 2px solid #333;
    color: #f2f2f2;
}
