css:
@-moz-document url-prefix("https://antyweb.pl"), url-prefix("http://antyweb.pl"), url-prefix("https://zeropln.antyweb.pl") {
body,
.bg-white,
input,
section .excerpt.right .inside,
section .excerpt.left .inside,
aside.art-aside .inside,
.content-wrapper{
    background: #1f1f1f;
    color: #fff;
}
.grid-box-big,
.art-list .item .inside .box-text,
.bg-grey-light,
.box-team,
.special-footer,
.job:hover,
.bg-grey,
.affix-bg-grey-light .affix{
    background: #191919;
    color: #fff;
}
.art-list .item .inside .box-text strong, .art-list .item .inside .box-text h1, .art-list .item .inside .box-text .entry_title, .art-list .item .inside .box-text .strong,
.art-list.al-inside .item .inside .box-text strong, .art-list.al-inside .item .inside .box-text h1, .art-list.al-inside .item .inside .box-text .entry_title,
section .art-lead,
section p,
section ul li, section ol li,
.job h3,
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(3) a,
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(4) a,
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(5) a,
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-3 a p,
.aside-header strong,
.links-list .item p,
#zero-pln h3 a, #zero-pln h4 a,
.text-black,
.box-team .team-list .item p,
.box-team p,
h1, h2, h3, h4,
h1 a, h2 a, h3 a, h4 a,
.box-social-counter p,
.text-grey-darkest,
section .excerpt .box-strong,
.b-more-list-item__title,
.block-more__title{
    color: #fff !important;
}

header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2,
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(1),
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(2),
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(3),
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(4),
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(5),
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-3{
    background: #000;
}
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(2) a img{
    filter: invert(1);
}


.links-list .item,
.art-list.al-3:not(.al-small) .item,
.art-list,
.art-list.al-2 .item .inside,
.aa-footer,
.category-link,
section .excerpt .box-strong{
    border-color: #2f2f2f;
}

.content ul li a{
    color: #fa4b2a;
}

.w-8.h-8.absolute.pin-r.bg-white.text-orange,
.category-link:hover{
    background: #3e3d3d;
}

.box-tags-default .tag,
.grid-box-commercial,
#articles-column{
    background: #303030 !important;
}
.box-tags-default .tag:hover{
    background: #FA4B2A !important;
}

::-webkit-scrollbar {
    width: 0.5em;
}
 
::-webkit-scrollbar-track{
    background: #303030;
}
 
::-webkit-scrollbar-thumb {
    background:#a5a5a5
}
}

@-moz-document regexp("https?://disqus\\.com/embed/comments/.*antyweb\\..*") {
/*Komentarze Disqus*/
.post-message p{
    color: #fff !important;
}

.publisher-anchor-color a,
a.publisher-anchor-color {
    color: #dadada !important;
}

.tab-conversation.active > a {
         color: #fff !important;
    }
     .nav-primary {
         border-bottom: 2px solid #fff;
    }

.active .nav-tab>a:after, .tab-conversation.active>a:after{
    background: #FA4B2A !important;
}


.textarea-wrapper,
.textarea{
    background: #292929;
    border-color: #464646;
    color: #fff;
}
.textarea-wrapper[aria-label="Dołącz do dyskusji..."]{
    color: #fff!important;
}

blockquote{
    color: #d6d6d6;
}

.post-actions{
    background: #3e3e3e;
    border-top-color: #3e3e3e;
}

.wysiwyg__item a{
    filter: invert(1);
}
}
