css:
@-moz-document domain("warframe.market") {
/*------------ Основа -------------*/
  *:not(.coloris)
  
  {
    color: skyblue !important;
    box-shadow: none !important;
    background-color: transparent !important;
    border-color: rgb(135,206,235,0.1) !important;
  }
    html, #newtab-window, body
  {
    background-image: linear-gradient(rgb(0,0,0), rgb(0,0,0)) !important;
  }
/*--------------- Поиск ---------------*/
  .header .search-field
  {
    background: rgb(0, 0, 0,.8) !important;
	border: solid 1px rgb(135,206,235,0.1) !important;
	box-shadow: 1px 1px 5px #000 !important;
  }
/*--------------- Заказы ---------------*/
  .widget-order__button:hover .big-red__button, .widget-order__button .big-red__button
  {
    background: rgb(0, 0, 0,.8) !important;
	border: solid 1px rgb(135,206,235,0.1) !important;
  }
  .modal__content
  {
    background: rgb(0, 0, 0,.9) !important;
	border: solid 1px rgb(135,206,235,0.1) !important;
	box-shadow: 0 1px 5px rgb(0, 0, 0) !important;
  }
  .seeker__dropdown
  {
    background: rgb(0, 0, 0,.8) !important;
	border-bottom: solid 1px rgb(135,206,235,0.1) !important;
	border-right: solid 1px rgb(135,206,235,0.1) !important;
	border-left: solid 1px rgb(135,206,235,0.1) !important;
	left: -1px !important;
  }
  .order-unit .order-unit__actions a, .btn
  {
    background-color: rgb(135,206,235,0.1) !important;
  }
  .item__page .content__header--transparent .container a
  {
    background-color: rgb(135,206,235,0.3) !important;
	border: solid 3px rgb(0,0,0,0.4) !important;
  }
  .item__page .content__header--transparent .container a:hover
  {
    border: 3px solid #2B9097 !important;
	box-shadow: 0 7px 15px -3px #265663 !important;
  }
  .item__page .content__header--transparent .container a.active
  {
    border: 3px solid rgb(0,191,255) !important;
	box-shadow: 0 7px 15px -3px #265663 !important;
  } 
  .order__own_line:nth-child(2n+1), .order__own_line
  {
    background-color: rgb(135,206,235,0.1) !important;
	border-radius: 2px !important;
  }
  .form__success, .btn__filter.active, .active.btn__action, .btn__radio.active
  {
    background-color: rgb(0, 255, 68, .32) !important;
    border-radius: 2px !important;
  }
  .listing__orders .listing__header h3
  {
    background-color: rgb(135,206,235,0.1) !important;
	  padding: 10px !important;
	  border-radius: 2px !important;
  }
  .widget-order__image
  {
    box-shadow: 0 5px 20px -1px #39A57E99 !important;
  }
/*---------------  ---------------*/
  .user__status .status__lastSeen
  {
    background-color: rgb(0,0,0,.8) !important;
  }
  .achievement__block, .achievement__block h3, .disclaimer
  {
    background-color: rgb(0,0,0,.5) !important;
  }
  .profile__stats > *
  {
    background-color: rgb(0,0,0,.8) !important;
	  border-radius: 4px !important;
	  box-shadow: 0 1px 5px rgb(0, 0, 0) !important;
	  margin: 4px !important;
  }
/*--------------- Шапка + Меню ---------------*/
  .floating-menu
  {
    background-image: linear-gradient(rgb(20,20,20,.9), rgb(0,0,0,.9)) !important;
	  box-shadow: 0 1px 5px rgb(0, 0, 0) !important;
  }
  .content__header, .content__header--second, [class="content__header flex--root"]
  {
    background-color: rgb(0, 0, 0) !important;
	  box-shadow: 0 1px 5px rgb(0, 0, 0) !important;
  }
  .floating-menu__section .root-menu-button .shift-2, .btn__light:hover, .btn__light, .user__about--edit textarea, .shift-1, .floating-menu__section .root-menu-button > ul
  {
    background-color: rgb(0,0,0,.9) !important;
	  box-shadow: 0 -4px 5px rgb(0, 0, 0) !important;
  }
  [class="shift-5"], .floating-menu__section .root-menu-button .shift-2
  {
    box-shadow: 0 1px 5px rgb(0, 0, 0) !important;
	  border-radius: 0 0 2px 2px !important;
	  background-color: rgb(0,0,0,.9) !important;
	  border-bottom: 1px solid rgb(135,206,235,0.1) !important;
	  border-right: 1px solid rgb(135,206,235,0.1) !important;
	  border-left: 1px solid rgb(135,206,235,0.1) !important;
  }
  .floating-menu__section .root-menu-button > ul li.online-switch > span.active
  {
    color: rgb(255,255,255) !important;
	  background-color: rgb(0,191,255,.4) !important;
	  text-shadow: 0 1px 1px rgb(0, 0, 0) !important;
  }
  .user__about--edit .subtext
  {
    background-color: rgb(0,191,255,.5) !important;
  } 
  .floating-menu__section .root-menu-button > ul li.button > a:hover, .floating-menu__section .root-menu-button > ul li.button > div:hover
  {
    background-color: transparent !important;
  }
  .user__about--edit textarea, .content__header--second select, .messenger__block, footer .footer__body .footer__language select, .content__body select, .content__header--second select, .content__header select, .shift-1, .order-unit .order-unit__actions button
  {
    border: 1px solid rgb(135,206,235,0.1) !important;
  }
  [class="content__header--second flex--root"], [class="row user-orders__sort"]
  {
	box-shadow: 0 10px 10px -8px rgb(135, 206, 235, .21) !important;
  }
  .has-success .input-group-addon
  {
    background-color: rgb(0, 255, 68, .32) !important;
  }
  [class="message-counter"], .floating-menu__section .root-menu-button.active > .combobox .message-counter, .floating-menu__section .root-menu-button.active > a .message-counter
  {
    border: 1px solid rgb(135,206,235,0.1) !important;
	background: rgb(12, 13, 13, .86) !important;
  }
/*--------------- Текст ---------------*/
  a:hover, a:hover *, a:visited:hover, a:visited:hover *, span[onclick]:hover, div[onclick]:hover, .widget-order__button:hover .big-red__text, .floating-menu__section .root-menu-button > ul li.button > div:hover *, .btn .fa + span:hover, [role="button"]:hover *, .floating-menu__section span:hover, .header .search-field button:hover *, [class="sort-button__name"]:hover, .sort-button.down .fa:hover, .sort-button.up .fa:hover, button:hover *
  {
    color: rgb(255,255,255) !important;
    text-shadow: 0 0 7px rgb(255,255,255,0.5) !important;
    text-decoration: none !important;
  }
  .item__page .content__header--transparent .container .item__name h1, .widget-order__button:hover .big-red__text, .widget-order__button .big-red__text, span, span:hover, .content__header--transparent h1, .content__header--transparent h2, .listing__orders .listing__header h3
  {
  text-shadow: 0 2px 1px rgb(0, 0, 0) !important;
  } 
  .order-unit .order-unit__description .order-unit__user-description .user__name, .user__name, .order-unit .order__price
  {
    color: rgb(0, 255, 187) !important;
    text-shadow: 0 1px 1px rgb(135,206,235,0.1) !important;	
  }
  .tabs li a.active, .tabs li div.active
  {
    border-bottom: 3px solid rgb(0,191,255) !important;
  }
  .content a.active, .widget-order__button .big-red__text
  {
    color: rgb(0,191,255) !important;
  }
  .user__about--edit .subtext
  {
    color: rgb(0,0,0) !important;
	text-shadow: 0 1px 1px rgb(255,255,255) !important;
  }
  [class="seeker__fake-input--visible"]
  {
    color: rgb(0, 191, 255, .31) !important;
  }
/*-------------- Индикаторы онлайн --------------*/
  .s-invisible
  {
   color: rgb(135,206,235,.6) !important;
  }
  .s-offline
  {
    color: rgb(255, 0, 0) !important;
  }
  .s-online
  {
    color: rgb(0, 255, 68) !important;
  }
  .s-ingame
  {
    color: rgb(26, 37, 255) !important;
  }
/*------------ Фикс ------------*/ 
  .profile__avatar
  {
    background-color: rgb(0,0,0,.9) !important;
    box-shadow: 0 1px 5px rgb(0, 0, 0) !important;
	  border-radius: 4px !important;
	  padding: 5px 3px 3px 4px !important;
	  top: -50px !important;
  }
  [class="description__buttons"], .btn
  {
    z-index: 99 !important;
  }
  .user__profile .content__header--transparent, .item__page .content__header--transparent
  {
    background-color: rgb(0,0,0,.7) !important;
  }
  #s3gt_translate_tooltip_body
  {
    background-color: rgb(0,0,0,.9) !important;
  }
  .floating-menu__section .root-menu-button, .floating-menu__section .root-menu-button > .combobox, .floating-menu__section .root-menu-button > a
  {
    background-color: rgb(0,0,0,.0) !important;
  }
  .flex--container, .content__body
  {
    background-color: rgb(0,0,0) !important;
  }
}
