css:
@-moz-document url-prefix("http://www.nicovideo.jp/my"), url-prefix("http://www.nicovideo.jp/user"), url-prefix("https://www.nicovideo.jp/my"), url-prefix("https://www.nicovideo.jp/user") {
/* --------------------
	ユーザー情報
 -------------------- */
.userDetail
{
    width: 955px;
    border: #bdbdbd solid 1px;
    border-radius: 0;
}
.userDetail a
{
    color: #0B3861 !important;
}

.userDetail .avatar
{
	width: 108px;
}

.userDetail .profile
{
}

.userDetail .profile h2,
.userDetail .profile .account
{
    margin: 0;
}

.userDetail .profile .stats,
.userDetailComment li
{
    padding: 4px 0 4px 6px;
    max-width: 1000px;
    width: 840px;
    border: #bdbdbd solid;
    border-width:1px 0 1px 0;
    background: #f4f4f4;
}
.userDetail .profile .stats ul li
{
    padding: 0;
}

.userDetail .accountEdit,
.userDetail .profileEdit
{
    padding: 10px;
    background: #f4f4f4;
    border: #bdbdbd solid;
    border-width:1px 0 1px 0;
}
.userDetail .accountEdit:hover,
.userDetail .profileEdit:hover
{
    text-decoration: none;
    background: none;
}
.userDetail .profileEdit
{
    right: 140px;
    bottom: 66px;
}

/* --------------------
	メインカラム
 -------------------- */
.wrapper .content
{
    border: #bdbdbd solid 1px;
    border-radius: 0;
}
.articleBody
{
    border-radius: 16px;
}
.content h3
{
    background: #0B3861;
    font-weight: normal;
    color: #E0E6F8;
    padding: 10px 0 10px 15px;
}
.content h3 a
{
    font-size: 70%; 
    color: #E0E6F8;
}

.content .menu-item a
{
    color: #0B3861 !important;
}
.log-content
{
    margin-right: 0 !important;
}
.log-target
{
    border-color: #fff;
    border-width: 0 0 0 4px;
    border-style: solid;
    padding-left:10px;
}
.log-target-thumbnail
{
    float: right !important;
}
.log-target-info
{
    margin-left: 0 !important;
}
.log-footer
{
    left: 10px !important;
    bottom: -4px !important;
}
.log-mute-button
{
    left: 5px !important;
    top: 40px !important;
}
/* -- ニコレポ色　*/
.log-target-video
{
    border-color: #00429B;
}
.log-target-live-program
{
    border-color: #4990e2;
}
.log-target-chblog
{
    border-color: #7e5422;
}
.log-target-solid
{
    border-color: #22CCFF;
}
.log-target-seiga-illust
{
    border-color: #fd9a01;
}
.log-target-manga-content
{
    border-color: #f169a7;
}
/* -- 元のタグ　*/
.log-target-type-video,
.log-target-type-live-program,
.log-target-type-book,
.log-target-type-solid,
.log-target-type-image,
.log-target-type-manga
{
    display: none;
}

/* --------------------
	サイドバー
 -------------------- */
.sidebar ul li a
{
    color: #555;
    padding: 8px;
}
.sidebar ul li a:hover
{
    color: #01A9DB;
    text-decoration: none;
    /*background: linear-gradient(#E0ECF8, #CEE3F6);*/
    background: linear-gradient(#f2f2f2, #d8d8d8);
}
/* -- ボタン：今いるページ　*/
.sidebar ul li.active a
{
    color: #E0E6F8 !important;
    padding: 10px 0 8px 12px;
    background: #0B3861;
}
.sidebar ul li.active a:hover
{
    text-decoration: none;
}
.sidebar ul li.active a.handler
{
    margin: 4px 4px 0 0 !important;
}
/* -- 小ボタン：表示・隠す　*/
.sidebar ul li a.handler
{
    background: none;
    margin: 8px 10px 0 0;
    padding: 0;
}
/* -- ボタン：アクティブなページ　*/
.sidebar ul li.parent ul.child li.active
{
	background: #0B3861;
}
.sidebar ul li.parent ul.child li.active a
{
    color: #E0E6F8 !important;
}
.sidebar ul li.parent ul.child li.active a:hover
{
	text-decoration: none;
}
}
