css:
@-moz-document url-prefix("https://www.youtube.com/watch") {
@media screen and (min-width: 3200px) {
	#player, #info, #meta, #related, .html5-video-container {
		position: fixed !important;
	}
	
	#player,
	#info,
	#meta {
		left: 8.14%;
	}
	#info,
	#meta,
	#comments,
	#contents,
	#related {
		overflow: auto;
	}
	
	#related #items {z-index: 10;}
	#related {
		left: 87.2%;
		width: 15% !important;
		height: 94%;
		top: 5%;
		z-index: 1;
	}
	#player {
		top: 5%;
	}
	#info {
		top: 60%;
		height: 12%;
	}
	#meta {
		top: 69%;
		height: 30%;
	}
	ytd-comments {
		position: fixed;
		left: 49.5%;
		height: 94%;
		top: 5%;
		width: 38% !important;
	}
	#guide-inner-content {
		position: fixed;
		left: 230px !important;
		top: 11%;
		visibility: visible !important;
		height: 81%;
		overflow: auto;
	}
	#rplayer-ads,
	#player-ads * {
		visibility: hidden;
		width: 0px;
		height: 0px;
		display: none;
	}
	#playlist > #container{
		position: fixed;width: auto;
		right: -15% !important;
		bottom: 0px;
		z-index: 999;height: 90%;
	}

	#playlist:hover #container {right: 0px!important; background: #1B1B1D;width: 500px; display: none;}
	#playlist #items > * , #playlist #items #meta{left: 0px;}
	#playlist #items #img {left: -1000px;}
	
	
	
}
}

@-moz-document url("https://www.youtube.com/feed/subscriptions"), url("https://www.youtube.com/") {
@media screen and (min-width: 3000px) {
	#primary {
		position: fixed;
		left: 10%;
		width: 90%;
		top: 5%;
		height: 95%;
		overflow: auto;
	}
}
}

@-moz-document url-prefix("https://www.youtube.com/watch") {
/* SMALL */
@media screen and (max-width: 600px) {
	#masthead-container {
		opacity: 0.1;
	}
		#masthead-container:hover {
		opacity: 1;
	}
	#player {
		position: fixed;
		top: -20px;
	}

}
}
