css:
@-moz-document domain("burningseries") {

}

@-moz-document domain("bs.to"), domain("burning-series.org"), domain("burning-seri.es") {
/*
===========================================================
This theme was originally created by L. Bosin
This version of the theme was customized by astron
The Banner was designed by drakonia
===========================================================
*/

@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 400;
    src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/2UX7WLTfW3W8TclTUvlFyQ.woff) format('woff');
}
* {
    font-family: Roboto !important;
    border: none !important;
}
html {
    /*background: #0D0D0D;*/
    background: linear-gradient(
      rgba(0, 0, 0, 0.7), 
      rgba(0, 0, 0, 0.7)
    ),url(https://abload.de/img/fcbuspyh4sgv.jpg) no-repeat; /*Background Image*/
    background-position: center;
    background-size: cover;
    height: auto;
}
body>#root {
    background: url(https://abload.de/img/bsto_banneraxsc0.jpg) no-repeat; /*Banner created by drakonia*/
    -webkit-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    -moz-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    border-radius: 2px;
    color: #fff;
}
a {
    color: #0288D1;
    text-decoration: none;
    text-transform: uppercase;
}
.andere-serien div.genre ul {		
    background-color: #212121;		
}
#user {
    background-color: #212121;
    -webkit-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    -moz-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    border-radius: 2px;
    height: auto;
    padding: 20px;
    position: absolute;
    right: 0;
}
nav>ul {
    display: flex;
    box-shadow: none;
    height: 2.75em;
}
nav>ul,
nav>ul>li>ul {
    background: #0288D1;
    line-height: 2em;
    border-radius: 0;
}
nav > ul > li > ul > li:first-child {
    border-top: none;
}
nav>ul>li {
    padding: .5em 0;
}
nav>ul>li:hover,
nav>ul>li>ul>li:hover {
    background: #21a9f2 !important;
}
nav>ul>li>ul {
    left: 0px;
    top: 2.75em;
    border-radius: 0 0 2px 2px;
}
nav>ul>li>ul>li {
    padding: .25em .5em;
    background: transparent;
}
nav>ul>li>ul>li:last-child {
    border-radius: 0 0 2px 2px;
}
nav>ul>li:last-child {
    padding: .5em;
}
#social-nav {
    display: none;
}
#sp_left>div>p {
    margin-bottom: 5px;
}
#sp_left>div>a {
    display: none !important;
}
ul.pages {
    padding: 5px;
}
.pages>li {
    width: 30px;
    line-height: 30px;
    border-radius: 2px !important;
    margin-left: 3px;
}
.pages>li:first-child {
    margin-left: 0;
}
.pages>li>a {
    color: rgba(255, 255, 255, .75);
}
.pages>li>a:hover {
    background-color: inherit;
    border-radius: 2px;
}
  
.serie .watched {
    background-color: #6FAC31 !important;
}
.fb-like {
    opacity: .1;
}
.fb-like:hover {
    opacity: 1;
}
.pages>li:hover {
    background-color: #3c3c3c;
    -webkit-box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 1);
    -moz-box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 1);
    box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 1);
}
.serie .watched:hover {
    background-color: #6FAC31 !important;
}
html::-webkit-scrollbar,
.home #shoutbox dl::-webkit-scrollbar,
#sbUser::-webkit-scrollbar,
#news > div::-webkit-scrollbar {
    width: 12px;
    background-color: rgba(0, 0, 0, .1);
}
html::-webkit-scrollbar-thumb,
#sbUser::-webkit-scrollbar-thumb,
.home #shoutbox dl::-webkit-scrollbar-thumb,
#news > div::-webkit-scrollbar-thumb {
    background-color: rgba(255, 255, 255, .25);
    width: 12px;
}
.icon.medal_gold {
    background: transparent url(https://wlu16www46.webland.ch/assets/bs_icon_1.png) no-repeat;
    background-size: 16px;
}
.icon.medal_silver {
    background: transparent url(https://wlu16www46.webland.ch/assets/bs_icon_2.png) no-repeat;
    background-size: 16px;
}
.icon.medal_bronze {
    background: transparent url(https://wlu16www46.webland.ch/assets/bs_icon_3.png) no-repeat;
    background-size: 16px;
}
.icon.page_white_edit {
    background: transparent url(https://wlu16www46.webland.ch/assets/bs_icon_settings.png) no-repeat;
    background-size: 16px;
}
.icon.eye {
    background: transparent url(https://wlu16www46.webland.ch/assets/bs_icon_check.png) no-repeat;
    background-size: 16px;
}
.icon.crossed_eye {
    background: transparent url(https://wlu16www46.webland.ch/assets/bs_icon_cross.png) no-repeat;
    background-size: 18px;
}
.icon.link_go {		
    background: transparent url(https://wlu16www46.webland.ch/assets/bs_icon_link.png) no-repeat;		
    background-size: 16px;		
}		
.icon.bug {		
    background: transparent url(https://wlu16www46.webland.ch/assets/bs_icon_bug.png) no-repeat;		
    background-size: 16px;		
}		
.icon.arrow_refresh {		
    background: transparent url(https://wlu16www46.webland.ch/assets/bs_icon_random.png) no-repeat;		
    background-size: 16px;	
}
.icon.page_white_wrench {
    background: transparent url(https://wlu16www46.webland.ch/assets/bs_icon_cover.png) no-repeat;		
    background-size: 16px;
}
.serie .epiInfo {
    margin-left: 0px;
    -webkit-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    -moz-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    border-radius: 0 2px 2px 2px;
    margin-top: 8px;
}
nav a {
    color: #fff;
}
#sp_right>small {
    display: none;
}
#sp_right {
    width: 185px;
}
#sp_right img {
    border-radius: 0;
}
#seriesdata table {
    margin: 0;
}
#seriesdata td ul li {
    border-radius: 0px;
}
#seriesdata td ul li:not(.noentry):hover {
    background: inherit;
    color: #0288D1;
}
tr:nth-child(even),
.home #shoutbox dl>dt,
.home li:nth-child(even) {
    background-color: rgba(255, 255, 255, .125);
}
span.slink {
    display: inline;
    color: #0288D1;
    text-decoration: none;
    text-transform: uppercase;
    font-size: .75em;
}
footer {
    padding: 5px 0 10px 0;
    font-size: .825em;
}
#sp_left>table>tbody>tr {
    height: 2em;
}
tr.watched:nth-child(even) {
    background-color: #7abf36 !important;
}
tr>td>a {
    color: #fff;
}
#serInput {
    width: 100%;
    padding: .5em;
    box-sizing: border-box;
    height: 2em;
    background-color: transparent;
    color: #fff;
    outline: none;
    border-radius: 0;
    border: 1px solid white !important;
}
#serInput:active,
#serInput:focus,
#serInput:hover {
    border: 1px solid #0288D1 !important;
}
.download-app {
    width: auto;
    font-size: 2em;
    font-weight: bold;
    margin: 0 0 10px 0;
    border-radius: 2px;
    background: #0288D1;
    color: #fff;
}
.download-app:hover {
    background: rgba(0, 0, 0, .125);
    color: #fff;
}
.home>#teaser,
.home>.column>section, .andere-serien div.genre {
    box-shadow: none;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    background: #0288D1 !important;
    border-radius: 2px;
}
.home>#teaser>div,
.home>.column>section>div {
    background-color: #3C3C3C;
}
#usrCnt {
    display: inline-block;
    width: 640px !important;
}
.user-center .col {
    background-color: transparent;
}
.user-center .col li {
    margin: 0 0 1px 0;
    border-radius: 0;
    background: rgba(255, 255, 255, .125);
}
.faq dt {
    border-radius: 0;
    background: rgba(255, 255, 255, .0625) !important;
}
.faq dt:hover,
.faq dt:target {
    background: rgba(255, 255, 255, .125) !important;
    box-shadow: none;
}
.messageBox {
    border-radius: 0;
}
.messageBox.error {
    background-color: #EF5350;
    color: #B71C1C;
}
.messageBox.info {
    background-color: #90CAF9;
    color: #1976D2;
}
fieldset > label > input,
fieldset > label > select {
    border-radius: 0;
    background-color: transparent;
    color: #FFF;
    border: 1px solid white !important;
}
fieldset > label > select > option {
    color: #FFF;
    background: #212121;
}
.home #sbSubmit {
    background: transparent;
    color: #FFF;
    border-radius: 0;
    padding: 0 .5em;
    border: 1px solid white !important;
}
.home section ul>li,
.home section dl>dt,
.home section dl>dd {
    padding: .25em;
}
.home section table>tbody>tr {
    height: 2em;
}
section#news > div > article > header {
    background-color: rgba(2, 136, 209, .125);
}
a[target="_blank"] {
    text-decoration: underline;
}
section#navigation > a,
section#navigation > div {
    background: transparent;
}
section#navigation > div:first-child {
    border: none !important;
    border-radius: 0;
    background: none;
}
#navigation {
    background: #212121;
    padding: .5em;
    -webkit-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    -moz-box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 1);
}
#navigation > div {
    line-height: 2em;
}
section#navigation > a:hover {
    background: transparent !important;
}
.home > #teaser,
.home > .column > section {
    background-image: none;
}
#shoutbox textarea {
    border-radius: 2px;
    background: rgba(255, 255, 255, .125);
    outline: none;
    color: #FFF;
}
.serie .infos>div {
    border-bottom: 1px solid rgba(255,255,255,.25) !important;
  box-shadow: none;
}
.serie .infos>div>p>span:hover {
  cursor: default;
  background: inherit;
  text-decoration: underline;
}
.serie div.episodes, .serie div.seasons {
  width: 49%;
  box-sizing: border-box;
  margin: 0 !important;
  padding: 0;
}

.serie div.seasons.full {
  margin: 0;
  width: 100%;
}
.serie .frame {
    height: inherit;
}
.serie .frame ul li.special {
  width: auto;
}
.serie .frame ul li {
  width: auto;
  margin: 3px 3px 0 0;
  border-radius: 2px;
  background-color: #333 !important;
}
.serie .frame ul li a {
  -webkit-transition: background-color 250ms ease-out;
       -o-transition: background-color 250ms ease-out;
     -moz-transition: background-color 250ms ease-out;
          transition: background-color 250ms ease-out;
  padding: .25em .75em;
}
.serie .frame ul li a:hover {
  background-color: rgba(255, 255, 255, .125) !important;
}
.serie .scrollbar {
  display: none;
}
.serie table.edit tr:first-child, .serie table.episodes tr:first-child,
.serie table.edit tr:last-child, .serie table.episodes tr:last-child {
  border-radius: 0;
}
.serie table.edit tr td:nth-child(1), .serie table.episodes tr td:nth-child(1) {
  padding: 2px 3px 3px 3px;
}
.serie table.episodes tr td:nth-child(1)>a {
  border-radius: 0;
  background-color: transparent;
  box-sizing: border-box;
  border: 1px solid rgba(0,0,0,.25) !important;
  -webkit-transition: background-color 250ms ease-out;
       -o-transition: background-color 250ms ease-out;
     -moz-transition: background-color 250ms ease-out;
          transition: background-color 250ms ease-out;
}
.serie table.edit tr, .serie table.episodes tr {
  line-height: 2em;
  -webkit-transition: background-color 250ms ease-out;
       -o-transition: background-color 250ms ease-out;
     -moz-transition: background-color 250ms ease-out;
          transition: background-color 250ms ease-out;
}
.serie table.episodes tr:focus, .serie table.episodes tr:hover {
  background-color: rgba(255, 255, 255, .125) !important;
}
.serie table.episodes tr.watched, .serie .frame ul li.watched {
  background-color: #70AC30 !important;
}
.serie table.episodes tr.watched:hover,
.serie .watched:hover {
  background-color: 7DBA36 !important;
}
.serie table.edit tr td:nth-child(3), .serie table.episodes tr td:nth-child(3) {
  top: 6px;
}
.serie table.episodes tr td:nth-child(1)>a:hover {
  background-color: rgba(0,0,0,.5);
}
.serie table.episodes tr.watched td:nth-child(1)>a:hover {
  background-color: rgba(0,0,0,.25);
}
.serie .hoster-player {
  background-color: transparent;
  width: 100%;
  text-decoration: none;
  border: 1px solid rgba(255,255,255,.125) !important;
}
.serie .hoster-player.deactivated {
  background-color: transparent;
}
.serie .hoster-player>h3, .serie .hoster-player>h2 {
  font-size: 1.5em;
  text-transform: none;
  color: #c0c0c0;
}
.serie .hoster-player>h2>u {
  color: #fff;
  text-decoration: underline;
}
.serie .hoster-tabs {
  border: 0 !important;
}

.serie .hoster-tabs>li {
  padding: 0;
  border: 1px solid transparent !important;
  margin-left: 1px;
}
.serie .hoster-tabs.top>li {
  margin-bottom: 0;
}
.serie .hoster-tabs.bottom>li {
  margin-top: 0;
}
.serie .hoster-tabs>li:first-child {
  margin-left: 0;
}
.hoster-tabs.top>li.active, .hoster-tabs.top>li:hover, .hoster-tabs.top>li.active:hover {
  border: 1px solid rgba(255,255,255,.125) !important;
  border-bottom-color: #121212 !important;
}
.hoster-tabs.bottom>li.active, .hoster-tabs.bottom>li:hover, .hoster-tabs.bottom>li.active:hover {
  border: 1px solid rgba(255,255,255,.125) !important;
  border-top-color: #121212 !important;
}
.hoster-tabs>li>a:hover, .serie .hoster-tabs>li>a:focus {
  background-color: transparent !important;
}
.hoster-tabs>li.active>a:focus, .hoster-tabs>li.active>a:hover, .serie .hoster-tabs>li.active>a {
  border-color: inherit !important;
  background-color: #121212 !important;
}
  
  body > #root, .serie .epiInfo {
    background-color: #121212;
  }
}
