css:
@-moz-document domain("robloxdev.com") {
.main-container {
	max-width: 100%;
}

.home {
	max-width: 1440px;
}

.d-none.d-lg-block.col-md-3.col-lg-3.col-xl-3.bd-sidebar {
	max-width: 15%;
}

.d-xl-block.col-md-3.col-lg-2.col-xl-2.bd-toc {
	max-width: 15%;
	flex: 0 0 15%;
}

.col-md-9.col-lg-7.col-xl-7.bd-content.with-toc-gdpr {
	max-width: 70%;
	flex: 0 0 70%;
}

.header .logo {
	width: 256px;
}

.navbar-inner {
	width: 70%;
}

.actions-wrap {
	max-width: 50%;
	min-width: 50%;
}

.table.tbl-items .td-col-1 {
    width: 17.5%;
}

.table.tbl-items .td-col-2 {
    width: 10%;
}

.table td, .table.tbl-items td {
    padding: 6px;
}

.td-col-3 .r-side p {
    margin: 0;
}

.td-col-1 name {
    padding-left: 6px;
}
}
