css:
@-moz-document domain("robloxdev.com") {
.markdown-field-data p, p, .custom-select-box.select2-container .select2-choice, .custom-select-dropdown .select2-results .select2-result, .markdown-field-data, .alphabetical-index .idx-col li a, .search-box-wrap input, .header .search-box .form-control, .swiftype-widget .autocomplete > div ul li a, .search-container .search-lists p, .markdown-field-data p code, .markdown-field-data ul li, .bd-sidebar .bd-toc-link span, .header .navbar-nav a {
    color: #e4e4e4
}

.markdown-field-data span, .learn-roblox-pages .time {
    border: 1px solid #2D2D2D !important;
	background-color: #242424 !important
}

h1, h2, h3, h4, h6, h6 a, a:hover, html, body, .learn-roblox-pages .learning-materials-content .nav-tabs a, .custom-tabs .nav-tabs a, .code-block.light-theme a, .code-block.dark-theme a, .code-block.light-theme li, .code-block.dark-theme li, .header .home-link a {
    color: #FFF
}

.alert h4 {
    color: #444c56
}

.container-fluid, .header .wrap .navbar-inner, .header .sidebar-visible {
    background-color: #1B1B1B
}

.header .home-link, .header .sidebar-visible .back-icon {
    background-color: #060606
}

.code-block {
    border: 1px solid #121212;
}

.header .bd-navbar-nav .nav-item > a:hover {
    color: #e4e4e4;
    background-color: #363636
}

.header .bd-navbar-nav .nav-link > span {
    color: #e4e4e4
}

code, .learn-roblox-pages .learning-materials-content .nav-tabs-wrap, .custom-select-box.select2-container .select2-choice, .select2-drop, .tags-wrap li, .swiftype-widget .autocomplete, .swiftype-widget, .custom-tabs .nav-tabs-wrap {
    border: 1px solid #2D2D2D;
	background-color: #242424
}

.learn-roblox-pages .learning-materials-content .nav-tabs, .parents-n-educators {
    background-color: #242424
}

.bd-sidebar .bd-search input:focus, .form-control:focus {
    background-color: #363636
}

.header .bd-navbar-nav .nav-item > a, .header .bd-navbar-nav .nav-item > a.active {
    color: #E4E4E4
}

.header, body, .footer {
	background-color: #121212;
}

.header .search-box {
    border-left: 1px solid #2D2D2D;
}

.form-control, .focus-on-gameplay {
    background-color: #242424
}

.bd-sidebar {
    background-color: #242424
}

.focus-on-gameplay .card {
    background-color: #383838;
    box-shadow: 0 0 15px #000
}

.bd-sidebar .bd-toc-link, .bd-sidebar .bd-toc-item .sub-nav a {
    color: #E4E4E4
}

.bd-sidebar .bd-toc-item .sub-nav a:hover, .bd-sidebar .bd-toc-item .active, .custom-select-dropdown .select2-results .select2-result.select2-highlighted, .learn-roblox-pages .bd-toc-item .bd-toc-link:hover, .autocomplete .active, .swiftype-widget .autocomplete > div ul li a:hover {
    background-color: #363636
}

.table.tbl-items th {
    background-color: #363636
}

table, .table {
    border-color: #2D2D2D;
    background-color: #0000001a;
}

.table.tbl-items thead, .table thead, .table.tbl-items tbody, .table.tbl-items tr {
    border-color: #2D2D2D;
}

.header .logo a {
    background-image: url('https://devforum.roblox.com/uploads/default/original/3X/1/6/163773791a0bea32fc8db829e7bca99b8d5487c5.png') !important;
}

.alert-success {
    background-color: #2a4130cc;
}

.custom-tabs .nav-tabs {
    background-color: #FFF0
}

.prettyprint ol li span, .actions-panel .copy span, .actions-panel .theme-wrap span, .expand-panel .view span {
    border: 0 solid #000 !important;
    background-color: #FFF0 !important
}

.main-container {
    max-width: 100%;
}

.code-block.light-theme, li.L0, li.L1, li.L2, li.L3, li.L4, li.L5, li.L6, li.L7, li.L8, li.L9 {
    background-color: #242424 !important;
    color: #FFF;
}

.code-block.light-theme .pln, .code-block.light-theme .pun {
    color: #FFF;
}

.code-block.light-theme .copy-icon {
    background-image: url("http://dgyj45q6wb7mf.cloudfront.net/static/images/copy-icon-white.svg");
}

.code-block.light-theme .expand-collapse-icon {
    background-image: url("http://dgyj45q6wb7mf.cloudfront.net/static/images/expand-icon-white.svg");
}

.fullscreen-view.code-block.light-theme .expand-collapse-icon {
    background-image: url("http://dgyj45q6wb7mf.cloudfront.net/static/images/collapse-icon-white.svg");
}

.code-block.light-theme .prettyprint {
    border-top: 1px solid #2D2D2D !important;
    border-bottom: 1px solid #2D2D2D !important;
}

.code-block.dark-theme .prettyprint {
    border-top: 1px solid #2D2D2D !important;
    border-bottom: 1px solid #2D2D2D !important;
}

.code-block .prettyprint ol:before {
    border-left: 1px solid #2D2D2D
}

.code-block .prettyprint ol li::before {
    color: #a6a6a6
}

.swiftype-widget .autocomplete:before {
    border-bottom-color: #2D2D2D
}
}
