css:
@-moz-document domain("developer.roblox.com") {
/* These are Discord Colors: http://www.color-hex.com/color-palette/25362 */

/* Header Background Color */
.header {
	background-color: rgb(44,47,51) !important
}

/* Header logo color correction (DevForum dark theme logo) */
.header .logo a {
	background-image: url(https://devforum.roblox.com/uploads/default/original/3X/1/6/163773791a0bea32fc8db829e7bca99b8d5487c5.png) !important;
}

/* Header text links */
.header .nav-link span {
	color: rgba(255,255,255,.8) !important
}

/* Header search adjustment */
.header .search-box {
	border-left: 1px solid rgb(35,39,42) !important
}
.header .search-box .form-control {
	background-color: rgb(44,47,51) !important;
	color: rgba(255,255,255,.8) !important
}
.autocomplete {
	background-color: rgb(44,47,51) !important;
	border: 2px solid rgb(44,47,51) !important
}
.autocomplete .articles {
	border-bottom-color: rgb(44,47,51) !important
}
.swiftype-widget .autocomplete > div ul li a:hover {
	background-color: rgb(35,39,42) !important
}

/* Sidebar header and link color correction */
.search-box-wrap input {
	background-color: rgb(44,47,51) !important;
	color: rgba(255,255,255,.8) !important
}
p.home-link {
	background-color: rgb(44,47,51) !important;
	border-bottom: 1px solid rgb(35,39,42) !important
}
p.home-link a {
	color: rgba(255,255,255,.8) !important
}
.navbar-inner {
	background-color: rgb(44,47,51) !important
}
.bd-sidebar {
	background-color: rgb(44,47,51) !important
}
.bd-sidebar .bd-toc-link {
	color: rgba(255,255,255,.8) !important
}
.bd-sidebar .bd-toc-item .sub-nav a {
	color: rgba(255,255,255,.8) !important
}
.bd-sidebar .bd-toc-item .sub-nav a:hover {
	background-color: rgb(35,39,42) !important
}
.learn-roblox-pages .bd-toc-item .bd-toc-link:hover {
	background-color: rgb(35,39,42) !important
}
.bd-sidebar .bd-toc-item .active {
	background-color: rgb(114,137,218) !important
}
.mob-nav-item a:hover {
	background-color: rgb(35,39,42) !important
}
.back-icon {
	background-color: rgb(44,47,51) !important
}
input:focus {
	border-color: rgb(114,137,218) !important
}
.header .nav-item a.active {
	border-bottom: 2px solid rgb(114,137,218) !important
}

/* Main body recoloring */
body {
	background-color: rgb(35,39,42) !important
}
footer {
	background-color: rgb(44,47,51) !important
}
body .container-fluid {
	background-color: rgb(35,39,42) !important
}
h1,h2,h3,h4,h5,h6 {
	color: rgba(255,255,255,.8) !important
}
code,a.property-item {
	background-color: rgb(35,39,42) !important;
	border-color: rgb(114,137,218) !important;
	color: rgb(114,137,218) !important
}
code a {
	color: rgb(114,137,218) !important
}
.markdown-field-data {
	color: rgba(255,255,255,.8) !important
}
.markdown-field-data li {
	color: rgba(255,255,255,.8) !important
}
.table-bordered th, .table-bordered td {
	border: 1px solid rgba(255,255,255,.8) !important
}
.table-primary,.table-primary th,.table-primary td {
	background: none !important
}
.bg-light {
	background: none !important
}
td mark {
	background-color: rgb(35,39,42) !important;
	color: rgba(255,255,255,.8) !important
}
div.r-side {
	color: rgba(255,255,255,.8) !important
}
.alert-success {
	background: none !important;
	border: 1px solid rgb(67,181,129) !important;
	border-radius: .5rem !important;
	color: rgb(67,181,129) !important
}
.alert-success p {
	color: rgb(67,181,129) !important
}
.alert-info {
	background: none !important;
	border: 1px solid rgb(250,166,26) !important;
	border-radius: .5rem !important;
	color: rgb(250,166,26) !important
}
.alert-info p {
	color: rgb(250,166,26) !important
}
.alert-primary {
	background: none !important;
	border: 1px solid rgb(114,137,218) !important;
	border-radius: .5rem !important;
	color: rgb(114,137,218) !important
}
.alert-primary p {
	color: rgb(114,137,218) !important
}
.alert-secondary {
	background: none !important;
	border: 1px solid rgb(153,170,181) !important;
	border-radius: .5rem !important;
	color: rgb(153,170,181) !important
}
.alert-secondary a {
	color: rgb(153,170,181) !important
}
.alert-warning {
	background: none !important;
	border: 1px solid rgb(250,166,26) !important;
	border-radius: .5rem !important;
	color: rgb(250,166,26) !important
}
.alert-warning p {
	color: rgb(250,166,26) !important
}
.alert-danger {
	background: none !important;
	border: 1px solid rgb(240,71,71) !important;
	border-radius: .5rem !important;
	color: rgb(240,71,71) !important
}
.alert-danger a {
	color: rgb(240,71,71) !important
}
p {
	color: rgba(255,255,255,.8) !important
}
a, .inner-wrap h5 {
	color: rgb(114,137,218) !important
}
div.desc {
	color: rgba(255,255,255,.8) !important
}
ul.tags-list li {
	background: none !important;
	border: 1px solid rgb(114,137,218) !important;
	color: rgb(114,137,218) !important
}
.nav-tabs,.nav-tabs-wrap {
	background-color: rgb(44,47,51) !important
}
.nav-tabs-wrap .custom-select-box a {
	background-color: rgb(44,47,51) !important;
	border: 2px solid rgb(35,39,42) !important
}
.custom-select-dropdown {
	background-color: rgb(44,47,51) !important;
	border-color: rgb(35,39,42) !important
}
.select2-results div {
	color: rgb(114,137,218) !important
}
.select2-results div:hover {
	background-color: rgb(35,39,42) !important
}
.focus-on-gameplay {
	background-color: rgb(44,47,51) !important
}
.parents-n-educators {
	background-color: rgb(35,39,42) !important
}
.focus-on-gameplay .card {
	background-color: rgb(35,39,42) !important
}
.r-details a {
	color: rgba(255,255,255,.8) !important
}
.markdown-field-data span {
	background: none !important;
	border-radius: .25rem !important;
}
font[color="green"] {
	color: rgb(67,181,129) !important
}

/* Effortless modifications (I coudln't care to categorize them) */
.bd-sidebar-wrap {
    background-color: rgb(44,47,51) !important;
}
.table .table-row td:first-child {
    background-color: rgb(35,39,42) !important;
}
.table .api-tag {
    color: rgb(114,137,218) !important;
}
html, body {
    color: rgba(255,255,255,.8) !important;
}
.table .arguments-wrap {
    color: rgba(255,255,255,.8) !important;
}
.table.tbl-items th {
    background-color: rgb(35,39,42) !important;
}
.gdpr-panel {
    background-color: rgb(35,39,42) !important;
}
}
