/* i really want this to be global */

#chat-pane .chat-message-continued{
  display: none !important;
}

#chat-pane .chat-action{
  display:none !important;
}

#chat-pane .chat-message-continued{
  display: none !important;
}

#chat-pane .chat-timestamp{
  display:none !important;
}

#chat-pane .chat-row:last-child{
  border: 0px !important;
}

#chat-pane .chat-message-divider{
  display: none !important;
}

html.app-rightbar-conference.app-rightbar-expanded #rightbar-container .rightbar-drawer{
width: 400px !important;
}

#chat-pane .chat-name{
  color: #ff5722 !important;
}

#rb-timer{
font-size: 9px !important;   
}

.rightbar-above-header{
  padding: 0 0 0 0 !important;
}

#rb-timer-container{
 padding: 0 0 0 0 !important;   
}

@-webkit-keyframes rotating /* Safari and Chrome */ {
from {
  -webkit-transform: rotate(0deg);
  -o-transform: rotate(0deg);
  transform: rotate(0deg);
}
to {
  -webkit-transform: rotate(360deg);
  -o-transform: rotate(360deg);
  transform: rotate(360deg);
}
}
@keyframes rotating {
from {
  -ms-transform: rotate(0deg);
  -moz-transform: rotate(0deg);
  -webkit-transform: rotate(0deg);
  -o-transform: rotate(0deg);
  transform: rotate(0deg);
}
to {
  -ms-transform: rotate(360deg);
  -moz-transform: rotate(360deg);
  -webkit-transform: rotate(360deg);
  -o-transform: rotate(360deg);
  transform: rotate(360deg);
}
}
#chat-pane .chat-picture {
-webkit-animation: rotating 10s linear infinite;
-moz-animation: rotating 10s linear infinite;
-ms-animation: rotating 10s linear infinite;
-o-animation: rotating 10s linear infinite;
animation: rotating 10s linear infinite;
}
