css:
@-moz-document url-prefix("https://www.youtube.com") {
/* ==UserStyle==
@name         You Tube - Red n Dark Theme. Black Theme Style.
@description  YouTube Red n' Dark - A black theme color to avoid change every time to You Tube Dark Theme. For those clever users that NEVER sign-in (and save their privacy...)
@version      2.4.93
@author       David R. Mier
@homepageURL  https://www.behance.net/DrMier
@supportURL   Please write on the comments sections.
@license      Attribution-NonCommercial-NoDerivatives 4.0 International
==/UserStyle== */


:root{
    
    /* Main Theme Color Accent */
    --yt-rash-MainThemeColor:#e30000;
    --yt-rash-MainThemeColorDimmed: #a00;
    
    /* Progress Bar in Video */
    --yt-rash-PlayerProgressBar: #0091ff;
    --yt-rash-PlayerControls: #f2faff;
    
    /* BG Controls */
    --yt-rash-BGMainSectionColor:#111;
    --yt-rash-BGTopSectionColor:#040404;
    --yt-rash-BGSideSectionColor:#222;
    --yt-rash-BGListResultColor: #111;

    /* You Tube - Dark Search Box */
    --yt-rash-searchbox-background: #313131;
    --ytd-rash-searchbox-legacy-border-color:#3b3b3b;
    --ytd-rash-searchbox-legacy-border-shadow-color: #2a2a2a;
    --yt-rash-searchbox-text-color: #a8a8a8;
    --yt-rash-searchbox-text-color-not-focus: #666;
    --ytd-rash-searchbox-legacy-button-border-color: hsl(0, 0%, 18.82%);
    --ytd-rash-searchbox-legacy-button-color: hsla(0, 0%, 100%, .08);
    --ytd-rash-searchbox-legacy-button-hover-color: hsla(0, 0%, 100%, .08);
    --ytd-rash-searchbox-legacy-button-hover-border-color: hsl(0, 0%, 18.82%);
    --yt-rash-searchbox-suggestions-border: #777;
    --yt-rash-searchbox-suggestions-background: #777;
    --yt-rash-searchbox-suggestions-text: #eee;
    --yt-rash-searchbox-suggestions-report: rgba(50, 50, 50, 0.9);
    --yt-rash-searchbox-suggestions-reporthover: rgba(49, 49, 49, .9);
 
    
    /* YouTube Logo and Logotype Color */
    --yt-rash-YOUTUBELogoColor: #f00;
    --yt-rash-YOUTUBELogoTypeColor: #fff;
    --yt-rash-YOUTUBELogoTypeCountryColor: #f00;
    --yt-rash-YOUTUBELogoSideMenuSelector: #bbb;
    
    /* Sign In Color Text */
    --yt-rash-SignInTextColor: #a00;
    
    /* Button Paper - Used in More or Expend options */
    --yt-rash-ButtonExpanderColorNormal: #333;
    --yt-rash-ButtonExpanderColorHover: var(--yt-rash-MainThemeColor);
    --yt-rash-ButtonExpanderTextColor: #686767;
    
    /* Video Info Control */
    --yt-rash-VideoTitlesColor: #ccc;
    --yt-rash-VideoOwnerColorNormal:#888;
    --yt-rash-VideoOwnerColorHover: var(--yt-rash-MainThemeColor);
    --yt-rash-VideoMetadata:#aaa;
    --yt-rash-VideoDescription:#777;
    
    /* Button Subscribe in Main Page Suggestions*/
    --yt-rash-SubscribeQuickButtonNormal: #333;
    --yt-rash-SubscribeQuickButtonTextNormal: var(--yt-rash-MainThemeColor);
    --yt-rash-SubscribeQuickButtonHover: var(--yt-rash-MainThemeColor);
    --yt-rash-SubscribeQuickButtonTextHover:#fff;
    
    /* Separators Color */
    --yt-rash-SeparatorColor: hsl(0, 0%, 24%);
    
    /* Front Page Side Menu */
    --yt-rash-FP-SideMenuTextNormal: #aaa;
    --yt-rash-FP-SideMenuTextHover: var(--yt-rash-MainThemeColor);
    --yt-rash-FP-SideMenuSelectionHover: #333;
    --yt-rash-FP-SideMenuSignInPromoText: #555;
    
    /* Search Page Colors */
    --yt-rash-SP-ResultsCountColor: var(--yt-rash-MainThemeColor);
    --yt-rash-SP-FilterButtonTitle: var(--yt-rash-MainThemeColor);
    --yt-rash-SP-FilterOptionsTitles: #bbb;
    --yt-rash-SP-FilterOptionsTexts: #999;
    --yt-rash-SP-FilterOptionPromoted: var(--yt-rash-SignInTextColor);
    --yt-rash-SP-ResultsChannelTitle: var(--yt-rash-VideoTitlesColor);
    --yt-rash-SP-ResultsChannelTitleHover: var(--yt-rash-MainThemeColor);
    --yt-rash-SP-ResultsChannelMetadata: var(--yt-rash-VideoMetadata);
    --yt-rash-SP-ResultsChannelDescription: var(--yt-rash-VideoDescription);                                                                     
    --yt-rash-SP-ResultsBadgeBackground: #2a2a2a;
    --yt-rash-SP-ResultsBadgeText: #a00;
    --yt-rash-SP-ResultsPlaylistTitle: var(--yt-rash-MainThemeColorDimmed);
    --yt-rash-SP-ResultsPlaylistContent: var(--yt-rash-VideoMetadata);
    --yt-rash-SP-ResultsPlaylistShowMore: var(--yt-rash-MainThemeColorDimmed);
    
    
    /* Video Player Page Colors */
    --yt-rash-VP-AutoplayButtonColorON: var(--yt-rash-MainThemeColor);
    
    --yt-rash-VP-VideoResultsPlaylistTitle: var(--yt-rash-MainThemeColor);
    --yt-rash-VP-VideoResultsPlaylistMoreButtonNormal: var(--yt-rash-SubscribeQuickButtonTextNormal);
    --yt-rash-VP-VideoResultsPlaylistMoreButtonHover: var(--yt-rash-SubscribeQuickButtonTextHover);
      
    --yt-rash-VP-LikeIconColor: hsl(91, 100%, 41%);
    --yt-rash-VP-LikeBarColor: hsl(91, 100%, 41%);
    --yt-rash-VP-DislikeBarColor: var(--yt-rash-MainThemeColor);
    --yt-rash-VP-DislikeLikeCounterColor: var(--yt-rash-VideoMetadata);
    --yt-rash-VP-ShareText: var(--yt-rash-MainThemeColor);
    
    --yt-rash-VP-CommentNew: #bbb;
    --yt-rash-VP-CommentAuthor: #4e9eec;
    --yt-rash-VP-CommentDate: #888;
    --yt-rash-VP-CommentContent: #aaa;
    --yt-rash-VP-CommentLikeCount: #56B200;

    --yt-rash-VP-ChatContainerColor: #222;
    --yt-rash-VP-ChatContainerText: #bbb;
    --yt-rash-VP-ChatContainerTickerColor: #333;
    --yt-rash-VP-ChatContainerScroller: #040404;
    --yt-rash-VP-ChatMessageAuthor: #3c86cc;
    --yt-rash-VP-ChatMessageText: #bbb;
    
    --yt-rash-VP-UserPlaylistContainerColor: #222;
    --yt-rash-VP-UserPlaylistContainerText: #777;
    --yt-rash-VP-UserPlaylistContainerTracksCount: #aaa;
    
    
    /* Channel Section Colors */
    --yt-rash-Channel-BannerBackgroundColor: #3d3d3d;
    --yt-rash-Channel-BannerOwnerTitleColor: #fff;
    --yt-rash-Channel-HeaderMenuOptions: #bbb;   
    --yt-rash-Channel-SearchField: #fff;
        
    --yt-rash-Channel-FeaturedSectionTitle: #aaa;
    --yt-rash-Channel-FeaturedSectionChannelName: #888;   
 
    
}
/* Google Fonts (SO FAR IMPOSSIBLE TO USE ROBOTO FONT)
--------------------------------------------------------------------------------*/
@font-face {
  font-family: 'Roboto' !important;
  font-style: normal;
  font-weight: 400;
  src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v20/KFOmCnqEu92Fr1Mu4mxK.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}


/* ::: YOU TUBE - COMMON VAR 

/* You Tube - Loading Flickering Correction */
html[style] {
    background-color: var(--yt-rash-BGMainSectionColor) !important;
}

body {
    background: #000;
    /* font-family: 'Geneva', sans-serif !important;  (SO FAR IMPOSSIBLE TO USE ROBOTO FONT)*/
}

/* You Tube :: Master Background Control */
ytd-app[background-color-update_] {
    background: black;
}


/* YouTube - Logo Color */
path.style-scope.ytd-topbar-logo-renderer{
    fill: var(--yt-rash-YOUTUBELogoColor);
}
/* YouTube - LogoType */
#logo-icon-container.ytd-topbar-logo-renderer #youtube-paths.ytd-topbar-logo-renderer path.ytd-topbar-logo-renderer {
    fill: var(--yt-rash-YOUTUBELogoTypeColor);
}
/* YouTube - LogoType Color - Country Color */
#country-code.ytd-topbar-logo-renderer {
    color: var(--yt-rash-YOUTUBELogoTypeCountryColor);
}

/* YouTube - Icons Color including HOME TRENDING HISTORY */
yt-icon, .yt-icon-container.yt-icon {
    fill: var(--yt-rash-MainThemeColor, currentcolor);
}


/* YouTube - Button used in Expand or More */
/* Button - Color Normal */
paper-button {
    background-color: var(--yt-rash-ButtonExpanderColorNormal);
}
/* Button - Color Hover */
paper-button:hover {
    background-color: var(--yt-rash-ButtonExpanderColorHover);
}
/* Button - Text Color */
#toggle.ytd-grid-renderer {
    color: var(--yt-rash-ButtonExpanderTextColor);
}


/* :: You Tube - Dark Search Box :: */
/* SearchBox - Background */
ytd-searchbox[mode="legacy"] #container.ytd-searchbox, #container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox {
    background-color: var(--yt-rash-searchbox-background);
    border: 1px solid var(--ytd-rash-searchbox-legacy-border-color);
    border-right: none;
    box-shadow: inset 0 1px 2px var(--ytd-rash-searchbox-legacy-border-shadow-color);
    padding: 2px 6px;
    border-radius: 2px 0 0 2px;
    color: var(--yt-rash-searchbox-text-color-not-focus);
}


/* SearchBox - Caret Box Active Color */
ytd-searchbox[mode="legacy"][has-focus] #container.ytd-searchbox, #container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"][has-focus] #container.ytd-searchbox{
    border:1px solid var(--yt-rash-MainThemeColorDimmed)
}
/* Search Box - Text Color */
ytd-searchbox[mode="legacy"] #container.ytd-searchbox input.ytd-searchbox, #container.ytd-searchbox input.ytd-searchbox, ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox input.ytd-searchbox {
    color: var(--yt-rash-searchbox-text-color);
}
/* Search Box - Text Color NOT FOCUS */
ytd-searchbox input {
    color: var(--yt-rash-searchbox-text-color-not-focus);
}
/* SearchBox - Icon */
#search-icon-legacy.ytd-searchbox {
    cursor: pointer;
    width: 65px;
    border: 1px solid var(--ytd-rash-searchbox-legacy-button-border-color);
    background-color: var(--ytd-rash-searchbox-legacy-button-color);
    border-radius: 0 2px 2px 0;
    margin: 0;
    --yt-icon-button-icon-width: 24px;
    --yt-icon-button-icon-height: 24px;
}
/* SearchBox - Icon Hover */
ytd-searchbox[mode="legacy"] #search-icon-legacy.ytd-searchbox:hover, ytd-searchbox[mode="legacy-centered"] #search-icon-legacy.ytd-searchbox:hover, #search-icon-legacy.ytd-searchbox:hover {
    border-color: var(--ytd-rash-searchbox-legacy-button-hover-color);
    background-color: var(--ytd-rash-searchbox-legacy-button-hover-border-color);
    box-shadow: 0 1px 0 rgba(0, 0, 0, 0.10);
}

/* SearchBox Suggestions - Section */
/* SearchBox Suggestions - Background Color */
div.sbsb_a{
    background: var(--yt-rash-searchbox-background);
}
/* SearchBox Suggestions - Text Color */
.gsfs {
    font-size: 1.6rem;
    color: var(--yt-rash-searchbox-text-color);
}
/* SearchBox Suggestions - Border Color & Width */
.sbdd_b{
    border: 2px solid var(--yt-rash-searchbox-suggestions-border);
/* SearchBox Suggestions - Text Color */
}
.sbsb_d{
    background-color: var(--yt-rash-searchbox-suggestions-background);
    color: var(--yt-rash-searchbox-suggestions-text);
}
/* SearchBox Suggestions - Report Section */
.sbfl_b {
    background: var(--yt-rash-searchbox-suggestions-report);
}
.sbfl_b:hover {
    background: var(--yt-rash-searchbox-suggestions-reporthover);
    color: var(--yt-rash-MainThemeColor);
}

/* Spinner/Loader for Content / Comments */
.spinner-layer.paper-spinner{
	position:absolute;
	width:100%;
	height:100%;
	opacity:0;
	white-space:nowrap;
	color:hsl(0, 100%, 50%);
}

/* 500 Internal Server Error Correction  */
/* Header Color Correction on Error Page */
header {
    background-color: var(--yt-rash-BGTopSectionColor);
}
/* Error Text Color */
h1, p {
    color: var(--yt-rash-VideoMetadata);
}


/* :::::::::::::::::::::::: */
/* :: MAIN MENU CONTROLS :: */
/* :::::::::::::::::::::::: */

canvas.ytd-multi-page-menu-renderer, caption.ytd-multi-page-menu-renderer, center.ytd-multi-page-menu-renderer, cite.ytd-multi-page-menu-renderer, code.ytd-multi-page-menu-renderer, dd.ytd-multi-page-menu-renderer, del.ytd-multi-page-menu-renderer, dfn.ytd-multi-page-menu-renderer, div.ytd-multi-page-menu-renderer, dl.ytd-multi-page-menu-renderer, dt.ytd-multi-page-menu-renderer, em.ytd-multi-page-menu-renderer, embed.ytd-multi-page-menu-renderer, fieldset.ytd-multi-page-menu-renderer, font.ytd-multi-page-menu-renderer, form.ytd-multi-page-menu-renderer, h1.ytd-multi-page-menu-renderer, h2.ytd-multi-page-menu-renderer, h3.ytd-multi-page-menu-renderer, h4.ytd-multi-page-menu-renderer, h5.ytd-multi-page-menu-renderer, h6.ytd-multi-page-menu-renderer, hr.ytd-multi-page-menu-renderer, i.ytd-multi-page-menu-renderer, iframe.ytd-multi-page-menu-renderer, img.ytd-multi-page-menu-renderer, ins.ytd-multi-page-menu-renderer, kbd.ytd-multi-page-menu-renderer, label.ytd-multi-page-menu-renderer, legend.ytd-multi-page-menu-renderer, li.ytd-multi-page-menu-renderer, menu.ytd-multi-page-menu-renderer, object.ytd-multi-page-menu-renderer, ol.ytd-multi-page-menu-renderer, p.ytd-multi-page-menu-renderer, pre.ytd-multi-page-menu-renderer, q.ytd-multi-page-menu-renderer, s.ytd-multi-page-menu-renderer, samp.ytd-multi-page-menu-renderer, small.ytd-multi-page-menu-renderer, span.ytd-multi-page-menu-renderer, strike.ytd-multi-page-menu-renderer, strong.ytd-multi-page-menu-renderer, sub.ytd-multi-page-menu-renderer, sup.ytd-multi-page-menu-renderer, table.ytd-multi-page-menu-renderer, tbody.ytd-multi-page-menu-renderer, td.ytd-multi-page-menu-renderer, tfoot.ytd-multi-page-menu-renderer, th.ytd-multi-page-menu-renderer, thead.ytd-multi-page-menu-renderer, tr.ytd-multi-page-menu-renderer, tt.ytd-multi-page-menu-renderer, u.ytd-multi-page-menu-renderer, ul.ytd-multi-page-menu-renderer, var.ytd-multi-page-menu-renderer {
    background: var(--yt-rash-BGSideSectionColor);
}

/* BG color Fix for menu accessed from other sections NOT HOME */
paper-item.ytd-account-settings {
    background-color: #111;
}
paper-item.ytd-account-settings:hover {
    background-color: #111;
}

/* Options Text Color Option */
paper-item.ytd-compact-link-renderer {
    color: var(--yt-rash-VideoOwnerColorNormal);
}

/* Option Dark Theme Text Color Option */
ytd-toggle-theme-compact-link-renderer {
    --paper-item-min-height: 40px;
    color: var(--yt-rash-VideoOwnerColorNormal);
}
/* Option Dark Theme Submenu Text Color Option */
ytd-simple-menu-header-renderer {
    background-color: #222;
    color: var(--yt-rash-MainThemeColor);
}
/* Option Dark Theme Submenu Text Color Option */
.description.ytd-toggle-item-renderer {
    color: var(--yt-rash-VideoOwnerColorNormal);
}

/* Option Language Submenu Text Color */
#language.ytd-account-settings, #country.ytd-account-settings, #restricted.ytd-account-settings, .container.ytd-account-settings {
    background-color: var(--yt-rash-BGListResultColor);
}


/* :: OPTION HELP SUBMENU TEXT COLOR :: */

/* Header BG Color */
.ghp-header {
    background-color: #222;
}
.help-panel-header {
    background-color: #222;;
}
/* :: SEARCH BOX COLOR CONTROL :: */
/* Searchbox Header BG */
.ghp-header-searchBox {
    border: 0;
    box-sizing: border-box;
    color: #000;
    opacity: .84;
    background-color: #313131;
}

/* Search Form Color Control */
.ghp-header-searchFormContainer {
    align-items: center;
    background-color: #313131;;
    border-radius: 2px;
    position: relative;
    width: 100%;
    box-shadow: 0 2px 5px 0 rgba(0,0,0,0.258824),0 2px 10px 0 rgba(0,0,0,0.156863);
}

/* Help Results BG Container */
.ghp-card {
    box-shadow: 0 2px 5px 0 rgba(0,0,0,0.258824),0 2px 10px 0 rgba(0,0,0,0.156863);
    background-color: #111;
}
.ghp-contentFrame {
    background: #111;;
}
/* Help Results Items Color Control */
.help-panel-search-result__text{
    border-bottom: 1px solid var(--yt-rash-SeparatorColor) !important;
    color: var(--yt-rash-VideoOwnerColorNormal) !important;
}
.ghp-searchResults .ghp-iconTextComponent-label {
    border-bottom: 1px solid var(--yt-rash-SeparatorColor) !important;
    color: var(--yt-rash-VideoOwnerColorNormal) !important;
}
span.help-panel-search-result__text.help-panel-search-result__text--external{
    border-bottom: 1px solid var(--yt-rash-SeparatorColor) !important;
    color: var(--yt-rash-VideoOwnerColorNormal) !important;
}

/* Help Results Separator after options */
.ghp-separator {
    border-top: 1px solid var(--yt-rash-VideoOwnerColorNormal);
}

/* Help Results Contact US options */
.ghp-iconTextComponent-label {
    color: var(--yt-rash-VideoOwnerColorNormal);
}


/* :: OPTION KEYBOARD SUBMENU COLOR CONTROL :: */
/* Main Background Color */
ytd-hotkey-dialog-renderer[dialog][dialog][dialog] {
    background: var(--yt-rash-BGSideSectionColor);
}
/* Section Main Title */
#title.ytd-hotkey-dialog-renderer {
    color: var(--yt-rash-MainThemeColorDimmed);
}

/* Actions Titles Colors */
#label.ytd-hotkey-dialog-section-option-renderer {
    color: var(--yt-rash-VideoOwnerColorNormal);
}
/* Actions Titles Hotkeys Colors */
#hotkey.ytd-hotkey-dialog-section-option-renderer {
    color: var(--yt-rash-MainThemeColor);
}

/* Actions Separator Color */
ytd-hotkey-dialog-section-option-renderer {
    border-top: 1px solid var(--yt-rash-SeparatorColor);
}


/* :: OPTION PROFILE SUBMENU COLOR CONTROL :: */
/* Header Main Title Background */
ytd-active-account-header-renderer {
    background-color: #3c3c3c;
}

/* Header Main Title Font Color */
#account-name.ytd-active-account-header-renderer {
    color: #eee;
}


/* :: OPTION NOTIFICATIONS SUBMENU COLOR CONTROL :: */
/* Main Content Background */
ytd-multi-page-menu-renderer[menu-style="multi-page-menu-style-type-notifications"] #sections.ytd-multi-page-menu-renderer > .ytd-multi-page-menu-renderer{
    background-color: #111;
}
/* Footer Background */
ytd-multi-page-menu-renderer[menu-style="multi-page-menu-style-type-notifications"] {
    background: #e30000;
}



/*  ::: YOUTUBE -  GLOBAL VARIABLES ::: */

/* VIDEO TITLE
   Used in : Front Page Recommended Channel Big Promo Titles */
#video-title.ytd-video-renderer {
    color: var(--yt-rash-VideoTitlesColor);
}
/* VIDEO OWNER
   Used in : Front Page Recommended Channel Big Promo Owner, Results Page, Player Playlist*/
#byline.ytd-video-meta-block {
    --yt-endpoint-color: var(--yt-rash-VideoOwnerColorNormal);
}
a.yt-simple-endpoint.yt-formatted-string:only-of-type:hover {
    color: var(--yt-rash-VideoOwnerColorHover);
}
/* VIDEO METADATA */
#metadata-line.ytd-video-meta-block{
color: var(--ytd-metadata-line-color, var(--yt-rash-VideoMetadata));
}


/* SUBSCRIBE BUTTON (Big Version) */
paper-button#button.style-scope.ytd-button-renderer.style-destructive{
    background-color: var(--yt-rash-SubscribeQuickButtonNormal);
    font-size: 85%;
    color: var(--yt-rash-SubscribeQuickButtonTextNormal);
}
paper-button#button.style-scope.ytd-button-renderer.style-destructive:hover{
    background-color: var(--yt-rash-SubscribeQuickButtonHover);
    font-size: 85%;
    color: var(--yt-rash-SubscribeQuickButtonTextHover);
}

/* LIST BACKGROUND COLOR - USED IN: FRONT PAGE GRID VIDEO, SEARCH RESULTS, PLAYLIST VIEWER SECTION */
div.ytd-section-list-renderer{
    background-color: var(--yt-rash-BGListResultColor);
}
/* SECTION LIST BACKGROUND COLOR - USED IN: FRONT PAGE GRID VIDEO, SEARCH RESULTS, PLAYLIST VIEWER SECTION, COMMENTS */
ytd-item-section-renderer {
    background-color: var(--yt-rash-BGListResultColor);
}

/* YouTube - Sign In button text color  */
ytd-button-renderer #button.ytd-button-renderer {
    color: var(--yt-rash-SignInTextColor);
    background-color: var(--yt-rash-BGSideSectionColor);
}

/* :::::::::::::::::::::::::::::::::: */
/* ::::: FRONT PAGE - VIDEO GRID :::: */ 
/* :::::::::::::::::::::::::::::::::: */

/* Background Main Color */
ytd-browse {
    background-color: var(--yt-rash-BGMainSectionColor) !important;
}
ytd-browse[page-subtype="channels"]{
    background-color: var(--yt-rash-BGMainSectionColor) !important;
}

/* Video Grid - Background Color */
#primary.ytd-two-column-browse-results-renderer {
    background-color: var(--yt-rash-BGListResultColor);
}
/* Video Grid - Sections Titles */
#title.ytd-shelf-renderer {
    color: var(--yt-rash-MainThemeColor);
}
/* Video Grid - Sections Title UPDATE NOV 2019 WIDESCREEN HOME PAGE */
#title.ytd-rich-grid-renderer {
    color: var(--yt-rash-MainThemeColor);
}
#title.ytd-rich-shelf-renderer {
    color: var(--yt-rash-MainThemeColor);
}

/* Video Grid - Video Title */
#video-title.yt-simple-endpoint.ytd-grid-video-renderer {
    color: var(--yt-rash-VideoTitlesColor);
}
/* Video Grid - Video Title UPDATE NOV 2019 WIDESCREEN HOME PAGE */
#video-title.ytd-rich-grid-video-renderer {
    color: var(--yt-rash-VideoTitlesColor);
}


/* Video Grid - Video Author */
ytd-grid-video-renderer.grid #byline.ytd-grid-video-renderer, [meta-block].grid.ytd-grid-video-renderer #byline.ytd-grid-video-renderer {
    --yt-endpoint-color: var(--yt-rash-VideoOwnerColorNormal);
    --yt-endpoint-hover-color: var(--yt-rash-VideoOwnerColorHover);
}
yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string:hover {
    color: var(--yt-rash-VideoOwnerColorHover);
}

/* Video Grid - Video Metadata Color (Views & Age)*/
#metadata-line.ytd-grid-video-renderer {
    color: var(--ytd-metadata-line-color, var(--yt-rash-VideoMetadata));
}


/* Video Grid - Playlist Titles */
h3.ytd-grid-radio-renderer {
    color: var(--yt-rash-VideoTitlesColor);
}


/* Video Grid - Channel Section Author Color*/
