/* :::::::::::::::::::::::: */ 
/* ::::: CHANNEL PAGE ::::: */
/* :::::::::::::::::::::::: */

/* Channel Main Background */
ytd-browse[page-subtype="channels"] {
    background-color: var(--yt-rash-BGMainSectionColor) !important;
}
ytd-page-manager > .ytd-page-manager {
    background-color: var(--yt-rash-BGMainSectionColor) !important;
}

/* Channel Header Background */
#channel-header.ytd-c4-tabbed-header-renderer, ytd-c4-tabbed-header-renderer {
    --yt-lightsource-section1-color: var(--yt-rash-Channel-BannerBackgroundColor);
}

/* Channel - Author Name */
#channel-title.ytd-c4-tabbed-header-renderer {
    color: var(--yt-rash-Channel-BannerOwnerTitleColor);
}
/* /* Channel - Author Name | 2019 Fix */
#text.ytd-channel-name {
    color: var(--yt-rash-Channel-BannerOwnerTitleColor);
}

/* Channel - Suscribers Counter */
#subscriber-count.ytd-c4-tabbed-header-renderer {
    color: var(--yt-rash-VideoMetadata);
}

/* Channel Menu Header */

/* Channel Menu - Tabbed Header Background */
#tabs-inner-container.ytd-c4-tabbed-header-renderer{
    background-color: var(--yt-rash-Channel-BannerBackgroundColor)
}

/* Channel Menu - Selected Section */
paper-tab.iron-selected.ytd-c4-tabbed-header-renderer {
    color: var(--yt-rash-MainThemeColor);
}
/* Channel Menu - Tabs Text */
paper-tabs.ytd-c4-tabbed-header-renderer {
    --paper-tabs-selection-bar-color: var(--yt-rash-MainThemeColor);
    color: var(--yt-rash-Channel-HeaderMenuOptions);
}
paper-tab.ytd-c4-tabbed-header-renderer:hover {
    color: var(--yt-rash-MainThemeColor);
}
/* Channel Menu - Search Field */
ytd-expandable-tab-renderer.ytd-c4-tabbed-header-renderer {
    --yt-expandable-color: var(--yt-rash-Channel-SearchField);
    --yt-expandable-unselected-color: var(--yt-rash-Channel-SearchField);
}
/* Channel Menu - Search Field Glow Color */
ytd-expandable-tab-renderer {
    color: var(--yt-rash-MainThemeColor);
}

/* Channel - Start Video - Title */
#title.ytd-channel-video-player-renderer {
    --yt-endpoint-color: var(--yt-rash-VideoTitlesColor);
}
/* Channel - Start Video - Description */
.content.ytd-channel-video-player-renderer {
    display: block;
    color: var(--yt-rash-VideoDescription);
}
/* Channel - Start Video - Read More */
.more-button.ytd-channel-video-player-renderer {
    --yt-endpoint-color: var(--yt-rash-ButtonExpanderTextColor);
    font-size: 1.1rem;
}

/* Channel - Special Playlist */
/* Channel - Special Playlist - Video Title */
span.ytd-grid-playlist-renderer {
    color: var(--yt-rash-VideoTitlesColor);
}
/* Channel - Special Playlist - Owner Title */
ytd-video-meta-block.grid #byline.ytd-video-meta-block, [meta-block].grid.ytd-video-meta-block #byline.ytd-video-meta-block {
    --yt-endpoint-color: var(--yt-rash-VideoOwnerColorNormal);
    --yt-endpoint-hover-color: var(--yt-rash-VideoOwnerColorHover);
}

/* Channel Featured - Sections */
/* Channel Featured - Title Section */
#title.ytd-vertical-channel-section-renderer {
    color: var(--yt-featured-channel-title-text-color-ALT);
}
/* Channel Featured - Owner Name*/
.title.ytd-mini-channel-renderer {
    color: var(--yt-primary-text-color-ALT);
}

/* Channel Related */
/* Channel Related - Separator */
#contents.ytd-browse-secondary-contents-renderer > .ytd-browse-secondary-contents-renderer:not(:first-child) {
    border-top: 1px solid var(--yt-rash-SeparatorColor);
    padding-top: 24px;
}
/* Channel Related - Title Section */
#title.ytd-vertical-channel-section-renderer {
    color: var(--yt-rash-Channel-FeaturedSectionTitle);
}
/* Channel Related - Owner Name*/
.title.ytd-mini-channel-renderer {
    color: var(--yt-rash-Channel-FeaturedSectionChannelName);
}
    

/* Channel Videos Section */
/* Channel Videos - Uploads Sort */
#label-text.yt-dropdown-menu {
    color: var(--yt-rash-VideoOwnerColorNormal);
}
/* Channel Videos - Uploads Sort Submenu BG */
paper-listbox.yt-dropdown-menu {
    background-color: var(--yt-rash-BGSideSectionColor);
}
paper-listbox.yt-dropdown-menu paper-item.yt-dropdown-menu:hover {
    background-color: #3d3d3d;
}
/* Channel Videos - Uploads Sort Submenu Text */
.item.yt-dropdown-menu {
    color: var(--yt-rash-VideoMetadata);
}

/* Channel Videos - Uploads Sort Submenu Footer */
iron-dropdown#dropdown.style-scope.paper-menu-button{
    background-color: var(--yt-rash-MainThemeColorDimmed);
}


/* Channel Videos - Sort By selector */
#icon-label.yt-dropdown-menu {
    color: var(--yt-rash-VideoOwnerColorNormal);
}




/* Channel Playlist Section */
/* Channel Playlist - Titles */
a#video-title.yt-simple-endpoint.style-scope.ytd-grid-playlist-renderer {
    color: var(--yt-rash-VideoOwnerColorHover);
}
yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string {
    color: var(--yt-rash-VideoTitlesColor);
}

/* Channel Playlist - Show More Button */
#view-all.ytd-grid-renderer {
    --yt-endpoint-color: var(--yt-rash-ButtonExpanderTextColor);
}






/* Channel Community Section */
/* Channel Community - Title Author */
#author-text.yt-simple-endpoint.ytd-backstage-post-renderer {
    color: var(--yt-rash-VP-CommentAuthor);
}

/* Channel Community - Comment */
#content-text.ytd-backstage-post-renderer {
    color: var(--yt-rash-VP-CommentContent);
}
/* Channel Community - Comment Post Background Image */
#content-attachment.ytd-backstage-post-renderer ytd-video-renderer.ytd-backstage-post-renderer {
    padding: 8px;
    background-color: var(--yt-rash-SP-ResultsBadgeBackground);
}

/* Channel Community - Comment Show More Button */
.align-by-text.ytd-backstage-comments-renderer {
    color: var(--yt-rash-ButtonExpanderTextColor);
    font-size: 1.2rem;
    font-weight: 500;
    line-height: 1.8rem;
    height: 27px;
}
/* Channel Community - Comment Show More Button Text */
.align-by-text.ytd-backstage-comments-renderer {
    color: var(--yt-rash-ButtonExpanderTextColor);
    font-size: 1.2rem;
    font-weight: 500;
    line-height: 1.8rem;
}
/* Channel Community - Comment Show More Button - Arrow Fix */
.more-button.ytd-backstage-comments-renderer, .less-button.ytd-backstage-comments-renderer {
    margin-top: -0px;
}


/* Channel Community - Post Comment Video Title */
ytd-video-renderer[is-backstage-video] #video-title.ytd-video-renderer {
    color: var(--yt-rash-VideoTitlesColor);
}



/* Channel Community - Poll */
/* Channel Community - Poll Text */
.text-area.ytd-backstage-poll-renderer {
    color: var(--yt-rash-VideoMetadata);
}


/* Channel About Section */
/* Channel About Sub-Sections Titles */
.subheadline.ytd-channel-about-metadata-renderer {
    color: var(--yt-rash-VideoTitlesColor);
}
/* Channel About Sub-Sections Titles */
#description.ytd-channel-about-metadata-renderer, #bio.ytd-channel-about-metadata-renderer {
    color: var(--yt-rash-VideoDescription);
}
/* Channel About Separator */
#description-container.ytd-channel-about-metadata-renderer, #bio-container.ytd-channel-about-metadata-renderer, #photos-container.ytd-channel-about-metadata-renderer, #details-container.ytd-channel-about-metadata-renderer, #links-container.ytd-channel-about-metadata-renderer {
    border-bottom: 1px solid var(--yt-rash-SeparatorColor);
}
/* Channel About Stats */
#right-column.ytd-channel-about-metadata-renderer > yt-formatted-string.ytd-channel-about-metadata-renderer {
    color: var(--yt-rash-VideoDescription);
}
/* Stats - Separator */
#right-column.ytd-channel-about-metadata-renderer > yt-formatted-string.ytd-channel-about-metadata-renderer {
    border-bottom: 1px solid var(--yt-rash-SeparatorColor);
}
}
