css:
@-moz-document domain("snapcraft.io") {
body,
.p-channel-map-overlay,
.p-featured-snaps,
.d-header {
	background-color: #222 !important;
    color: #d6d6d6 !important;
}
footer,
.container__lightStrip___8bUU7,
.documentation {
	background-color: rgba(255,255,255,.1) !important;
    border: none !important;
}
#main-content {
	background: rgba(255,255,255,.1) !important;
}
.p-logo-links > .p-card {
	background: rgba(255,255,255,.2) !important
}
.p-strip--light {
	background-color: rgba(255,255,255,.1) !important;
}
.p-code-snippet,
.p-code-snippet__action,
.p-form__group input,
.select-kit-collection {
	background-color: #222 !important;
    color: #d6d6d6 !important;
    border: none;
}
.p-code-snippet {
	border: 1px solid rgba(255,255,255,.2) !important;
}
.p-code-snippet__action {
	border-left: 1px solid rgba(255,255,255,.2) !important;
}
.p-code-snippet__input {
	color: #d6d6d6 !important;
}
.b-hero {
	opacity: 0.9;
}
.documentation__nav li a,
.top-row a,
.category-title-link span,
.link-top-line a {
    color: #d6d6d6 !important;
}
pre.highlight {
	background-color: #222 !important;
    border: none !important;
    color: #d6d6d6;
}
.slide-in {
	background-color: #222 !important;
    color: #d6d6d6 !important;
    border: 1px solid #d6d6d6;
}
.select-kit .select-kit-row.is-highlighted {
	background: rgba(255,255,255,.1);
}
.category-breadcrumb #ember801,
#navigation-bar li:nth-of-type(1),
#navigation-bar li:nth-of-type(2) {
	background-color: #363636 !important;
    color: #d6d6d6 !important;
}
select {
	background-color: #464646 !important;
    color: #d6d6d6 !important;
}
.p-carousel .p-carousel__next, .p-carousel .p-carousel__prev {
	background-color: #d6d6d6 !important;
}
.p-view-store-button,
.p-button--neutral {
	background-color: #222222 !important;
    color: #d6d6d6 !important;
    border: 1px solid rgba(255,255,255,.2) !important;
}
.p-tabs__item a {
    color: #d6d6d6 !important;
}
.p-tabs__link:hover:before, .p-tabs__link[aria-selected=true]:before {
	background-color: #111111;
}
.p-card--post {
	background-color: #111111;
}
.p-card--post > .p-card__content {
	border: none;
}
.p-card--post > .p-card__content > p {
    color: #d6d6d6;
}
.p-featured-snaps, .p-logo-list {
	background-color: #111111;
}
}
