css:
@-moz-document domain(youtube.com) {

/* video titles */
#video-title
{
    visibility: hidden !important;
}

/* main page, must hover over individual videos */


#dismissable.style-scope.ytd-grid-video-renderer:hover #video-title
{
    visibility: visible !important;
}

#dismissable.style-scope.ytd-video-renderer:hover #video-title
{
    visibility: visible !important;
}
.ytd-watch-next-secondary-results-renderer.use-ellipsis:hover #video-title
{
    visibility: visible !important;
}
ytd-radio-renderer.style-scope.ytd-item-section-renderer:hover #video-title
{
    visibility: visible !important;
}
ytd-playlist-renderer.style-scope.ytd-item-section-renderer:hover #video-title
{
    visibility: visible !important;
}
ytd-grid-playlist-renderer.style-scope.ytd-grid-renderer:hover #video-title
{
    visibility: visible !important;
}
ytd-grid-playlist-renderer.style-scope.yt-horizontal-list-renderer:hover #video-title
{
    visibility: visible !important;
}
ytd-grid-radio-renderer:hover #video-title
{
    visibility: visible !important;
}
ytd-playlist-panel-video-renderer:hover #video-title
{
    visibility: visible !important;
}

a.yt-simple-endpoint.style-scope.ytd-playlist-video-renderer:hover #video-title
{
    visibility: visible !important;
}
/* if you would like to display the entire section when hovered over, use this:

#dismissable:hover #video-title
{
    visibility: visible !important;
}

*/

/* video page sidebar titles */

#dismissable.style-scope.ytd-compact-video-renderer:hover #video-title
{
    visibility: visible !important;
}


/* comments */

#content-text
{
    visibility: hidden !important;
}
span.more-button.style-scope.ytd-comment-renderer
{
    visibility: hidden !important;
}
span.less-button.style-scope.ytd-comment-renderer
{
    visibility: hidden !important;
}
#body:hover #content-text
{
    visibility: visible !important;
}


#body:hover span.more-button.style-scope.ytd-comment-renderer
{
    visibility: visible !important;
}
#body:hover span.less-button.style-scope.ytd-comment-renderer
{
    visibility: visible !important;
}
div.more-button.style-scope.ytd-comment-replies-renderer
{
    visibility: visible !important;
}
}
