css:
@-moz-document url-prefix("https://serebii.net/"), url-prefix("http://serebii.net/"), url-prefix("https://www.serebii.net/"), url-prefix("http://www.serebii.net/") {
html > body {
	background:#333;
	background: linear-gradient(to right, rgba(0,0,0,.7) 5%, rgba(16,13,13,.97) 22.5%, rgba(16,13,13,.97) 77.5%, rgba(0,0,0,.7) 95%) , url("data:image/svg+xml; utf8, <svg xmlns='http://www.w3.org/2000/svg' height='350' width='180'> <polygon points='145,150 108,113 104,117 76,117 58,99 58,71 62,67 25,30 5,50 5,120 55,170 125,170 145,150' fill='grey' /><polygon points='72,57 76,53 104,53 122,71 122,99 118,103 155,140 175,120 175,50 125,0 55,0 35,20 72,57' fill='darkred' /><polygon points='97,103 108,92 108,78 97,67 83,67 72,78 72,92 83,103 97,103' fill='grey' /> <polygon points='148,279 148,251 152,247 115,210 95,230 95,300 145,350 180,350 180,297 166,297 148,279' fill='grey' /> <polygon points='145,180 125,200 162,237 166,233 180,233 180,180 145,180' fill='darkred' /> <polygon points='162,258 162,272 173,283 180,283 180,247 173,247 162,258' fill='grey' /> <polygon points='55,330 18,293 14,297 0,297 0,350 35,350 55,330' fill='grey' /> <polygon points='32,251 32,279 28,283 65,320 85,300 85,230 35,180 0,180 0,233 14,233 32,251' fill='darkred' /> <polygon points='18,272 18,258 7,247 0,247 0,283 7,283 18,272' fill='grey' /> </svg>") center/54px fixed repeat , #030303;
}

@media screen and (max-width: 1280px) {
html > body {
	background:#333;
	background: linear-gradient(rgba(16,13,13,.9) 25%, rgba(16,13,13,.9) 75%) , url("data:image/svg+xml; utf8, <svg xmlns='http://www.w3.org/2000/svg' height='350' width='180'> <polygon points='145,150 108,113 104,117 76,117 58,99 58,71 62,67 25,30 5,50 5,120 55,170 125,170 145,150' fill='grey' /><polygon points='72,57 76,53 104,53 122,71 122,99 118,103 155,140 175,120 175,50 125,0 55,0 35,20 72,57' fill='darkred' /><polygon points='97,103 108,92 108,78 97,67 83,67 72,78 72,92 83,103 97,103' fill='grey' /> <polygon points='148,279 148,251 152,247 115,210 95,230 95,300 145,350 180,350 180,297 166,297 148,279' fill='grey' /> <polygon points='145,180 125,200 162,237 166,233 180,233 180,180 145,180' fill='darkred' /> <polygon points='162,258 162,272 173,283 180,283 180,247 173,247 162,258' fill='grey' /> <polygon points='55,330 18,293 14,297 0,297 0,350 35,350 55,330' fill='grey' /> <polygon points='32,251 32,279 28,283 65,320 85,300 85,230 35,180 0,180 0,233 14,233 32,251' fill='darkred' /> <polygon points='18,272 18,258 7,247 0,247 0,283 7,283 18,272' fill='grey' /> </svg>") center/108px fixed repeat , #030303;
}

body > table:nth-child(3) > tbody > tr:not(:first-child) > td:not(:nth-child(2)) {
display:none;
}	
	
}

body[onselectstart="return false"] {
	background-color:transparent !important;
}
body > center > table * {
	background:transparent !important;
	color:white;
	font-size:20px;
	border:0;
}
body > center td #menu b {
	display:none;
}
body > center td {
	border:0 !important;
	padding:0;
	Vertical-align:middle;
}
body > table:nth-child(2) {
	height:40px;
	color:#a6ffc8 !important;
	background-size:54px 105px;
	text-align:center;
	background:none !important;
}
body > table:nth-child(2) {
}
body > table:nth-child(2) td::before {
	CONTENT:"Serebii.net";
	color:#c22;
	font-family:"Impact", Charcoal, sans-serif;
	font-style: italic;
	font-size:50px;
}
body > table:nth-child(2) td {
	height:40px;
	border:none;
	background:none !important;
}
body > table:nth-child(2) a {
	background:red;
	height:300px;
	width:500px;
}
body > table:first-child, body > table:nth-child(2) img {
	display:none;
}
#menu b {
	display: block;
	width:100%;
	text-align:center;
}
#menu a {
	display:none;
}
#menu > img[height='27'], #menu > a > img[height='27'] {
	filter:hue-rotate(65deg) contrast(250%) saturate(0%) brightness(120%) invert(100%);
	border-radius:5px;
	border-bottom:5px #000 solid;
	border-top:5px #000 solid;
	border-left:0;
	border-right:0;
	margin: 4px 0;
}
tr:first-child #menu {
	text-align:center;
	background:transparent;
}
tr:first-child #menu:first-child a {
	border-top:#aa2323 10px solid;
	border-bottom:#b8b8b8 10px solid;
	border-radius:10px;
	text-align:center;
	display:inline-block;
	background:#222;
	padding:4px 6px;
	font-size:20px;
	margin:2px 4px;
}
tr:first-child #menu {
	font-size:0;
}
body > table > tbody > tr:first-child > td > table > tbody > tr > td:last-child {
	position:absolute;
	margin:0;
	top:0;
	right:0;
}
#menu {
	background:rgba(255, 0, 0, .2);
	color:#fcfcdd;
	padding:10px 6px;
	margin:0 4px;
	border-radius:14px;
	min-height:18px;
	border:0;
}
#menu a {
	border-radius:3px;
	color:#d6d6d6;
	display:block;
	margin:2px 0;
	max-width:200px;
}
#menu a:hover {
	text-decoration:none;
	color:#f44;
}
#menu br {
	display:none;
}
#menu tr, #menu td {
	border:0;
}
body > table > tbody > tr > td {
	background:none !important;
	border:0 !important;
}
.post .pics {
	margin:0 12px;
	border:2px #340808 solid;
}
div.post + div {
	display:none;
}
div.post {
	background-color:rgba(15, 15, 15, .7) !important;
	border-radius:20px;
	display:block;
	float:left;
	margin:4px 0 10px 0;
}
div.post ~ div {
	float:left;
}
.post .info {
	border-bottom:#f88 2px dotted;
}
.post .subcat {
	display:block;
}
img[src*="/anime/pokemon/251.gif"] {
	display: none;
}
body > table:nth-child(3) {
	padding:0;
	border:0 !important;
	border-spacing: 0 90px !important;
	border-collapse: collapse !important;
}
body > table:nth-child(3) > tbody > tr > td {
	padding:0;
}
body > table:nth-child(3) > tbody > tr > td:nth-child(2) {
	border:14px solid transparent !important;
	border-radius:18px;
	margin:12px 3px !important;
}
body > table:nth-child(3) > tbody > tr > td:nth-child(2) > font {
	text-align:justify;
	font-size:16px;
	color:#ddd;
}
body > table:nth-child(3) > tbody > tr > td:nth-child(2) > font > div:not(:first-child):not([align="center"]) {
	background:rgba(255, 255, 255, .2) !important;
	padding:10px;
	display:block;
	border-radius:20px;
	width:auto !important;
	max-width:1024px;
	margin:4px auto 2px auto;
}
body > table:nth-child(3) > tbody > tr > td:nth-child(2) > font >p:not([align="center"]){
	max-width:1024px;
	margin:12px auto 8px auto;
	font-size:12px;
	color:#fff;
}
body > center > table {
}
p[align="center"] > table tr td form * {
	width:100%;
}
#menu input, form > input {
	background:#ddd !important;
	border:0;
	border-radius:4px;
	margin: 0 4px;
}


form select{
		background:#ddd !important;
	border:0;
	border-radius:4px;
	margin: 0 4px;
width:80%;
	max-width:200px;
	min-width:80px;
}
body > center td {
	background:red;
	Color:white !important;
	border:4px solid #ff8;
	padding:18px;
	text-align:center;
}
div.post * {
	text-indent:0 !important;
	font-size:105%;
}
div.post h3, div.post h1, div.post h2 {
	color:#d44;
	font-style:bold;
}
div.post p.title {
	color:#c66;
	margin:8px 0 2px 0;
}
div.post p a {
	font-size:inherit;
	text-decoration:none;
	font-style:italic;
	color:#dba;
}

div.pics {
	border-color:transparent !important;
}
td.tooltabhead, td.fooevo, td.fooleft, td.footwo, td.foo, td.footop {
	background:#c22;
	border-radius:12px 12px 0 0;
	margin:0;
	border:0;
}
td.fooinfo, td.cen, td.footype, th.attheader, td.foohin, td.fooben {
	border-radius: 3px;
	margin:0;
	border:0;
}
td.tooltabcon {
	background:none;
	border-radius:0 0 12px 12px;
	border:2px #fff solid;
	border:0;
}
td.footype img {
	transform:rotate(-90deg);
	border:0;
}
td.curr, td.foodet {
	background:#c22;
	border-radius:12px;
	border:0;
}
.alterdex {
	background:#232323;
	border:0;
	padding:2px 0;
	display:block;
	height:auto;
	border-radius:8px;
	text-align:center;
	border-top:8px solid #c22;
	border-bottom:8px solid #ddd;
}
.art {
	background:none;
	border:none;
}
}
