css:
@-moz-document domain("www.cubetv.sg") {
html, .room-page-wrap {
    background: #212121;
}

.chat-content {
    background: #2d2d2d;
    border: 1px solid #3d3c4c;
}

.chat-content .chat-scroll-box .type1 .name {
    color: #adadad;
    font-weight: bold;
    font-size: smaller;
}

.chat-content .chat-scroll-box .type1 .massage {
    color: #d8d8d8;
    font-size: smaller;
}

.room-page-left-top {
    background: #2d2d2d;
    border: 1px solid #3d3c4c;
}

.room-page-left-top .box-content .game-infor .game-name {
    color: #bfbfbf;
}

.room-page-left-top .box-content .follow-btn {
    background: #44446b;
}

.topnav {
    background: #333 !important;
    border-bottom: none !important;
}

.topnav .logo-box img {
    filter: invert(100%) saturate(0%) !important;
}

.right-fixed-nav {
    background: #333 !important;
    border: none !important;
}

.chat-content .send-chat-box .chat-textarea {
    border: none;
    color: #e4e4e4;
}

.chat-content .send-chat-box .area {
    border: 1px solid #666;
}

.chat-content .send-chat-box .area:hover {
    border: 1px solid #999;
}

.chat-content .send-chat-box .send-btn {
    background: #44446b;
}

.live-lists .recom-infos {
    background: #333 !important;
}
}
