css:
@-moz-document url-prefix("https://horriblesubs.info/") {
body {
	background-color: #282828;
	color: #ffffff;
}
.navbar-brand {
	background-image: none!important;
	color: #F5F7FA !important;
}
.navbar-brand:hover {
	color: /*[[accentcolor]]*/ !important;
}
.navbar.navbar-default {
	background-color: #080808;
	box-shadow: 1px 1px 4px #181818;
}
.navbar-default .navbar-nav > li > a {
	color: #ffffff;
}
.navbar-nav > li:hover > .caret {
    color: /*[[accentcolor]]*/;
}
.dropdown-menu > li > a {
    color: #ffffff;
    padding: 0;
}
.dropdown-menu {
    background-color: #383838;
}
.navbar-default .navbar-nav .dropdown-menu > li:hover, .navbar-default .navbar-nav .dropdown-menu > li:focus, .dropdown-menu > .active {
    background-color: #484848;
}
.dropdown-menu>.active>a, .dropdown-menu>.active>a:focus, .dropdown-menu>.active>a:hover, .dropdown-menu>.active>.caret, .dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover, .dropdown-menu>li:hover>a, .dropdown-menu>li:hover>.caret {
    color: /*[[accentcolor]]*/;
}
a {
    color: /*[[accentcolor]]*/;
}
.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > .caret, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus, .navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li:hover > a, .navbar-default .navbar-nav > li > a:focus, .navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:hover, .navbar-default .navbar-nav > .open > a:focus {
	color: /*[[accentcolor]]*/;
}
.alert-success {
    color: /*[[accentcolor]]*/;
    background-color: #282828;
    border-color: /*[[accentcolor]]*/;
}
.post-inner-content {
	background-color: #383838;
	box-shadow: 1px 1px 4px #181818;
}
.series-image img {
	box-shadow: 1px 1px 4px #181818;
}
.featured > .row > div .featured-item .featured-image {
	box-shadow: 1px 1px 4px #181818;
}
.well {
	background-color: #383838;
	box-shadow: 1px 1px 4px #181818;
}
.latest-releases {
	background: #383838
}
.latest-releases .active {
	background: #484848
}
.latest-releases .active a:hover {
	background: #585858!important;
	color: #ffffff !important;
}
.latest-releases a:hover,
.latest-show-more a:hover {
	background: #484848!important;
	color: #ffffff !important;
}
.latest-releases-res .badge,
.rls-label .badge {
	background-color: #282828a8;
}
.rls-info-container a:focus, .show-more a:focus, .rls-label:hover, .entry-title {
    color: /*[[accentcolor]]*/;
}
.latest-releases-res:hover .badge:hover,
.rls-label:hover .badge:hover {
	background-color: /*[[accentcolor]]*/a8;
}
.latest-releases a:hover .badge,
.rls-label:hover .badge {
	background-color: #282828a8;
}
.la-badge {
	background-color: #585858;
}
.table-hover > tbody > tr:hover,
.schedule-table > tbody > tr:hover {
	background-color: #585858 !important;
}
.latest-releases a,
.latest-show-more a {
	color: /*[[accentcolor]]*/;
}
#secondary .widget a {
	color: #ffffff;
}
a:hover,
a:focus {
	color: #ffffff;
}
#secondary .widget a:hover {
	color: /*[[accentcolor]]*/;
}
#secondary .widget a:hover {
	background: transparent;
}
.post-navigation a,
.page-numbers,
.pagination .prev,
.pagination .next {
	border: 1px solid #484848;
	background-color: #383838;
}
.post-navigation a:hover,
.pagination .prev:hover .pagination .next:hover {
	color: #FFF;
	background: #484848;
	border: 1px solid #585858;
}
.entry-meta a {
	color: #ffffff;
}
a .li:hover {
	background-color: #00ff00;
}
.widget ul li {
	border-bottom: 1px solid transparent;
	border-bottom-color: transparent;
}
.table > tbody > tr > td {
	border-bottom: 1px solid transparent;
}
.table-hover > tbody > tr:hover {
	background-color: #484848!important;
}
.form-control {
	color: #ffffff;
	background-color: #282828;
	border-color: #181818
}
.hs-shows {
	border-top: 1px solid transparent;
}
table,
tr,
td {
	border: 1px solid transparent;
}
.latest-releases ul li {
	border-bottom: 1px solid transparent;
}
#disqus_thread {
	background: #383838;
	box-shadow: 1px 1px 4px #181818;
}
.ind-show:hover {
	background-color: #484848!important;
}
.rls-label:hover {
	background: #484848 !important;
}
.rls-info-container {
	border-bottom: 1px solid transparent;
}
.schedule-today {
	background-color: #484848;
}
h4.hrf-title {
	color: #ffffff;
	background-color: #484848;
}
.hrf-content {
    color: #ffffff;
}
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
    color: /*[[accentcolor]]*/
}
.hrf-title.close-faq span {
	background-color: #383838;
}
}

@-moz-document url("https://free.timeanddate.com/clock/i4vybhfo/n137/fn17/th1/ta1") {
#c1 {
	background-color: #383838;
}
#t1c,
#t1 {
	color: #ffffff;
}
}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=horriblesubs") {
body {
	color: #ffffff;
}
.textarea {
    color: #ffffff;
	background-color: #2a2e2e;
}
.highlighted-post .highlighted .post-content {
	background-color: #383838;
}
.recommend-icon {
    color: /*[[accentcolor]]*/;
}
.comment__footer .vote-down,
.comment__footer a,
.comment__header .vote-down,
.comment__header a {
	color: #ffffff;
}
.publisher-anchor-color a {
    color: /*[[accentcolor]]*/ !important;
}
.active .publisher-nav-color::after {
    background: /*[[accentcolor]]*/ !important;
}
.community-name:hover {
	color: /*[[accentcolor]]*/;
}
.nav-tab > a {
	color: #ffffff;
}
a:hover {
	color: #ffffff !important;
}
.icon-cog:hover,
.icon-export:hover,
.label:hover {
	color: /*[[accentcolor]]*/;
}
.nav-tab > a:hover {
	color: /*[[accentcolor]]*/;
}
.unread .notification-icon {
    color: /*[[accentcolor]]*/;
}
.dropdown-toggle {
	color: #ffffff!important;
}
.dropdown-toggle:hover {
	color: /*[[accentcolor]]*/!important;
}
.highlighted > .post-content {
	padding: 12px;
	border-radius: 0px;
}
.tab-conversation.active > a {
	color: #ffffff!important;
}
.thread-likes .label-count {
	color: /*[[accentcolor]]*/;
}
.alert.alert--realtime,
.alert.info,
.alert.success,
.dark .alert.alert--realtime,
.dark .alert.info,
.dark .alert.success {
	background: /*[[accentcolor]]*/;
}
.post-content.new .indicator,
.post-content.target .indicator {
	background: /*[[accentcolor]]*/;
}
.realtime-replies {
	color: #ffffff;
}
.realtime-button .indicator {
	background: /*[[accentcolor]]*/;
}
.comment__footer .vote-down:hover,
.comment__footer a:hover,
.comment__header .vote-down:hover,
.comment__header a:hover {
	color: /*[[accentcolor]]*/;
}
.post-content .post-menu > li a {
	color: /*[[accentcolor]]*/!important;
}
h5,
h6 {
	color: /*[[accentcolor]]*/;
}
.help-icon {
	border: 2px solid /*[[accentcolor]]*/;
}
.help-icon:before {
	color: /*[[accentcolor]]*/;
}
.caret {
	border-top: 4px solid /*[[accentcolor]]*/;
}
.btn:not(:disabled).active,
.btn:not(:disabled):active {
	background: /*[[accentcolor]]*/;
}
.email a:hover,
.install a:hover,
.privacy a:hover {
	color: /*[[accentcolor]]*/;
}
.placeholder {
	color: #707070
}
.postbox section label {
	color: #ffffff;
}
a {
	color: /*[[accentcolor]]*/;
}
a:hover {
	color: /*[[accentcolor]]*/;
}
.disqus-footer__link {
	color: #ffffff;
}
.textarea-wrapper {
	background: #282828;
	border: 2px solid #000000;
}
.post-actions {
	background: #282828;
	border-top: solid 2px #000000;
}
.input--text {
	background: #282828;
	border: 2px solid #000000;
}
.post-content .post-body spoiler:focus,
.post-content .post-body spoiler:hover {
	background: #383838;
	color: inherit
}
}
