css:
@-moz-document url-prefix("https://horriblesubs.info") {
body {
    background-color: #111;
    color: #DDD;
}
::selection {
    color: #FFF;
    background-color: #AAA;
}
.navbar.navbar-default {
    background-color: #222;
    box-shadow: 0 0 0 #000;
}
.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > .caret, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus, .navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li:hover > a, .navbar-default .navbar-nav > li > a:focus, .navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:hover, .navbar-default .navbar-nav > .open > a:focus {
    color: #FFF;
    background-color: transparent;
}
.navbar-nav > li {
    color: #333;
}
.navbar-nav > li:hover > .caret {
    color: #F72;
}
.navbar-default .navbar-nav > li > a {
    color: #AAA;
}
.navbar-default .navbar-nav .dropdown-menu > li:hover, .navbar-default .navbar-nav .dropdown-menu > li:focus, .dropdown-menu > .active {
    color: #FFF !important;
    background-color: #444 !important
}
.dropdown-menu {
    background-color: #222 !important;
}
.dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover, .dropdown-menu > li:hover > a, .dropdown-menu > li:hover > .caret {
    background: none !important;
    color: #FFF !important;
}
.post-inner-content {
    background-color: #111;
    color: #DDD;
    box-shadow: 0 0 0 #000
}
.well {
    background-color: #111;
    box-shadow: 0 0 0 #000;
    overflow: visible;
}
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
    color: #DDD
}
.widget ul li {
    border-bottom: 0 solid #111
}
.latest-releases .active {
    background: #111;
}
.la-badge {
    background: none;
    color: #555
}
a {
    color: #DDD;
    text-decoration: none;
    -webkit-transition: all 0.0s;
    -moz-transition: all 0.0s;
    -o-transition: all 0.0s;
    transition: all 0.0s;
}
a:hover, a:focus {
    color: #FFF;
}
#secondary .widget a {
    color: #DDD
}
#secondary .widget > h3 {
    color: #DDD;
    margin-bottom: 25px;
}
#secondary .widget a:hover {
    background: none;
    color: #FFF
}
.entry-title a {
    color: #777;
}
.entry-title a:hover {
    color: #FFF;
}
.form-control:focus {
    border-color: #A51;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(102, 175, 233, .6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(102, 175, 233, .6);
}
.form-control {
    color: #FA2;
    background-color: #222;
    border: 1px solid #333;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075);
}
.table>tbody>tr>td, .schedule-table>tbody>tr {
    border-bottom: 1px solid #222;
    border-top: none;
}
.table-hover>tbody>tr:hover, .schedule-table>tbody>tr:hover {
    background-color: #222 !important;
}
aside {
    background-color: #111;
    color: #DDD
}
#disqus_thread {
    background: #222;
    box-shadow: 0 0 0 #000;
}
.dl-type {
    margin-right: 5px;
    margin-left: 5px;
    display: inline-block;
    color: #444;
}
.rls-info-container a:focus, .show-more a:focus, .rls-label:hover, .entry-title {
    color: #FFF;
}
.rls-label:hover {
    background: #111 !important;
}
.rls-info-container {
    border-bottom: 0 solid #000;
}
.latest-releases .active a:hover {
    background: #222 !important;
    color: #FFF
}
.latest-releases a, .latest-show-more a {
    color: #DDD;
}
.alphabet-title {
    border-bottom: 1px solid #BBB;
}
.ind-show:hover {
    background: #111 !important;
}
.latest-releases a:hover {
    background: #222 !important;
    color: #FFF
}
.latest-show-more a:hover {
    background: #111 !important;
    color: #FFF;
}
.latest-releases-res .badge, .rls-label .badge {
    background: none;
    color: #666
}
.latest-releases a:hover .badge, .rls-label:hover .badge {
    background: none;
    color: #777;
    -webkit-transition: all 0.0s;
    -moz-transition: all 0.0s;
    -o-transition: all 0.0s;
    transition: all 0.0s;
}
.rls-label:hover .badge {
    background: none;
    color: #777;
}
.latest-releases ul li {
    border-bottom: 1px solid #222;
}
.series-image img {
    box-shadow: 0 0 8px #CCC;
}
.alert-success {
    color: #D71;
    background-color: #210;
    border-color: #631;
}
.navbar-brand {
    background-image: linear-gradient(to bottom right, #FFF, #FFF, #222)!important;
}
.schedule-today {
    background-color: #111;
}
.table-hover>tbody>tr:hover {
    background-color: #111 !important;
}
.hrf-title {
    color: #DDD!important;
    background: #222!important;
}
.hrf-content {
    color: #DDD!important;
}
}

@-moz-document url("https://free.timeanddate.com/clock/i4vybhfo/n137/fn17/th1/ta1") {
* {
    background: #111 !important;
    color: #DDD !important;
}
}

@-moz-document regexp("(https|http)://.*disqus.com/embed/comments/.*(horriblesubs.info).*") {
* {
    background-color: #222 !important;
    color: #DDD !important;
}
}
