css:
@-moz-document domain("paperswithcode.com") {

body {
  background-color: #222;
  color: #ccc;
}

b, strong {
  color: #aaa;
}

.navbar-collapse, .order-1, .order-3 {
  background-color: #222 !important;
}

.bg-white {
  background-color: #222 !important;
}

/* Search */
.header .global-search {
  background-color: #222;
  border-color: #333;
  color: #ccc;
}

.header .global-search:focus {
  color: #ccc;
}

.header .global-search:hover {
  color: #ddd;
}

.ui-widget-content {
  background-color: #222;
  color: #aaa;
}

.ui-autocomplete li.ui-autocomplete-category {
  border-bottom-color: #333;
}

.ui-widget.ui-widget-content {
  border-color: #333;
}

.navbar-light .navbar-nav .nav-link-right a {
  color: #ccc;
}

.navbar-light .navbar-nav .nav-link-right a:hover {
  color: #ddd;
}

.sign-up .btn-primary {
  background-image: linear-gradient( to bottom, #01494b 0%, #006857 1%, #01494b 100% ); /* #02aab0 #00cdac */
}

.btn-primary {
  color: #ddd;
}

/* 'Trending Research' */

h1 {
  color: #aaa;
}

/* 'Latest', 'Greatest' */
.list-button {
  color: #ccc;
}

/* 'Subscribe' */
.list-button-subscribe a {
  background-color: #0c494a; /* #21cbce; */
  color: #ccc !important;
}

/*** Row item ***/

.item {
  border-color: #333;
}

.item h1 a {
  color: #aaa !important;
}

/* Thumbnail */

.item-image {
  filter: invert(86.5%);
}

.item-image:hover {
  filter: invert(0%);
  transition-delay: 1s;
}

/* Stars */
.item-interact .badge-secondary {
  background-color: #282828;
  border-color: #333;
  color: #999;
}

/* 'Paper' */
.item-interact .badge-light, .item-interact .badge-light:focus {
  background-color: #0a432f; /* #1ab37c; */
  border-color: #333;
  color: #ccc;
}

/* 'Code' */
.item-interact .badge-dark, .item-interact .badge-dark:focus {
  background-color: #15488b; /* #1f6bcd; */
  border-color: #333;
  color: #ccc;
}

.item-content .sota p {
  color: #999;
}

/* Tags */

.item-content .badge-primary {
  background-color: #282828;
  border-color: #333;
  color: #aaa;
}

.item-content .badge-primary:hover {
  background-color: #333;
}


/* Footer */
.footer {
  border-top-color: #444;
}



/*************** paperswithcode.com/trends ***************/

.highcharts-background {
  fill: #222 !important;
}

.custom-select {
  background-color: #282828;
  border-color: #333;
  color: #ccc;
}

.input-group-addon {
  color: #ccc;
}

.trends-page .input-group-addon {
  background-color: #282828;
  border-top-color: #333;
  border-bottom-color: #333;
}

.form-control {
  background-color: #282828;
  border-color: #333;
  color: #ccc;
}

/* Calendar */
.datepicker.dropdown-menu {
  background-color: #282828;
  color: #ccc;
}

.datepicker table tr td.range, .datepicker table tr td.range:hover, .datepicker table tr td.range.disabled, .datepicker table tr td.range.disabled:hover {
  background-color: #333;
}

/* Legend */

.highcharts-legend-box {
  fill: #282828;
}

.highcharts-legend-item > text {
  fill: #aaa !important;
}

.highcharts-grid-line {
  stroke: #333 !important;
}

.highcharts-axis-labels > text {
  fill: #ccc !important;
}

.highcharts-axis-title {
  fill: #aaa !important;
}

/* 'How the Data is Collected' */
.text-muted {
  color: #999 !important;
}

/*************** paperswithcode.com/sota ***************/

.browse-sota-title h1 {
  color: #aaa;
}

.task-group-title a, .task-group-title a:visited {
  color: #aaa;
}

/* Row item */
.card {
  background-color: #282828;
}

/* ' See all N tasks' */
.sota-all-tasks a:visited {
  color: #168a8c; /* #21cbce */
}

.sota-all-tasks a {
  color: #168a8c; /* #21cbce */
}

/*************** paperswithcode.com/task/<T> ***************/
/*** e.g. paperswithcode.com/task/semantic-segmentation ***/

/* Img */
img[src="/media/tasks/default.gif"] {
  filter: invert(86.5%);
}

.card-background-image[style="background-image: url('/media/tasks/default.gif');"] {
  filter: invert(84%);
}

.item-content img {
  filter: invert(20%);
}

.sota-table-preview td {
  border-color: #333;
}

.sota-table-preview .table-striped > tbody > tr:nth-child(2n+1) > th {
  background-color: #282828 !important;
  border-bottom-color: #333;
  color: #aaa;
}

.sota-table-preview .table-striped > tbody > tr:nth-child(2n+1) > td {
  background-color: #282828 !important;
}

/* 'Add a Result' */
.badge-primary {
  background-color: #282828;
  border-color: #333;
  color: #aaa;
}

.badge-primary:hover {
  background-color: #333;
}

/* 'Add a new evaluation result row' */

.modal-content {
  background-color: #282828;
}

.modal-header {
  border-bottom-color: #333;
}

.modal-content .select2-container--default .select2-selection--single {
  background-color: #282828;
  border-color: #333;
}

.modal-content input[type="text"], .modal-content input[type="number"] {
  background-color: #282828;
  border-color: #333;
  color: #aaa;
}

.select2-dropdown {
  background-color: #282828;
  border-color: #333;
}

.select2-container--default .select2-results__option--highlighted[aria-selected] {
  background-color: #282828 !important;
}

.select2-container--default .select2-search--dropdown .select2-search__field {
  border-color: #333;
}

.select2-search select2-search--dropdown {
  background-color: #282828 !important;
}

.modal-footer {
  border-top-color: #333;
}

.modal-footer .btn-primary {
  background-color: #168a8c; /* #21cbce; */
}

/* thumbnail */
.sota-thumb {
  filter: invert(86.5%);
}

/* 'Best method' */
.black-links a {
  color: #aaa !important;
}

/* 'See all' */
.sota-table-preview .btn-primary {
  background-color: #0c494a; /* #21cbce; */
  color: #ccc !important;
}

/*** 'Greatest papers with code' ***/

/* '#N best model' */
.item-content .sota img {
  filter: invert(86.5%);
}

/*************** paperswithcode.com/sota/<S> ***************/
/*** e.g. paperswithcode.com/sota/real-time-object-detection-on-coco ***/


/* Plots */

.highcharts-text-outline {
  stroke: #282828 !important;
}

.highcharts-label.highcharts-data-label.highcharts-data-label-color-undefined tspan {
  fill: #ccc !important;
}

.highcharts-axis-line {
  stroke: #666 !important;
}

.highcharts-tick {
  stroke: #666 !important;
}

.highcharts-point[fill="#ddd"] {
  fill: #666 !important;
}

.highcharts-button-symbol {
  stroke: #666 !important;
}

.highcharts-button-box {
  fill: #282828 !important;
}

.highcharts-menu {
  background-color: #282828 !important;
}

.highcharts-menu-item {
  color: #aaa !important;
}

/* 'Edit' */
.badge-edit-sota {
  background-color: #0c494a; /* #21cbce; */
  color: #ccc;
}

/*** Table ***/

.view-sota-table table {
  border-color: #333;
}

.view-sota-table th {
  background-color: #282828 !important;
  border-bottom-color: #333;
  color: #aaa;
}

.view-sota-table td .method-name a {
  color: #aaa !important;
}

.view-sota-table td {
  border-color: #333;
}

.view-sota-table .table-striped > tbody > tr:nth-child(2n+1) > td {
  background-color: #222;
}

.view-sota-table .table-striped > tbody > tr:nth-child(2n) > td {
  background-color: #282828;
}

/*************** paperswithcode.com/paper/<P> ***************/
/*** e.g. paperswithcode.com/paper/pvn3d-a-deep-point-wise-3d-keypoints-voting ***/

/** 'PDF', 'Abstract' **/
.paper-abstract .badge-light, .paper-abstract .badge-light:focus {
  background-image: linear-gradient( to bottom, #01494b 0%, #006857 1%, #01494b 100% ); /* #02aab0 #00cdac */
  border-color: #333;
  color: #ccc;
}

/** 'Code' **/

.paper-implementations img {
  filter: invert(20%);
}

/* 'Quickstart in Colab' */

.paper-implementations .badge-primary {
  background-color: #282828;
  border-color: #333;
}

.code-table td .badge-primary:hover {
  background-color: #333;
  color: #ddd;
}

/*** 'Results from the Paper' ***/

.paper-evaluation-badge {
  background-color: #282828;
}

/* '#k best model for ...' */
.paper-evaluation-badge img {
  filter: invert(86.5%);
}

.sota-table th {
  background-color: #282828 !important;
  border-bottom-color: #333;
  color: #aaa;
}

.sota-table td {
  border-color: #333;
}

.sota-table .table-striped > tbody > tr:nth-child(2n) > td {
  background-color: #282828 !important;
}

.sota-table .table-striped > tbody > tr:nth-child(2n+1) > td {
  background-color: #282828 !important;
}

/* 'Compare' */
.sota-table .btn-primary {
  background-color: #0c494a; /* #21cbce; */
  color: #ccc;
}

/*************** paperswithcode.com/paper/<P>/review/?hl=N ***************/
/*** e.g. paperswithcode.com/paper/efficientdet-scalable-and-efficient-object/review/?hl=10602 ***/


.extracted-table .from-paper {
  background-color: #282828;
}

.extracted-table .paper-extracts {
  background-color: #282828;
  border-color: #333;
}

/* 'Results in Papers With Code' */
.pwc-tab {
  background-color: #005d52; /* #00c2ae; */
  color: #ccc;
}

/* 'Unmatched results' */
.manual-review .review-blob .blob-left-border-gray {
  background-color: #333;
}

.blob-input-gray button {
  background-color: #282828;
  border-color: #333;
  color: #ccc;
}

/* 'PDF', 'Paper Record' */
.extracted-table .badge-light, .extracted-table .badge-light:focus {
  background-image: linear-gradient( to bottom, #01494b 0%, #006857 1%, #01494b 100% ); /* #02aab0 #00cdac */
  border-color: #333;
  color: #ccc;
}

/** Tables **/
.tableWrapper table tr td.border-t {
  border-top-color: #333;
}

.tableWrapper table tr td.border-r {
  border-right-color: #333;
}

.tableWrapper table tr td.border-b {
  border-bottom-color: #333;
}

.tableWrapper table tr td.border-tt {
  border-top-color: #333;
}

.tableWrapper table tr td.border-bb {
  border-bottom-color: #333;
}

.tableWrapper table tr td.border-rr {
  border-right-color: #333;
}

.tableWrapper span.text-bold {
  color: #5c74cb;
}

.arxiv-table.tableWrapper b, .arxiv-table.tableWrapper strong {
  font-weight: normal;
}

.manual-review .review-blob {
  background-color: #282828;
  border-color: #333;
}

.manual-review .review-blob .blob-left-border-green {
  background-color: #054b00; /* #6eff63; */
}

.manual-review .review-blob .blob-left-border-blue {
  background-color: #1d2b52; /* #5c77c5; */
}

.manual-review .review-blob .blob-left-border-yellow {
  background-color: #554e00; /* #fff254; */
}

.blob-input-references button {
  background-color: #243565;
  border-color: #1c2959;
  color: #ccc;
}

.manual-review .review-blob .blob-block input {
  background-color: #282828;
  border-color: #333;
  color: #ccc;
}

.arxiv-table .highlight-green {
  background-color: #054b00;
}

.arxiv-table .highlight-blue {
  background-color: #001459;
}

.arxiv-table .highlight-yellow {
  background-color: #554e00;
}

.blob-input {
  background-color: #282828;
  color: #ccc;
}

@keyframes pulsegreen {
  0% {
      background-color: #333;
  }
  100% {
      background-color: #054b00;
  }
}

@keyframes pulse {
  0% {
    background-color: #333;
  }
  100% {
      background-color: #005252;
  }
}

/* Pop-up */
.arxiv-add-wrap {
  background-color: #282828;
  border-color: #024e00 !important;
}

.add-annotation-button.add-annotation-button-00 {
  background-color: #006a17 !important; /* rgb(130, 255, 157); */
  border-color: #006a17 !important; /* rgb(130, 255, 157); */
}

.add-annotation-button.add-annotation-button-05 {
  background-color: #002708 !important; /* rgb(0, 242, 53); */
  border-color: #002708 !important; /* rgb(0, 242, 53); */
}

/* 'Parse references' */

.parse-references-modal .modal-footer button:disabled, button[disabled] {
  background-color: #282828 !important;
  border-color: #333 !important;
}

.parse-references button {
  color: #ccc;
}


.fixed-footer {
  background-color: #282828;
  border-top-color: #333;
}

.fixed-footer .help-button {
  background-color: #007888; /* #00BCD4; */
}

.fixed-footer .save-review-button {
  background-color: #00791a; /* #00C52B; */
}

/*************** paperswithcode.com/area/<A>/<SA> ***************/
/*** e.g. paperswithcode.com/area/computer-vision/pose-estimation ***/

.card-img-top[style="background-image: url('/media/tasks/default.gif');"] {
  filter: invert(86.5%);
}

/*************** paperswithcode.com/about ***************/

h2 {
  color: #aaa;
}

}
