css:
@-moz-document domain('dododex.com') {
    :root {
        --header-bg-color: /*[[header-bg-color]]*/;
        --body-bg-color: /*[[body-bg-color]]*/;
        --selector-color: /*[[selector-color]]*/;
        --selector-color-hover: /*[[selector-color-hover]]*/;
        --text-scale-1: /*[[text-scale-1]]*/;
        --text-scale-2: /*[[text-scale-2]]*/;
        --feature-box-hover: /*[[feature-box-hover]]*/;
        --feature-box: /*[[feature-box]]*/;
    }
    html body.accent div#contentwrap div#content header {
        background-color: var(--header-bg-color);
    }
    html body.accent div#contentwrap div#content div#body.accent {
        background-color: var(--body-bg-color);
    }
    input {
        background-color: var(--selector-color);
    }
    .button:hover {
        background-color: var(--selector-color);
        color: var(--text-scale-1);
    }
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    a,
    .white,
    #contentwrap {
        color: var(--text-scale-1);
    }
    #nav .hiddennav {
        background-color: var(--header-bg-color);
    }
    #nav .hiddennav a:hover {
        background-color: var(--selector-color);
        color: var(--text-scale-2);
    }
    #nav .hiddennav li a {
        color: var(--text-scale-1);
    }
    #contentwrap {
        background-color: var(--body-bg-color);
    }
    footer {
        background-color: var(--header-bg-color);
    }
    .crBox:hover {
        background-color: var(--feature-box-hover) !important;
    }
    .crBox {
        background-color: var(--feature-box) !important;
    }

/*
 * taming page
 */
    .crSettings {
        background-color: var(--header-bg-color);
        margin-top: 0;
    }
    html body.accent div#contentwrap div#content div {
        border: none !important;
    }
    #search,
    .whiteinput {
        background-color: var(--selector-color) !important;
        color: var(--text-scale-1);
    }
    #levelForm input:focus {
        background-color: var(--selector-color-hover) !important;
    }
    .actionButton {
        background-color: var(--selector-color);
        color: var(--text-scale-1)
    }
    .actionButton:hover {
        background-color: var(--selector-color-hover);
        color: var(--text-scale-2)
    }
    .tamingTable th,
    .rowHeaderItem {
        color: var(--text-scale-1);
    }
    .tamingTable td {
        background-color: var(--selector-color);
        border-color: var(--selector-color-hover);
    }
    .tamingTable tr:hover td {
        background-color: var(--selector-color-hover);
    }
    .darkReverse {
        color: var(--text-scale-2)
    }
    .knockCount {
        color: var(--text-scale-2);
    }
    .ko .rowItemN:hover {
        background-color: var(--selector-color);
    }
    .cardBody {
        background-color: var(--header-bg-color) !important;
    }
    .darker,
    #body.accent .pill {
        color: var(--text-scale-2);
    }
    .light,
    .creatureIcon a,
    .dark,
    .itemLabel {
        color: var(--text-scale-1);
    }
    .row {
        background-color: var(--header-bg-color) !important;
    }

/*
 * Kibble page
 */
    .kg .creatureIcon a {
        background-color: var(--selector-color);
    }
    .kg .creatureIcon a:hover {
        background-color: var(--selector-color-hover);
    }
    .gridTable td {
        background-color: var(--selector-color);
    }
    .gridTable th {
        color: var(--text-scale-1);
    }
 
 /*
 * modifiers
 */
    tr:nth-child(2n) {
        background-color: var(--header-bg-color)
    }
    html body.accent div#contentwrap div#content div#body.accent div#content div.wrap table tbody tr td.center div.row {
        background-color: var(--body-bg-color) !important;
    }
    html body.accent div#contentwrap div#content div#body.accent div#content div.wrap table tbody tr:nth-child(2n) td.center div.row {
        background-color: var(--header-bg-color) !important;
    }
 
 /*
 * ark creature stats
 */
    tr:nth-child(2n) {
        background-color: var(--header-bg-color) !important;
    }
}
