@-moz-document domain("alza.cz") {
body, #boxc #binfo, #tpf2>li, #tpf>li, #tpf2>li .tree2, #tpf>li .tree2, #detailItem #tabs .tabsStickyBg.fixed, .priceDetail .guarantee, .priceDetail #avlc, #detailItem .variantListView.showcolapser .container .collapsed, #orderpage, body.default #orderpage.hasShoppingLists #ordernav, #ordernav, #order2, #order2> *, #order3> *, #alzaDialog, .allVariantsButton, .priceSummaryInnerBlock, .priceSummaryInnerBlock tr.withVat td:first-of-type span, .deliveryPaymentPrice, #order2Summary .items .item .price span, .LastOrderAccessories, .LastOrderAccessories .box, .blockBasketPopup .blockBasketPopupWrapper, .headericonsc, .notificationBlock   {
    background-color: black !important;
    background: black !important;
    color: #9a9a9a !important;
}

#detailItem .variantListView.expanded .container .collapsed .item.disabled, .paymentContainer.disabled {
    background-color: #3c1c1c !important;
    cursor: not-allowed !important;
}
