    background-color: #313131;
}

.deliveryContainer:hover, .paymentContainer:hover, .allVariantsButton:hover, .deliveryContainer.selected, .paymentContainer.selected, .blockBasketPopup .blockBasketPopupWrapper .basketPopupItem:hover {
    background-color: #313131 !important;
}

.shoppingListOrder1 .listsContainer .all .items .itemsContainer .shoppingListBox .close:after {  
    content:"⨯";
    color: #561515;
    font-size: 2rem;
     margin-top: -7px;
    display: block;
}
.shoppingListOrder1 .listsContainer .all .items .itemsContainer .shoppingListBox .close {
    background: none;
}

.shoppingListOrder1 .listsContainer .all .items .itemsContainer .shoppingListBox {
    background-color: #111111;
}
#detailItem #tabs .tabsStickyBg.fixed {
    border-bottom:1px solid #a1a1a1;
}
body #content0, #h1cc, #h1c, #blockRecentlyViewedDetails, .articleLab, #detailItem {
    background: black !important;
}
.catlist .rel .name, .catlist .subC .name, .catlist .til .name, .catlist .wiz .name {
    color: #aaaaaa;
}
body .catlist .rel .container, body .catlist .subC .container, body .catlist .til .container, body .catlist .wiz .container , #detailItem .dynamicPromo .box{
    background: #2f2f2f !important;
    border-color: #2f2f2f;
    color: #aaaaaa;
}
